css:
@-moz-document domain("dolarhoje.com") {
  body {
      background: #f8f8f8;
  }
  #topo {
      width: 100%;
      position: absolute;
      top: 0;
      left: 0;
  }
  #topo p, #whatsapp-share-button, #telegram-share-button, #topo .menu li:nth-child(3) {
      display: none !important;
  }
  #topo .menu li {
      box-sizing: border-box;
      width: 7.14285714%;
      display: block;
      float: left;
      margin: 0;
      padding: 0;
      text-align: center;
      border-right: 1px solid #e0e0e0;
      height: 40px;
      line-height: 40px;
      font-size: 13px;
  }
  #topo a {
      text-decoration: none !important;
      display: block;
      background: #f0f0f0;
      height: 40px;
      line-height: 40px;
      color: #eda848;
  }
  #cotacao {
      background: #fff;
      padding: 15px 0 17px 0;
  }
  .activated .cotMoeda {
      background: #fafafa !important;
      border: 1px solid #f0f0f0;
  }
  .cotMoeda {
      font-size: 45px;
  }
}
