css:
@-moz-document domain("boards.br.leagueoflegends.com"), domain("boards.na.leagueoflegends.com"), domain("boards.pbe.leagueoflegends.com") {
  html body .backdrop {
	background-color: black;
    background-position: top center;
    background-repeat: no-repeat;
    background-image: url(http://cdn.leagueoflegends.com/lolkit/1.1.6/resources/images/bg-default.jpg) !important;
    height: 1080px;
}
html body .backdrop .header, html body .backdrop .magic {
	background-image: none !important;
}
#search-form {
    display: flex;
    background: #fefefe;
}
.discussions table {
    background: #efefef;
}
.discussion-list-item .riot-commented .number, .discussion-list-item .age .number, .discussion-list-item .num-comments .number, .discussion-list-item .view-counts .number, body a, body a:visited, body #page-main .riot-apollo.voting ul.riot-voting.authenticated li.total-votes {
    color: #003030;
}
.discussion-list-item .title .discussion-title a, .discussion-list-item .title .discussion-title a:visited {
    color: #257372;
    font-weight: normal;
}
.discussion-list-item {
    border: 1px solid #fcfcfc;
    border-bottom: 1px solid #d7d7d7;
}
.discussions.discussion-list.box {
    background-color: #a0a0a0;
    border: 5px solid #a0a0a0;
}
.discussion-list-item div.thumbnail-fallback {
    border: 1px solid #a0a0a0;
}
.discussion-list-item {
    height: 80px;
}
.discussion-list-item .title {
    padding-bottom: 5px;
}
.discussion-list-item td {
    border-bottom: 1px solid #d7d7d7;
    border-top: 1px solid #fcfcfc;
}
.discussion-list-item .num-comments, .discussion-list-item .view-counts {
    border-right: 10px solid transparent;
}
.discussion-list-item .voting {
    border-left: 5px solid transparent;
}
.apollo-header {
	padding-top: 60px;
}
.apollo-header h2 a {
    color: #dedede !important;
    text-shadow: 2px 2px 5px rgba(0,0,0,0.7),-1px -1px 4px rgba(0,0,0,0.5);
}
.apollo-header .controls, .apollo-header .controls a, .apollo-header .controls a.active {
    text-shadow: 1px 1px 1px black;
    color: #dedede;
    text-decoration: none;
}
.side .box, #discussion.box, .nested-comment {
    background: #efefef;
    border: 5px solid #a0a0a0;
}
.new-discussion-box.box {
    border: none;
    padding: 0;
}
.side .box.collapseable .toggle {
    padding: 0;
    border: none;
}
#navigation-container.box.collapseable .content {
    margin: 0;
    margin-left: -10px;
    margin-right: -9px;
}
#navigation-container.box.collapseable .content a {
    padding-left: 20px;
}
.nested-comment .list .nested-comment {
    background: #efefef !important;
}
#discussion #content, .nested-comment .body-container .body {
    color: #444;
}
.button, body a.button, #uploader .plupload_button, .riot-athena .btn, #cboxClose, #board-details button {
    background-color: #5c5983;
    color: #e0e0e0;
    border: 2px solid #3d3b5b;
    outline: none;
}
.button:hover, body a.button:hover, #uploader .plupload_button:hover, .riot-athena .btn:hover, #cboxClose:hover, #board-details button:hover {
    background-color: #506090;
    color: #ffffff;
    outline: none;
    border: 2px solid #3d3b5b;
}
#navigation-container a, .board-navigation a {
    color: #7b5a36;
    border-bottom: 1px solid #d7d7d7;
    border-top: 1px solid #fcfcfc;
    display: block;
    padding: 10px 5px;
}
#navigation-container a:hover, .board-navigation a:hover {
    color: #200000;
}
#navigation-container .markdown-nav br {
    content: " ";
    display: none;
}
.nested-comment .body-container .body, .nested-comment .body-container .footer {
    border-top: 1px solid #fcfcfc;
    margin-top: 0px;
    padding-top: 15px;
}
.nested-comment .body-container .header, .nested-comment .body-container .body {
    padding-bottom: 15px;
    border-bottom: 1px solid #d7d7d7;
}
.nested-comment .body-container .footer {
    padding-bottom: 15px;
}
.reply.form-container .textarea-holder, .edit.form-container .textarea-holder {
    background-color: #fff;
    border: 5px solid #a0a0a0;
}
.reply.form-container .bottom-bar, .edit.form-container .bottom-bar {
    background-color: #e1e1e1;
    border: 5px solid #a0a0a0;
    border-top: none;
}
.reply.form-container .preview.box, .edit.form-container .preview.box {
    color: #003030;
    background-color: #bebdbd;
    padding: 8px 15px;
    border: 5px solid #a0a0a0;
    border-top: none;
}
.comments-header-container a.flat-toggle {
    color: #f0f0f0;
}
.comments-header-container a.flat-toggle:hover {
    color: #fff;
}
}
