css:
@-moz-document url-prefix("https://www.onlinekhabar.com/") {
/*
     * Name:    Dark onlinekhabar.com
     * Author:  Kraj
     * Version: v1.0
     */

/* Background */
html, body {
    color: #c3c3c3;
    background: #202020;
}

header {
    background: #404040;
}

.hamburger {
    color: #fff;
}

header #header_right {
    color: #bfbfbf;
}

#trending {
    background: #585858;
    border-bottom: 1px solid #c80000;
}

#trending #the_page ul li.head {
    color: #c3c3c3;
}

#trending #the_page ul li a {
    color: #c3c3c3;
}

#the_page, #body_a {
    background: #303030;
}

#body_a li a {
    color: #4689cc;
}

#two_col a, #three_col a {
    color: #4689cc;
}

#mst {
    background: #3a3a3a;
}

.news_loop {
    color: #d4d4d4;
}

#mst ul.tabs_1 li a {
    color: #a1b1c1;
}

#one_col .intbox h2 a {
    color: #4689cc;
}

#featurebx h2 a {
    color: #4689cc;
}

a {
    color: #4689cc;
}

#special {
    border-bottom: 1px solid #c80000;
}
#section_head {
    border-top: 3px solid #c80000;
    border-bottom: 1px solid #c80000;
}

#section_head_txt {
    color: #c80000;
}

.intbox {
    background: #3a3a3a;
}

h2 a {
    color: #4689cc;
}

#section_main .int_loop .news_loop h3 a {
    color: #999;
}

.opinion {
    background: #3a3a3a;
}

#bichar {
    color: rgba(255,255,255,0.8);
    background-color: rgb(58, 58, 58);
}

#featurebx h1 {
    background: #3a3a3a;
    border-bottom: 1px #c80000 solid;
}

#featurebx {
    background: #3a3a3a;
}

#hmpopular {
    background: #3a3a3a;
}

#two_col .tech_main {
    background: #3a3a3a;
}

.t_loop {
    background: #3a3a3a;
}

.ent_lft {
    background: #3a3a3a;
}

#section_main .ent_lft h2 a {
    color: #6391bf;
}

#section_head_rt ul li a {
    color: #999;
}

.ent_right {
    background: #3a3a3a
}

#right_cbox {
    background: #3a3a3a;
}

#right_chead {
    background: #999;
}

.tmb_cap {
    background: #3a3a3a;
}

#foot_cont {
    background: #202020;
}

.foot_div ul li a {
    color: #9e9e9e;
}

#foot_cont h2 {
    color: #3174b8;
}

a.to-top {
    background: #3068a0;
    padding: 2px 2px;
}

.inside_head {
    color: #3c7ec1;
}

#arch_title {
    background: #585858;
}

#sing_cont p {
    color: #c3c3bc;
}

#sing_left h2 {
    color: rgba(229,229,229,0.8);
}

#respond {
    background: #3a3a3a;
    border: none;
}

#comment-box {
    background: #3a3a3a;
    color: #e5e5e5;
    border: none;
}

element.style {
    background: #3a3a3a;
}

#tauko {
    background: rgba(255, 255, 255, 0);
}

.ctitle {
    background-color: #585858;
}

#popular ul.tabs_1 {
    background: #3a3a3a;
}

#popular ul.tabs_1 li a {
    color: #c3c3c3;
}

#intfe {
    background: #3a3a3a;
}
}
