css:
@-moz-document domain('l2tracker.net'), domain("l2cstat.net")
{

/*======================background===========================*/
body
{
    background:#222931 url("https://live.haaard.ru/bg-l2tracker.png") top no-repeat;
    /* background-size:100%; */
    font-family: "Tahoma", Arial, Verdana;
    font-size: 12px;
    cursor:url("http://xxxcodlxxx.at.ua/st/cur/nrm.cur"), text;
}

a:hover
{
    cursor:url("http://xxxcodlxxx.at.ua/st/cur/lnk.cur"), text;
}

p
{
    font-family: "Tahoma", Arial, Verdana;
    font-size: 12px;
    color:#BF9F60;
}

#footer
{
    bottom: 0;
    text-align: right;
    color:#cccccc;
}

.wrap
{
    text-align: center;
}

.content
{
    display: inline-block;
    margin-top:200px;
    padding-top:27px;
    background: url("https://i.imgur.com/ohVeUgn.png") top repeat-x;
}
table, th, td
{
    border: 1px solid #262628;
    border-collapse: collapse;
    font-family: "Tahoma", Arial, Verdana;
    font-size: 12px;color: #BF9F60;
}

caption
{
    font-size: 14px;
    font-weight: bold;
    margin-top: 5px;
    color:#BF9F60;
    text-transform:uppercase;
    background: #1a1a18;
}

td, th
{
    padding: 2px 4px;
}

th
{
    background: #1a1a18;
    color:#BF9F60;
}

td
{
    border: 1px solid #262628;
    color:#79716b;
}

thead th
{
    text-align: center;
    background: #1a1a18;
    color: #CCCCCC;
    font-size: 100% !important;
}

tbody th
{
    font-weight: bold;
}

tbody tr
{
    background: #1a1a18;
}

tbody tr.odd
{
    background: #F7F9FC;
}

table a:link
{
    cursor:url("http://xxxcodlxxx.at.ua/st/cur/nrm.cur"), text;
    color: #bfb7a0;
    text-decoration: none;
}

table a:visited
{
    cursor:url("http://xxxcodlxxx.at.ua/st/cur/nrm.cur"), text;
    color: #bfb7a0;
    text-decoration: none;
}

table a:hover
{
    cursor:url("http://xxxcodlxxx.at.ua/st/cur/lnk.cur"), text;
    color: #FFFFFF;
    text-decoration: none !important;
}

tr:hover th, tr.odd:hover th
{
    border: 1px solid #262628 !important;
    background: #1f221b !important;
}

tr:hover, tr.odd:hover
{
    background: #1f221b;
}

.ally_img
{
    display: inline-block;
    width: 8px;
    height: 12px;
    background-repeat: no-repeat;
}

.clan_img
{
    display: inline-block;
    width: 16px;
    height: 12px;
    background-repeat: no-repeat;
    padding-right: 5px;
}

.red
{
    color: #ff121c;
}

.green
{
    color: #008000;
}

a.tooltips
{
    position: relative;
    display: inline;
}

a.tooltips span
{
    position: absolute;
    width: 240px;
    color: #000000;
    background: #D1EBFC;
    height: 25px;
    line-height: 25px;
    text-align: center;
    visibility: hidden;
    border-radius: 5px;
}

a.tooltips span:after
{
    content: '';
    position: absolute;
    top: 100%;
    left: 50%;
    margin-left: -8px;
    width: 0;
    height: 0;
    border-top: 8px solid #D1EBFC;
    border-right: 8px solid transparent;
    border-left: 8px solid transparent;
}

a:hover.tooltips span
{
    visibility: visible;
    opacity: 0.8;
    bottom: 30px;
    left: 50%;
    margin-left: -76px;
    z-index: 999;
}

span.highlight
{
    background-color: #C6D9DB;
    cursor: pointer;
}

span.splink
{
    color: #0A5794;
    cursor: pointer;
}

::-webkit-scrollbar-track
{
    background-color: #1d201a;
}

::-webkit-scrollbar
{
    width: 6px;
    background-color: #1d201a;
}

::-webkit-scrollbar-thumb
{
    background-color: #ccc;
}
}
