css:
@-moz-document domain("www.hplovecraft.com") {
@font-face {
  font-family: 'Fondamento';
  font-style: normal;
  font-weight: 400;
  src: local('Fondamento Regular'), local('Fondamento-Regular'), url(http://themes.googleusercontent.com/static/fonts/fondamento/v2/bHQyc5zrMLI5-R-me5j-ei3USBnSvpkopQaUR-2r7iU.ttf) format('truetype');
}

::-webkit-scrollbar { display: none; }

body { background-color: #222; }
.pagelayout { 
  background-color: #111;
  width: 100%;
  max-width: 800px;
  height: calc(100% - 80px);
  padding: 40px 0;
  overflow-y: auto;
}
.pagelayout > table { background-color: transparent; width: 75% }
.pagelayout > table font {
  font-family: 'Fondamento', sans-serif;
} 
.pagelayout > table td { color: #ccc; }
.pagelayout > table b { color: #fff; }
.pagelayout > table a font { color: #fc0; }
.pagelayout > table a:hover font { color: #fe8; }
.pagelayout > table > tbody > tr:nth-of-type(3) font { 
  font-size: 1em;
}

img { mix-blend-mode: screen; filter: invert(1); }
map + img, tr:nth-of-type(3) a img, h3 + table img { filter: none; mix-blend-mode: normal; }

.hang {color: #ccc; }

a { text-decoration: none; color: #fe8; }
a:visited { color: #b8c; }
a:hover { color: #fff; }
}

@-moz-document url("http://www.hplovecraft.com/study/cc/") {
table tr:nth-of-type(3) table table, table tr:nth-of-type(3) table table tr { background-color: transparent; }
table tr:nth-of-type(3) table table tr:first-of-type { color: #fff; }
}

@-moz-document url("http://www.hplovecraft.com/") {
table table img { filter: none; mix-blend-mode: normal; }
}
