css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.ebay.com") {
/* Removes content and column from the right side of pages */
.sidebar, #sidebar2 {
    display : none !important;
}
#sidebar-column {
    display : none !important;
}
/* Removes content and column from the left side of pages */
.lw-left-nav {
    display : none !important;
}
/* Disables pop up balloons on the topic title pages */
.thread-snippet {
    display : none !important;
}
/* Removes author image content from the topic title pages and author images from the posts */
.lw-photo img, .lw-author-photo-bezel img, .lw-author-photo-frame td {
    display : none !important;
}
/* Increases font size for topic titles for quick acreening */
h3 div.lw-featured-post-excerpt, .lw-value h3, .lw-text h3, .lw-forum-table h2 {
    font-size : 18px !important;
}
}
