css:
@-moz-document domain("bangumi.tv"), domain("bgm.tv"), domain("chii.in") {
::selection {
    background: rgba(240, 145, 153, .4);
}
*,
small.feed,
textarea,
span.avatarNeue {
    transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    font-family: "SFMono-Regular", Consolas, "Liberation Mono", Menlo, Courier, "Noto Serif CJK SC", monospace;
}
#navMenuNeue
li,
#navMenuNeue li a span,
.tl_relation a,
.tl_group a,
.tl_wiki a,
.tl_index a,
.tl_blog a {
    transition: all 0ms !important;
}
body.bangumiNeue,
body.bangumi,
body {
    background-color: #f1f1f1;
}
#headerSubject h1 {
    background: #fff;
    margin: auto;
    line-height: 70px;
    width: 85%;
    padding-left: 15%;
}
#headerNeue2,
#navNeue2 #navMenuNeue li a.chl,
.progress
.inner {
    background: transparent url(http://bgm.tv/img/rc2/bg_header_v2.png) repeat-x;
}
#headerNeue2 a.logo {
    background: transparent url(http://bgm.tv/img/rc2/logo_rc2_m.png) left 50% no-repeat;
}
.idBadgerNeue .avatar span.avatarNeue {
    background-image: url(data:image/svg+xml;base64,) !important;
}
span.avatarNeue {
    border-radius: 50%;
}
#comment_list div.light_even,
div#comment_list div.light_odd,
ul.browserMedium,
ul.browserCoverMedium,
#entry_list
div.item,
#columnA #blog,
#columnA #index,
#columnA #group,
#columnA #friend,
#columnA #mono,
#columnA #anime,
#columnA #book,
#columnA #game,
#columnA #music,
#columnA #real,
#prgManager,
.TsukkmiBox,
.timelineTabs,
ul.browserList li.item,
ul.coversSmall,
ul.browserCoverSmall {
    background: #fff !important;
    border-radius: 2px;
    padding: 25px;
    transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14);
    margin-bottom: 14px;
    border-collapse: collapse;
}
#comment_list div.light_even:hover,
div#comment_list div.light_odd:hover,
ul.browserMedium:hover,
ul.browserCoverMedium:hover,
#entry_list
div.item:hover,
#columnA #blog:hover,
#columnA #index:hover,
#columnA #group:hover,
#columnA #friend:hover,
#columnA #mono:hover,
#columnA #anime:hover,
#columnA #book:hover,
#columnA #game:hover,
#columnA #music:hover,
#columnA #real:hover,
#prgManager:hover,
.TsukkmiBox:hover,
.timelineTabs:hover,
ul.browserList li.item:hover,
ul.coversSmall:hover,
ul.browserCoverSmall:hover{
    box-shadow: 0 2px 8px 0 rgba(0, 0, 0, 0.14);
}
#comment_list div.light_even:active,
div#comment_list div.light_odd:active,
ul.browserMedium:active,
ul.browserCoverMedium:active,
#entry_list
div.item:active,
#columnA #blog:active,
#columnA #index:active,
#columnA #group:active,
#columnA #friend:active,
#columnA #mono:active,
#columnA #anime:active,
#columnA #book:active,
#columnA #game:active,
#columnA #music:active,
#columnA #real:active,
#prgManager:active,
.TsukkmiBox:active,
.timelineTabs:active,
ul.browserList li.item:active,
ul.coversSmall:active,
ul.browserCoverSmall:active {
    box-shadow: 0 4px 16px 0 rgba(0, 0, 0, 0.14);
}
ul.innerRightList {
    width: unset;
}
textarea.reply,
textarea.quick,
textarea,
select.form {
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14) inset;
}
input.inputBtn,
a.fancyBtn {
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14);
    background: #4EB1D4 !important;
}
input.inputtext {
    border-bottom: .5px solid #ccc;
    border-top: 0;
    border-left: 0;
    border-right: 0; 
}
input.inputtext:active,
input.inputtext:hover {
    border-bottom: .5px solid #F09199;
}
input.inputBtn:active,
a.fancyBtn:active,
input.inputBtn:hover,
a.fancyBtn:hover {
    box-shadow: 0 4px 16px 0 rgba(0, 0, 0, 0.14);
}
#sliderContainer.sticky {
    background: url(http://bgm.tv/img/b5/bg_sub_list.png) repeat-x #f5f5f5 bottom;
}
div#timeline ul li {
    margin: 5px 0 14px 0;
}
#navMenuNeue li a span {
    color: rgba(255, 255, 255, .8);
}
#navMenuNeue li a span:hover {
    color: rgba(255, 255, 255, 1);
}
#headerNeue2 #headerSearch {
    transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    border-radius: 2px;
    background: rgba(255, 255, 255, .3);
    color: rgba(0, 0, 0, .3);
}
#headerNeue2 #headerSearch:active,
#headerNeue2 #headerSearch:hover {
    background: #fff;
}
a.epBtnUnknown,
a.epBtnAir,
a.epBtnAir:active,
a.epBtnAir:visited,
.tinyMode a.epBtnUnknown,
.tinyMode a.epBtnAir,
.tinyMode a.epBtnAir:active,
.tinyMode a.epBtnAir:visited,
a.sepBtnUnknown,
a.sepBtnAir,
a.sepBtnAir:active,
a.sepBtnAir:visited {
    border: 1px solid #DAEAFF;
}
a.epBtnWatched,
a.epBtnWatched:active,
a.epBtnWatched:visited,
.tinyMode a.epBtnWatched,
.tinyMode a.epBtnWatched:active,
.tinyMode a.epBtnWatched:visited,
a.sepBtnWatched,
a.sepBtnWatched:active,
a.sepBtnWatched:visited {
    border: 1px solid #4897FF;
}
a.epBtnNA,
a.epBtnNA:active,
a.epBtnNA:visited,
.tinyMode a.epBtnNA,
a.sepBtnNA,
a.sepBtnNA:active,
a.sepBtnNA:visited {
    border: 1px solid #E0E0E0;
}
.progress
.inner
small {
    display: none;
}
.progress
.inner {
    height: 8px;
}
#dock
.content {
    position: fixed;
    right: 70px;
    bottom: 70px;
    border-radius: 50%;
    background: #F09199;
    width: 50px;
    height: 50px;
    padding: 0;
    box-shadow: 0 2px 8px 0 rgba(0, 0, 0, 0.28);
}
#dock
.content:hover {
    position: fixed;
    right: 70px;
    bottom: 70px;
    border-radius: 50%;
    background: #F09199;
    width: 50px;
    height: 50px;
    padding: 0;
    box-shadow: 0 4px 16px 0 rgba(0, 0, 0, 0.28);
}
#dock
.content::before {
    content: "＋";
    font-family: sans-serif;
    color: white;
    font-size: 48px;
    position: relative;
    top: 13px;
    left: 1px;
}
#dock
.content:hover::before {
    content: "×";
    font-family: sans-serif;
    color: white;
    font-size: 48px;
    position: relative;
    top: 13px;
    left: 7px;
    z-index: -1;
}
#dock
ul {
    opacity: 0;
    width: 50px;
    height: 50px;
}
#dock
ul:hover,
#dock
ul:active {
    opacity: 1;
}
#dock li a {
    display: block;
    background: #fff;
    padding: 5px;
    margin: 5px;
    border-radius: 2px;
    position: relative;
    width: 100px;
    right: 130px;
    bottom: 200px;
    box-shadow: 0 2px 8px 0 rgba(0, 0, 0, 0.14);
}
#dock li a:hover {
    box-shadow: 0 4px 16px 0 rgba(0, 0, 0, 0.14);
}
#dock li a:active {
    box-shadow: 0 8px 32px 0 rgba(0, 0, 0, 0.14);
}
div#timeline
h4.Header
div.SimpleSidePanel
h2 {
    font-size: smaller;
}
ul.browserFull li.item
span.rank {
    border-radius: 5rem;
}
ul.browserCoverMedium,
ul.coversSmall {
    padding: 25px 10px;
}
#entry_list {
    margin-top: 10px;
}
img.avatar,
ul.browserList li.item
img.cover {
    max-width: 48px;
}
}

