@-moz-document domain("bangumi.tv"), domain("bgm.tv"), domain("chii.in") {
div.idBadgerNeue span.avatarNeue,
.borderNeue,
div#timeline ul li span.info,
#columnTimelineInnerWrapper,
input.inputtext {
    box-shadow: 0 0 0 rgba(0, 0, 0, 0);
}
div.commentList,
div.section_line,
#columnTimelineInnerB,
#sliderContainer,
#columnTimelineInnerWrapper,
div.menu_inner,
table.topic_list
tr.even,
table.topic_list
tr.odd,
input.inputtext,
ul.browserFull
li.item {
    background: none;
}
#headerNeue2 #headerSearch select,
#headerNeue2 #headerSearch,
#sliderContainer,
.borderNeue,
div#timeline ul li span.info,
#columnTimelineInnerWrapper,
div.menu_inner, 
.progress,
textarea.reply,
textarea.quick,
textarea,
select.form,
.panelProgress
input.inputtext,
input.inputBtn,
a.fancyBtn,
div.light_odd,
div#timeline
h4.Header,
div.light_even,
div.SimpleSidePanel
h2 {
    border: 0;
}
a.epBtnWatched,
a.epBtnWatched:active,
a.epBtnWatched:visited,
a.epBtnUnknown,
a.epBtnAir,
a.epBtnAir:active,
a.epBtnAir:visited,
#prgManagerHeader ul.categoryTab li a,
#columnTimelineInnerWrapper ul.timelineTabs li a,
textarea.reply,
input.inputtext,
textarea.quick,
textarea,
input.inputtext,
input,
select.form,
.progress
.inner,
input.inputBtn,
a.fancyBtn,
a.p, a.p:active, a.p:visited,
#columnSubjectBrowserB ul.grouped li
a,
#columnSubjectBrowserB ul.grouped li a:hover, #columnSubjectBrowserB ul.grouped li
a.focus,
#airMonthMenuPrev, #airMonthMenu,
strong.p_cur,
a.btnPink, a.btnGray, a.btnBlue,
input.searchBtnL,
div.SimpleSidePanel
h2,
ul.sideEpList
li,
span.avatarNeue:hover {
    border-radius: 2px;
}
ul.collect
li.item,
table.topic_list
tr.even,
table.topic_list
tr.odd,
ul.timeline
li,
div#timeline ul li,
ul.browserFull
li.item {
    border-bottom: .5px solid #E0E0E0;
}
}

@-moz-document url("http://bangumi.tv/"), url("http://bgm.tv/"), url("http://chii.in/") {
#columnHomeA {
    width: 88%;
    margin: 10px auto;
    float: unset;
}
.sideInner,
div.SidePanelMini,
#header {
    display: none;
}
}
