css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("altmarket.net") {

#altinfo {
background-image: url(http://i40.tinypic.com/244piww.png) !important;
color: black !important; }

#container {
background-image: url(http://i44.tinypic.com/5pelvb.png) !important; }

#user_info {
color: #84c6e7 !important; }
#user_info a:link {
color: #caedff !important; }
#user_info a:hover {
color: white !important; }

body {
font-family: Verdana, Arial, Helvetica, sans-serif !important;
font-size: 12px !important;
margin: 0px !important;
color: #007ab7 !important;
background-color: #015e8d !important;
background-image: url(http://i40.tinypic.com/2vv7sdz.gif) !important;
background-position: center !important;
background-repeat: repeat-y !important; }

#nav {
background-image: url(http://i44.tinypic.com/2rhy3dc.png) !important; }

#navtop {
background-image: url(http://i43.tinypic.com/29p9t8k.png) !important; }

#navbot {
background-image: url(http://i43.tinypic.com/29p9t8k.png) !important; }

body a:link {
color: #69c0ec !important; }

body a:visited {
color: #007ab7 !important; }

body a:hover {
color: white !important; }

a:link {
color: #C0C0C0 !important }

a:visited {
color: black !important; }

.alttable {
color: skyblue !important;
background-color: skyblue !important;
border: skyblue thin solid !important; }

.altcolor1 {
color: skyblue !important;
background-color: #007ab7 !important; }

.altcolor2 {
color: skyblue !important;
background-color: #025077 !important; }

.altcolor1 a:visited {
color: #025077 !important }

.altcolor3 {
background-color: #025077 !important;}

.input_box {
background-color: lightblue !important; }

form:hover {
color: skyblue !important; }

/*forum*/

.forumline { 
background-color: skyblue !important; 
border: 2px #006699 solid !important; }

td.row1 { 
background-color: #3e95c0 !important;
color: #004060 !important; }

td.row2 { 
background-color: #0572a9 !important;
color: #002334 !important; }

td.row3 { 
background-color: #015e8d !important; }

td.row1 a:link {
color: #034161 !important; }

td.row1 a:visited {
color: #056292 !important; }

td.cat,td.catHead,td.catSides,td.catLeft,td.catRight,td.catBottom {
background: url(http://i43.tinypic.com/qnl63t.png) !important; 
border: #FFFFFF !important; 
border-style: solid !important; 
height: 28px !important; }

td.row3Right,td.spaceRow {
background-color: #015e8d !important; }

td.row3Right a:visited {
color: #003049 !important; }

td.spaceRow a:visited {
color: #003049 !important; }

td.row2 a:visited {
color: #003049 !important; }
td.cat,td.catHead,td.catSides,td.catLeft,td.catRight,td.catBottom {
background-image: url(http://i43.tinypic.com/qnl63t.png) !important;
border: #000000 !important; }

td.rowpic {
background-image: url(http://i43.tinypic.com/qnl63t.png) !important;
background-repeat: repeat-x !important;
border: medium #0b3246 solid; }

th {
color: #c0eaff !important; }

.code {
color: #003c5b !important;
background-color: skyblue !important; 
border: #0b3246 thin solid !important; }

.quote {
color: #003c5b !important;
background-color: skyblue !important; 
border: #0b3246 thin solid !important; }

hr {
color: #003c5b !important;
background-color: #003c5b !important; }

input, textarea, select {
color: navy !important; }

input.post, textarea, select {
background-color : lightblue !important; }

input.button {
background-color : lightblue !important; }

input.mainoption {
background-color : lightblue !important; }

.postbody {
color: #003c5b !important; }

}
