css:
@-moz-document domain(www.youtube.com)
{

/* header styles */

#masthead-container {
    opacity: 0;
    transition: 0.60s;
    z-index: 998 !important;
    width: 100%;
    margin-left: auto;
    margin-right: auto;
    
}
#masthead-container:hover {
    opacity: 1 !important;
}

/* top 0 */
#page-manager.ytd-app{
    margin-top: 0px;
}
ytd-watch:not([theater]) #top.ytd-watch {
    margin-top: 0px;
}

/* header styles */

.appbar-hidden #masthead-positioner-height-offset {
    height: 0 !important;
}
.appbar-guide-menu-layout {
    margin-top: 0 !important;
    z-index: 0 !important;
}
#masthead-appbar {
    display: none;
}
#yt-masthead-container {
    border-bottom: 0 !important;
}
.ytp-gradient-bottom,
.ytp-gradient-top {
    opacity: 0;
}

/* player */
.html5-progress-list, .ytp-progress-list, .video-ads .html5-progress-list.html5-ad-progress-list, .video-ads .ytp-progress-list.ytp-ad-progress-list {
   height: 6px !important;
}
.html5-progress-bar-container, .ytp-progress-bar-container {
   margin-bottom: -6px;
}

.ytp-chrome-bottom {
   width: 100% !important;
   height: 33px;
   line-height: 33px;
   left: 0px !important;
}

.ytp-chrome-top, .ytp-chrome-bottom, .ytp-svg-fill, .ytp-time-display, .ytp-time-separator, .ytp-time-duration {
	color: #fff; 
    fill: #fff;
    text-shadow: none;    
}

.ytp-svg-shadow {
    stroke:#fff;
    stroke-opacity:.15;
    stroke-width:2px;
    fill:none
}
/* player */

.video-row.ytd-watch-card-video-list-renderer {
    border-bottom: 1px solid #bdbdbd;
    opacity:0.6;
}

ytd-button-renderer.style-primary[disabled][is-paper-button] {
    background-color: hsla(206.1, 79.3%, 52.7%, .0);
}

.ytd-two-column-search-results-renderer, .ytd-secondary-search-container-renderer {
	margin-top:15px;
}

/* searchbox text color */

ytd-searchbox[mode=legacy] #container.ytd-searchbox input.ytd-searchbox, ytd-searchbox[mode=legacy-centered] #container.ytd-searchbox input.ytd-searchbox {
    color: #bdbdbd;
}

/* searchbox text color */

/* searchbox new update */

ytd-searchbox[mode=legacy] #container.ytd-searchbox, ytd-searchbox[mode=legacy-centered] #container.ytd-searchbox {
    background-color: var(--yt-searchbox-background);
    border: 1px solid #111111;
    border-right: none;
    box-shadow: inset 0 1px 2px #111111;
    padding: 2px 6px;
    border-radius: 2px 0 0 2px;
}

/* searchbox new update */


#search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox {
    color: #bdbdbd;
    opacity: 1;
}

ytd-searchbox[mode=legacy] #search-icon-legacy.ytd-searchbox:hover, ytd-searchbox[mode=legacy-centered] #search-icon-legacy.ytd-searchbox:hover {
    border-color: #111;
    background-color: #111;
}

#search-icon-legacy.ytd-searchbox {
    border: 1px solid #111;
    background-color: #111;
}

/* searchbox new update */

/* searchbox result background and text */

.sbdd_b {background: #111;border-color: #111;}
.sbsb_c:hover {background: #262626;}
.sbsb_c {color:#bdbdbd;}
.sbsb_a {background: #111;}

/* searchbox result background and text */


/* dark style */

*:not([style-scope]):not(.style-scope) {
    --yt-primary-color: hsl(0, 0%, 74%);
    --yt-primary-text-color: hsl(0, 0%, 80%);
    --yt-hovered-text-color: hsl(0, 0%, 74%);
    --yt-secondary-text-color: hsl(0, 0%, 74%);
