css:
@-moz-document url-prefix("http://www.sailorfuku.com/")
{

 
  html
  {
    background: url('http://image.noelshack.com/fichiers/2017/37/6/1505556039-nekoashi-verso.png') !important;
  }
  
  #game_header a#logo
  {
    background: url('http://image.noelshack.com/fichiers/2017/37/6/1505559811-jquj9.png') !important;
    width: 173px;
  }
  
#game_header {
    background-color: rgb(155, 117, 91) !important;
    background-image: none;
}
  
  #game_header #navigation li.nav-button a, #game_header #navigation li.nav-button span {
    color: #e6c8b5;
    line-height: 34px;
}
  
  #game_header #navigation-extra li.nav-button a, #game_header #navigation-extra li.nav-button span {
    display: block;
    color: #363636;
    text-decoration: none;
    line-height: 34px;
    padding: 0 10px;
    margin: 0 3px;
    position: relative;
}

#game_header #navigation-extra li.nav-button a, #game_header #navigation-extra li.nav-button span {
    color: #363636;
    line-height: 34px;
}
  
  #game_header #navigation-extra li.nav-button a, #game_header #navigation-extra li.nav-button span {
    color: #e6c8b5 !important;
    line-height: 34px;
}
  
  #game_header a:hover, #game_header span:hover {
    background-color: #77553d;
}
  
  span.spch-bub-inside em {
    font-style: normal;
    background: #f98d00;
    color: white;
    padding: 0 0.4em;
    border-radius: 0.25em;
    position: relative;
    font-size: 9px;
    vertical-align: super;
}
  
  .profile-clubs-list .separator {
    color: #ffbf27;
}
  
  #game_header #navigation .notification-count {
    border-radius: 0.25em;
    font-style: normal;
    font-size: 8px;
    background: #f59a00;
    color: white;
    font-weight: normal;
    display: block;
    line-height: normal;
    padding: 0 3px;
    position: absolute;
    top: 3px;
    left: 31px;
    font-family: verdana;
    text-align: center;
    z-index: 1;
}
  
    .menu-links .spch-bub-inside em {
    border-radius: 0.25em;
    font-style: normal;
    font-size: 12px;
    background: #f59a00;
    color: white;
    padding: 0 0.4em;
    position: relative;
    vertical-align: super;
}
  
  .menu-links .spch-bub-inside .point {
    width: 0;
    height: 0;
    position: absolute;
    top: 0.4em;
    left: 0.4em;
    display: block;
    border-left: 0.45em solid #f59a00;
    border-bottom: 0.45em solid #FFF;
    border-bottom: 0.45em solid transparent;
    overflow: hidden;
}

   #game_header #navigation-extra li.nav-button a, #game_header #navigation-extra li.nav-button span
  {
    display: block;
    color: #b08394;
    text-decoration: none;
    line-height: 34px;
    padding: 0 10px;
    margin: 0 3px;
    position: relative;
  }
  
  a {
    color: #0e7163;
}
  .pun a:hover, .pun a:active, .pun a:focus {
    color: #0e7163;
}
  
.pun .inew .icon {
    border-color: #ffd48b #fbbd54 #ffaf27 #f69d03;
}
  
body {
    font-family: "lucida grande",tahoma,verdana,arial,sans-serif;
    color: #cb6083;
    font-size: 12px;
    text-align: left;
}
  
.menu-links a {
    background: rgb(155, 117, 91);
        background-image: none;
    background-image: none;
    background-image: none;
    color: #e6c8b5;
    background-image: none;
}
  
   .menu-links a:hover
  {
    text-decoration: none;
    background-image: linear-gradient(#9b755b, #654633);
    color: white;
  }
  
  .menu-links a.active {
    color: #f98d00;
    background: white;
        background-image: none;
}
  
  .profile-calendar table .calendar-month {
    font-weight: bold;
    font-size: 15px;
    text-align: center;
    color: #3fb0ac;
    font-family: Amaranth;
}
  
  .profile-calendar table td.ok span {
    display: block;
    background-color: #f98d00;
    color: white;
    font-weight: bold;
    height: 100%;
    line-height: 200%;
}
  
  .profile-calendar table td.ok span:hover {
    background-color: #f96400;
    cursor: pointer;
}
  
  
  
  #contentContainer
  {
    background: rgba(255, 255, 255, 0.75);
  }
  
@-moz-document url-prefix("http://www.sailorfuku.com/hito.profil.avatar.html?id=")
{
  html
  {
    background: url('http://www.zupimages.net/up/15/1ng') !important;
  }
       }
}
