css:
@-moz-document domain(fer2.net) {
body
{
    background: none;
    background-color: rgb(60, 60, 60);
    color: rgb(183,183,183);
}

#header
{
    background: none;
    background-color: inherit;
}

#headerProxy {
    background: none !important;
}

.discussionListItem {
    background-color: rgba(47, 47, 47, 1);
    border-left: none;
    border-right: none;
    border-bottom: 1px solid rgb(97, 61, 16);
    color: rgb(230, 107, 27);
}

.discussionListItem .posterAvatar, .discussionListItem .stats {
    background-color: rgba(20, 20, 20, 1);
}

.dark_postrating_thread_rating {
    color: rgba(138, 138, 138, 1);
}

.dark_postrating_outputlist li strong, .dark_postrating_thread_rating strong {
    color: rgb(138, 138, 138);
}

a:link, a:visited {
    color: rgb(230, 107, 27);
}

.sidebar .secondaryContent {
    background-color: rgb(40, 40, 40);
    color: rgb(150, 150, 150);
    border: none;
}

.pollBlock .pollOptions, .pollBlock .pollResults {
    width: 100%;
    table-layout: fixed;
    display: table;
    margin: 5px 0;
    padding: 5px 0;
    border-top: none;
    border-bottom: none;
}

.PollContainer {
    border: none;
}

.button {
    font-style: normal;
    font-weight: bold;
    font-size: 13px;
    font-family: 'Open Sans', Helvetica, Arial, sans-serif;
    color: #000000;
    background-color: rgb(64, 38, 20);
    padding: 0px 6px;
    border: 1px solid rgb(0, 0, 0);
    text-align: center;
    outline: none;
    line-height: 34px;
    display: inline-block;
    cursor: pointer;
    box-sizing: border-box;
    height: 36px;
}

.button:hover, .button[href]:hover, .buttonProxy:hover .button {
    color: rgb(230, 107, 27);
    text-decoration: none;
    border-color: rgb(230, 107, 27);
}

.profilePostListItem {
    border-top: none;
}

.footer .pageContent {
    border-bottom: none;
}

.pageContent.no-prefooter {
    border-top: none;
}

.footer a:link, .footer a:visited {
    color: rgb(230, 107, 27) !important;
}

.footerLegal a:link, .footerLegal a:visited {
    color: rgb(230, 107, 27) !important;
}

.footerLegal a.concealed:hover, .footerLegal .concealed a:hover {
    color: rgb(230, 107, 27) !important;
}

.crumbs a:link, .crumbs a:visited {
    color: rgb(230, 107, 27) !important;
}

#pageNodeContent {
    border: none;
    background-color: inherit;
}

.messageList .message {
    background-color: rgb(47, 47, 47);
    border: none;
}

.message .likesSummary {
    background-color: rgb(62, 62, 62);
}

.primaryContent {
    background-color: rgb(35, 35, 35);
    padding: 12px;
    border: none;
}

.bbCodeQuote .attribution {
    border: none;
}

.bbCodeQuote .quoteContainer {
    background-color: rgb(68, 68, 68);
    border: none;
}

.quickReply {
    background-color: rgb(37, 37, 37);
    border: none;
}

.redactor_box 
{
    border: none;
}
}
