css:
@-moz-document domain("linustechtips.com") {
/*Rounding*/
.ipsBox:not( .ipsBox_transparent):not( .ipsModerated) {
    border-radius: 10px;
}
.ipsApp .ipsButton {
    border-radius: 10px;
}
.lmg-card .card-img {
    border-radius: 10px;
}
#elSearch {
    border-radius: 10px;
}
.lmgPost {
    border-radius: 10px;
}
.ipsApp .ipsSpacer_top,
.ipsApp .ipsSpacer_both {
    border-radius: 10px;
}
.ipsComposeArea_editor {
    background: #ffffff;
    border-radius: 10px;
}
.ipsApp .ipsSpacer_bottom.ipsSpacer_half,
.ipsApp .ipsSpacer_both.ipsSpacer_half {
    border-radius: 10px;
}
.ipsGrid > [class*="ipsGrid_span"] {
    border-radius: 10px;
}
.ipsPad {
    border-radius: 10px;
}
.cProfileRepScore_neutral {
    border-radius: 10px;
}
.ipsAreaBackground {
    border-radius: 10px;
}
.cStreamFilter {
    border-radius: 10px;
}
.ipsQuote {
    border-radius: 5px;
}
.ipsUserPhoto {
    border-radius: 15px;
}
*,
.ipsApp input[type="search"] {
    border-radius: 10px;
}
.ipsFollow .ipsCommentCount {
    border-radius: 5px 7px 7px 5px;
}
/*Border Fixes*/
.lmgComment_footer {
    border-top: 0px solid #D8D8D8;
}
.ipsDataItem {
    border-width: 0 0 0px 0;
}
.ipsQuote {
    border-style: none;
}
html[dir="ltr"] .ipsButton_split > .ipsButton:first-child,
html[dir="ltr"] .ipsButton_split > li:first-child > .ipsButton {
    border-radius: 10px 0 0 10px;
}
html[dir="ltr"] .ipsButton_split > .ipsButton:last-child,
html[dir="ltr"] .ipsButton_split > li:last-child > .ipsButton {
    border-radius: 0 10px 10px 0;
    border-right-width: 1px;
}
#elFlashMessage.cPostFlash {
    border-radius: 10px;
}
#elFlashMessage a {
    border-radius: 0px;
}
a[data-mentionid] {
    border-radius: 5px;
}
.ipsProgressBar.ipsProgressBar_fullWidth {
    border-radius: 10px;
}
.video-js .vjs-progress-control .vjs-load-progress,
.video-js .vjs-progress-control .vjs-play-progress,
.video-js .vjs-progress-control .vjs-progress-holder {
    border-radius: 0 7px 7px 0;
}
#elProfileHeader {
    border-radius: 15px 15px 0px 0px;
}

#elProfileStats {
    border-radius: 0px 0px 15px 15px;
}

.ipsHovercard {
    border-radius: 15px;
}

.ipsButtonBar .ipsPagination .ipsPagination_page.ipsPagination_active a {
    border-radius: 10px;
}

.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_page a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_next a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_prev a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_first a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_last a {
    border-radius: 10px;
}

.ipsStreamItem.ipsStreamItem_contentBlock {
    border-radius: 10px;
}

html[dir] .ipsReact_reactCount:last-child {
    border-radius: 10px;
}

html[dir="ltr"] .ipsReact_reactCount {
    border-radius: 10px;
}

.ipsPagination:not( .ipsPagination_mini) .ipsPagination_page a,
.ipsPagination:not( .ipsPagination_mini) .ipsPagination_next a,
.ipsPagination:not( .ipsPagination_mini) .ipsPagination_prev a,
.ipsPagination:not( .ipsPagination_mini) .ipsPagination_first a,
.ipsPagination:not( .ipsPagination_mini) .ipsPagination_last a {
    border-radius: 10px;
}

.ipsTags .ipsTag,
.ipsTag_prefix {
    border-radius: 0px 7px 7px 0px;
}

html[dir="ltr"] .ipsButtonRow li:first-child > a,
html[dir="ltr"] .ipsButtonRow li:first-child > button,
html[dir="ltr"] .ipsButtonRow li:first-child > span {
    border-radius: 10px;
}

.cProfileRepScore {
    border-radius: 10px;
}

.ipsComposeArea_attachments {
    border-radius: 0 0 10px 10px;
}

.ipsComposeArea_editorPaste {
    border-radius: 10px 10px 0 0;
}

.cke-editable {
    padding-bottom: 10px
}

.ipsImage {
    max-width: 100%;
    height: auto;
    border-radius: 10px;
}

.ipsAdvertisement_medium {
    border-radius: 10px
}

.ipsAdvertisement_large {
    border-radius: 10px
}

.ipsAdvitisement_small {
    border-radius: 10px
}

#amznBanners_assoc_banner_placement_default_0_img {
    border-radius: 10px;
}

.cke_top {
    border-radius: 10px 10px 0 0;
}

/*Rounding Undos and Border Fixes*/
.ipsType_break {
    border-radius: 0px;
}
.longSignature {
    border-radius: 0px;
}
#secondaryNav ul li.lmgNav_active {
    border-radius: 0px;
}
.ipsBreadcrumb.ipsBreadcrumb_bottom {
    border-radius: 0px;
}
#secondaryNav ul li:not(.lmgNav_active):hover {
    border-radius: 0px;
}
.ipsBreadcrumb.ipsBreadcrumb_top {
    border-radius: 0px;
}
.ipsQuote {
    border-style: none;
    padding-bottom: 2px;
}
.vjs-has-started .vjs-control-bar {
    border-radius: 0 0 10px 10px;
}
#elProfileActivityOverview .ipsStreamItem,
#elUserContent .ipsStreamItem,
#elSingleStatusUpdate .ipsStreamItem {
    border-radius: 0px;
}
.ipsBreadcrumb > ul > li {
    border-radius: 0px;
}

.signatureHideBar {
    border-radius: 0px;
}

#ipsLayout_header header {
    border-radius: 0px;
}

#lmgNav {
    border-radius: 0px 0px 10px 10px;
}

.ipsType_richText {
    border-radius: 0px;
}

.ipsTabs_item {
    border-radius: 10px 10px 0px 0px;
}

.cStreamFilter > li > a {
    border-right: 0px solid rgba(0, 0, 0, 0.1)
}

.ipsDialog_title {
    border-bottom: 0px solid #D2D2D2;
}

.ipsType_sectionTitle + .ipsButtonBar {
    border-bottom: 0px solid #DADADA;
}

.ipsBox:not( .ipsBox_transparent):not( .ipsModerated) {
    margin-bottom: 15px;
}

.ipsTooltip a {
    border-bottom: 0px dotted #fff;
}

html[dir="ltr"] #elProfileStats > ul:last-child > li {
    border-right: 0px;
}

*,
.ipsApp input[type="search"] {
    border-radius: 0 0 0px 0px;
}

.ipsPageHead_barText,
.ipsPageHead_barText_small {
    border-radius: 0px 0px 5px 5px;
}

.cProfileHeader_name h1 {
    border-radius: 5px 5px 5px 0;
}

.cke_editable {
    border-radius: 0;
}

ul.ipsMenu,
.ipsMenu > ul {
    border-radius: 11px;
}

.ipsCoverPhoto_photo {
    top: 0px;
}

}
