css:
@-moz-document domain(rootnode.pl)
{
  
  @font-face {
  font-family: 'Roboto';
  src: local('Roboto'), local('Roboto'), /* in case the font is installed */
       url(https://fonts.googleapis.com/css?family=Roboto:300,300i,400,400i,700,700i&subset=latin-ext)  format('woff');
}
 
      body {
          font-family: 'Roboto', sans-serif;
      }
  
        .ipsType_normal {
            font-size: 16px;
        }

        #ipsLayout_header header {
            padding: 5px 0;
            background-color: #494A53;
            padding-bottom: 40px;
        }
        /* */
        a:hover {
            color: #47BDF6;  
        }
        .ipsType_normal {
            font-size: 18px;
        }

#ipsLayout_sidebar {
    min-width: 315px;
    max-width: 315px;
    padding: 15px 0 15px 0;
    overflow: hidden;
    border-radius: 0;
}

        .ipsUserPhoto_large img, img.ipsUserPhoto_large, .ipsUserPhoto_large:after {
            width: 90px;
            height: 90px;
            border-radius: 45px;
            transition: all 0.4s ease-in-out;
        }

        .cPost:hover .ipsUserPhoto_large img {
             /* border-radius: 5px;*/
          }

			.ipsNavBar_secondary > li > a:hover,
              .ipsBreadcrumb > ul > li > a:hover {
                		color: #47BDF6;
              }

  
}
