css:
@-moz-document domain("ebanoe.it") {
.post-excerpt, .single .the_content_wrapper
	, h1, .text-logo #logo
	, h2
	, h3
	, h4, .style-simple .sliding_box .desc_wrapper h4
	, h6
	, #comments .commentlist>li .comment-author
	, .single-post .the_content_wrapper ul, .single-post .the_content_wrapper ol { color: #989898; }
h1 a, h1 a:hover, h3 a, h3 a:hover, h4 a, h4 a:hover, h6 a, h6 a:hover, a h6, a h6:hover { color: #3fc5d8; }
h1 a:visited, h3 a:visited, h4 a:visited, a:visited h6 { color: #1876ac; }

#Wrapper, #Content
	, .header-classic #Top_bar
	, .post-desc
	, .pager .pages { background-color: #222; }

#comments .commentlist>li .comment-body
	, .post-item
	, .Recent_posts ul li .desc { color: #989898; background: #222; outline: 1px solid #989898; }

img.scale-with-grid, #Content img {
	-webkit-filter: grayscale(0.8);
    filter: grayscale(0.8);
    transition: all 300ms;
    opacity: 0.6;
}

img.scale-with-grid:hover, #Content img:hover {
    -webkit-filter: grayscale(0);
    filter: grayscale(0);
    opacity: .99;
}

blockquote {
    background-color: rgba(224,224,224,.25);
    color: #bbb;
}
}
