	}

	/*Changed text highlight*/
	::selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	::-moz-selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}

	/*Outline for links*/
 	a
 	{
		outline-color: var(--main-color) !important;
	}

	body, html
	{
		background-color: var(--main-background) !important;
		color: var(--dimer-text) !important;
	}
	a, a:visited
	{
		color: var(--main-color) !important;
		opacity: .9;
		text-decoration: none !important;
	}
	a:hover
	{
		opacity: 1;
	}



	header.row, header.page
	{
		background-color: var(--main-background) !important;
		border-color: var(--hover-background) !important;
	}


	/*Logo*/
	h1.logo a
	{
		background-image: url("https://raw.githubusercontent.com/RaitaroH/Jisho-DeepDark/master/Images/logo_katakana.png") !important;
	}


	p, li, h1, h2, h3, h4
	{
		color: var(--main-text) !important;
	}


	.icon
	{
		fill: var(--main-text) !important;
	}
	.icon:hover
	{
		fill: var(--main-color) !important;
	}


	/*Footer*/
	footer .ornament
	{
		display: none !important;
	}
	footer
	{
		background-image: url("https://raw.githubusercontent.com/RaitaroH/Jisho-DeepDark/master/Images/footer.png") !important;
	}


	/*Search*/
	form.search .input_method_button:hover, form.search .input_method_button:hover > .icon, form.search .search-form_clear-button
	{
		background-color: transparent !important;
		fill: var(--main-color) !important;
	}
	form.search .input_method_button h4
	{
		color: var(--dimer-text) !important;
	}
	form.search .main, form.search .inner, form.search .submit
	{
		background-color: var(--second-background) !important;
	}
	form.search .keyword
	{
		color: var(--main-text) !important;
	}
	form.search .search_type
	{
		color: var(--dimer-text) !important;
	}
	form.search .search_type
	{
		border-color: var(--hover-background) !important;
	}
	.filter.small.button, .button
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.filter.small.button.active, .filter.small.button:hover, form.search .search_type:hover, .button:hover, form.search .input_method_button:hover h4
	{
		color: var(--main-color) !important;
	}
	form.search .submit:hover .search-icon
	{
		fill: var(--main-color) !important;
	}


	/*Draw*/
	.text_input .selected
	{
		background-color: var(--main-color) !important;
	}
	.speech_area_active #speech_button, .radical_area_active #radical_button, .handwriting_area_active #handwriting_button
	{
		border-bottom-color: var(--main-color) !important;
	}
	form.search .instructions .arrow, form.search .instructions .text
	{
		color: var(--main-text) !important;
	}
	.handwriting .panel, form.search .results .result, form.search .results .result_label
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	form.search .results .result:hover, form.search .results .result_label:hover, form.search .results .show_more:hover, form.search .results .show_less:hover
	{
		color: var(--main-color) !important;
	}
	form.search .input_methods, form.search .scroll_indicator
	{
		background: transparent !important;
	}

	/*Radicals*/
	.radical_table
	{
		background: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.radical_table .number, .radical_table .number:hover
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.radical_table li:hover
	{
		color: var(--main-color) !important;
	}
	.radical_table .radical.radical-image
	{
		background-image: none !important;
	}
	.radical.area.combined_mode .reset_radicals
	{
		color: var(--main-color) !important;
	}
	.radical.area.combined_mode .reset_radicals:hover
	{
		background: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	
	
	/*Inflection table*/
	table caption
	{
		color: var(--main-text) !important;
	}
	table tr th, table tr td
	{
		color: var(--dimer-text) !important;
	}


	/*Search results*/
	#main_results h4 .result_count, .concept_light-meanings .meaning-tags, .concept_light-meanings .meaning-abstract, .sense-tag.tag-restriction, .concept_light-meanings .meaning-definition-section_divider, .kanji_light .info, .concept_light-meanings .supplemental_info, .concept_light-meanings .sentences
	{
		color: var(--dimer-text) !important;
	}
	.concept_light-meanings .meaning-meaning, .concept_light-status .concept_light-status_link
	{
		color: var(--main-text) !important;
	}
	.concept_light-status .concept_light-status_link:hover
	{
		color: var(--main-color) !important;
	}
	.concept_light, .kanji_light, .sentences_block .sentence
	{
		border-color: var(--hover-background) !important;
	}
	.concept_light-status .concept_light-tag, .concept_light-status .concept_light-tag a
	{
		background-color: var(--hover-background) !important;
		color: var(--main-color) !important;
	}
	.fact
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
		outline: none !important;
	}
	.fact li
	{
		text-align: center;
	}
	.fact.grammar-breakdown h6
	{
		color: var(--dimer-text) !important;
	}

	/*Links dropdown*/
	.f-dropdown
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	.f-dropdown li:hover, .f-dropdown li:focus
	{
		background-color: var(--hover-background) !important;
	}
	.f-dropdown::before, .f-dropdown::after
	{
		border-bottom-color: var(--hover-background) !important;
	}
	.f-dropdown li a
	{
		color: var(--main-text) !important;
	}
	.f-dropdown li a:hover
	{
		color: var(--main-color) !important;
	}

	.kanji-details__main-readings-list a
	{
		border: none !important;
	}

	/*Kanji strokes*/
	.stroke_order_diagram--current_path
	{
		stroke: var(--main-text) !important;
	}
	.stroke_order_diagram--existing_path
	{
		stroke: var(--dimer-text) !important;
		opacity: .6;
	}
	.stroke_order_diagram--path_start
	{
		fill: var(--main-color) !important;
	}
	.stroke_order_diagram--bounding_box, .stroke_order_diagram--guide_line
	{
		stroke: var(--hover-background) !important;
	}
	.kanji_strokes canvas
	{
		filter: invert(90%) grayscale(100%);
	}
	.kanji_strokes .kanji_play_button
	{
		background: transparent !important;
	}
	.kanji_strokes.show_actions .actions
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.kanji_strokes .actions .ok_button
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.kanji_strokes .actions .ok_button:hover
	{
		color: var(--main-color) !important;
	}
	.noUi-base, .noUi-origin
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		border-radius: 0 !important;
	}
	.noUi-background
	{
		background-color: var(--main-color) !important;
	}
	.noUi-handle
	{
		background-color: var(--main-color) !important;
		border-color: var(--main-color) !important;
	}

	/*Dictionaries and such*/
	.tabs dd.active a, .tabs .tab-title.active a, .tabs dd > a, .tabs .tab-title > a, table
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.tabs dd.active a:hover, .tabs .tab-title.active a:hover, .tabs dd > a, .tabs .tab-title > a:hover
	{
		color: var(--main-color) !important;
	}
	table tr
	{
		background-color: var(--second-background) !important;
	}
	.dic_ref, .dic_name
	{
		color: var(--main-text) !important;
	}


	/*Forum*/
	.discussion_thread .comment_body .discussion_status, .discussion_thread .comment_body .comment_status
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.discussion_thread .comment_body
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.discussion_thread .comment_body .discussion_about
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}

	/*Forum comments*/
	.discussion-username__forum-flair
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.discussion-username__is-admin
	{
		background-color: var(--main-color) !important;
		border-color: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	.discussion_thread .comment_body.discussion__body--started-by-admin
	{
		outline-color: var(--main-color) !important;
	}
	code
	{
		background-color: var(--second-background) !important;
		border-color: var(--second-background) !important;
		color: var(--main-text) !important;
	}
	label
	{
		color: var(--main-text) !important;
	}


	.pagination span.disabled, .pagination a
	{
		border-color: var(--hover-background) !important;
	}


	/*Login*/
	.reveal-modal, dialog
	{
		background-color: var(--main-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.close-reveal-modal
	{
		color: var(--main-text) !important;
	}
	.close-reveal-modal:hover
	{
		color: var(--main-color) !important;
	}
	input[type="text"], input[type="password"], input[type="date"], input[type="datetime"], input[type="datetime-local"], input[type="month"], input[type="week"], input[type="email"], input[type="number"], input[type="search"], input[type="tel"], input[type="time"], input[type="url"], input[type="color"], input[type="submit"], textarea
	{
		background-color: var(--second-background) !important;
		border: 1px solid var(--hover-background) !important;
		color: var(--main-text) !important;
