css:
@-moz-document regexp("https://azrael\\.digipen\\.edu/~mmead/www/Courses/(2018/winter/cs170|CS170|2017/fall/cs120|CS120)/.*") {
.SectionHeader
  {
    background-color: rgb(100,100,100) !important;
    border-color: darkgray !important;
  }


  PRE.SourceCode2 {
     background-color : rgb(90, 90, 90) !important;
     border-color : darkgray !important;
  }

  PRE.SourceCode EM {
     color : #c7f !important;
  }

  PRE.SourceCode {
     background-color : rgb(90,90,90) !important;
     border-color : darkgray !important;
  }

  P.TechNote {
     background-color : #258 !important;
     border-color : darkgray !important;
  }

  H1, H2, H3 {
     color : #fff !important;
  }

  BODY {
     background : #2E3136 !important;
     color : #fff !important;
  }

  a {
     color : #8cf !important;
  }
  
  a:visited {
     color : #a9f !important;
  }

  a:active {
     color : #d06 !important;
  }
  
  font {
     filter : hue-rotate(120deg) invert(100%) !important;
     -webkit-filter : hue-rotate(120deg) invert(100%) !important;
  } /* WHY ARE THERE FONT TAGS WITH COLOR ATTRIBUTES IN 2017 */
  
  /* From styles.css--not perfect because images have colored backgrounds, so it's personal preference */
  .footnote, .warning, .info, .question {
     color: #FFF !important;
     background-color: #258 !important;
  }
}

@-moz-document regexp("https://azrael\\.digipen\\.edu/~mmead/www/Courses/(2018/winter/cs170|2017/fall/cs120)/(labs/labdata.html|practice/practicedata.html)") {
/* Exception for lab/practice data pages */
font[color="black"] {
    filter : none !important;
    -webkit-filter : none !important;
    color : white !important;
  }
}

@-moz-document regexp("https://azrael\\.digipen\\.edu/~mmead/www/Courses/(2018/winter/cs170|2017/fall/cs120)/(projects|practice|labs)/.*") {
/* Special notebook image for assignment pages (blanket application, so assignment pages that didn't have it before will have it) */
body {
    margin-left: 50px;
    margin-right: 20px;
    background-color : #2E3136 !important;
    background : url('data:image/jpeg;base64,') !important;
  }
}
