css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("byui.brightspace.com") {
	/* Version 1.1
	   Brandon Hartshorn brandonhartshorn@gmail.com
	   Adapted from Chrome extension by Austin Golding */
	/* The HUGE header title at the top of each page, redundant because breadcrumbs */
	.d2l-header-bottom-box-responsive {
		width: 15% !important;
		display: none !important;
	}
	/* Next two for course unit list on left */
	.d2l-body-compact {
		padding: 0 30px 0 14px !important;
	}
	.d2l-le-TreeAccordionItem {
		padding: 5px 0 5px 0 !important;
	}
	/* Above assignment lists */
	div[class^="d2l_1_119_"] {
		padding-top: 0 !important;
	}
	/* Padding above Unit title */
	.d2l-twopanelselector-padding {
		padding-top: 10px !important;
	}
	/* Hide Unit progress bar */
	.d2l-progresschart {
		display: none !important;
	}
	
	/* Unit progress bar padding */
	.progressChartLabel {
		padding-top: 0 !important;
	}
	
	/* Download, Send to Binder button hide (CAN BE FOUND IN DROP-DOWN ARROW NEXT TO UNIT NAME) */
	div[class^="d2l_1_105"] {
		display: none !important;
	}
	.d2l-heading-title {
		display: none !important;
	}
	.d2l-page-header {
		margin-bottom: 0 !important;
	}
	.d2l-widget-header {
		padding: 0;
	}
	/* Calendar Items */
	.d2l-datalist-item-content {
		padding-top: 0.2rem !important;
		padding-bottom: 0.2rem !important;
	}
	.d2l-widget {
		padding-bottom: 1rem;
	}
	.d2l-typography {
		font-family: Lato, 'Lucida Sans Unicode', 'Lucida Grande', sans-serif;
		letter-spacing: 0 !important;
		font-size: .85rem !important;
		font-weight: 100 !important;
		line-height: 1.2rem !important;
	}
	.d2l-typography .d2l-heading-2, .d2l-typography .vui-heading-2 {
		letter-spacing: 0 !important;
		font-size: .9rem !important;
		font-weight: 100 !important;
		line-height: 1.2rem !important;
	}
	.d2l-typography .d2l-heading-2, .d2l-typography .vui-heading-2 {
		letter-spacing: 0 !important;
		font-size: .95rem !important;
		font-weight: 100 !important;
		line-height: 1.2rem !important;
	}
	.d2l-max-width {
		margin: 0 auto !important;
		max-width: 100% !important;
	}
	.d2l-navigation-s-band-color {
		height: 0 !important;
		display: none;
	}
	.d2l-navigation-s-main-wrapper {
		height: 30px !important;
	}
	.d2l-navigation-s-header-container {
		height: 40px !important;
	}
	div {
		margin-top: 0 !important;
		margin-bottom: 0 !important;
	}
	.d2l-twopanelselector-side-bg.d2l-twopanelselector-side-sep, .d2l-twopanelselector-side.d2l-twopanelselector-side-sep {
		border-right: none !important;
		background: none !important;
	}
	body {
		background: none !important;
	}
}
