css:
@-moz-document domain("leagueofgraphs.com") {
  ::-webkit-input-placeholder {
      color: #aaa !important;
  }
  .top-bar, #pageContainer {
      max-width: 100% !important;
  }
  .top-bar, .top-bar .title-area, .top-bar-section .has-form, .top-bar-section li:not(.has-form) a:not(.button) {
      background-color: #2980B9 !important;
  }
  .top-bar-section li:not(.has-form).active a:not(.button) {
      background-color: #3498DB !important;
  }
  .top-bar #searchArea .search_field {
      background-color: #f0f0f0;
      color: #777;
  }
  .top-bar #searchArea .search_button {
      background-color: #555;
      padding-right: 10px;
      padding-left: 10px;
  }
  .accordion .accordion-navigation>a, .accordion dd>a, #sidebar {
      background: #323240;
  }
  #sidebar #sidebar-accordion .sidebar-navigation>a:hover, #sidebar #sidebar-accordion .sidebar-navigation:not(.active)>a:not(:hover), #sidebar #sidebar-accordion .sidebar-navigation.active>a {
      border-left: none;
  }
  #sidebar #sidebar-accordion .sidebar-navigation.active>a {
      border-bottom: none;
  }
  #sidebar #sidebar-accordion .sidebar-navigation :hover, #sidebar #sidebar-accordion .sidebar-navigation.active>a {
      color: #f0f0f0;
  }
  .accordion .accordion-navigation>a, .accordion dd>a {
      color: #999;
      font-weight: normal;
  }
  .accordion .accordion-navigation>a:hover, .accordion dd>a:hover {
      background: #292937;
  }
  #sidebar #sidebar-accordion .sidebar-navigation {
      border-bottom: #404050 1px solid;
  }
  .top-bar .name h1 a {
      background: url(http://i.imgur.com/6ZWoimy.png) center center no-repeat;
      display: block;
      width: 140px;
      height: 50px;
  }
  .top-bar .name h1 a img, .top-bar .left.hide-for-small-down {
      display: none !important;
  }

  .pageBanner:before {
      content: "";
      position: absolute;
      left: 0;
      width: 100%;
      height: 10.9375rem;
      background: inherit;
      filter: blur(5px) opacity(0.8) grayscale(1) brightness(50%);
      overflow: hidden;
  }
  .pageBanner .txt {
      position: absolute;
      left: 100px;
  }
  .pageBanner .img {
      position: absolute;
  }
  .sidebar-container {
      position: relative;
  }
}
