css:
@-moz-document domain(clien.net), domain(www.clien.net) {
body { min-width:1300px; }
body.bg { min-width:0; }

.header_wrap .nav_header { width:100%; }

.container { width:auto; }
.nav_container { width: calc(100% - 11px); min-width:0; }
.nav_body { width:calc(100% - 320px); }
.nav_content, .content_main  { width:100%; }
.main_top_ad { width:calc(100% - 46px); overflow:hidden; }
.main_top_ad #div-pc-main-top { width:calc(100% - 63px); }
.main_center_ad #div-pc-main-center { width:calc(100% - 63px); text-align:center; }
.main_center_ad #div-pc-main-center div { display:block; position:relative; margin:0 auto; }
.contents_main .section_list { width:24.3%; }
.contents_main .section_list.gallery { width:100%; position: absolute; top: 945px; }
.contents_main .section_gallery { max-height:none; }
.contents_main .section_gallery .gallery_list { width:49.3%; }
.contents_main .section_gallery .gallery_list .gallery_subject { width:100%; }
.contents_main .section_gallery .gallery_list .gallery_img::after { display:none; }
.contents_main .section_list .list_item { display:block; width:100%; }
.contents_main .section_list.notice .list_item { display:table; }
.section_list .list_item .list_title .list_subject { max-width:100%; }
.contact_name .nickname { max-width:100%; }
.book .book_item:nth-child(3n+0) { margin:0 46px 46px 0; }
.book .book_item:nth-child(5n+0) { margin:0 0 46px 0; }
.list_item.jirum .list_symph { width:3px; }
.list_item.jirum .list_title { width:calc(70% - 427px); }
.list_item.jirum .list_title.wide { width:calc(100% - 357px); height:51px; }
.list_item.jirum .list_author .list_author_line { width:237px; text-align:center; }
.list_item.jirum .list_title .list_subject,
.list_item.jirum .list_title.wide .list_subject { max-width:none; white-space: normal; }
.list_item.jirum .list_title:hover .keyword { position: relative; bottom: -5px; }
.list_item.jirum .list_title:hover .list_subject { padding:0; }
.list_item.jirum .list_title .list_reply { display:none; top:0px; }
.list_item.jirum .list_title:hover .list_subject .list_reply { display:inline-block !important; margin:0; top:0px; }
.list_item.jirum .list_title .list_subject .list_reply { display:inline-block; }

.card_item .card_contents { width:100%; }

.post_view .attached_image img { display:block; clear:both; }
.post_view .post_content img { display:block; }

.content_list, .content_view, .content_join, .content_error, .content_regist { width:100%; }
#div-pc-list-top, #div-pc-list-below { width:calc(100% - 31px); text-align:center; }
.list_item .list_author { width:237px; }
.list_item .list_author .nickname { width:213px; min-width:100%; white-space: nowrap; overflow: hidden; text-align:center; }
.list_item .list_author .nickname span { max-width:100%; width:100%; }
.list_item .list_author .nickname:hover { position:absolute; white-space:normal; height:auto; overflow:hidden; background-color:white; z-index:99; padding:7px 7px 7px 7px; margin-top:-16px; border:1px dotted #ccc; }
.list_item .list_author .nickname:hover span { white-space:normal; height:auto; overflow:visible; }
.list_item .list_title .list_subject { max-width:100%; }

.list_category { width:auto; }
.list_category .category_item { min-width:100px; }

.board_upper_image { text-align:center; }
.board_upper_image img { display:inline-block; }

.nav_container .snb { position: inherit; min-height: 1485px; overflow-y: hidden; top:0px; }

.content_somoim { width:100%; }
.somoim_main .main_ad #div-pc-main-center { width:calc(100% - 63px); text-align:center; }

.board_upper_image img { max-height:300px; width:auto; }
}
