css:
@-moz-document domain("afasinsite.nl") {
/* @-moz-document domain("afasinsite.nl") { */
html, body, div, p, span, li, a {
  font-family: -apple-system, BlinkMacSystemFont, 
    "Segoe UI", "Roboto", "Oxygen", 
    "Ubuntu", "Cantarell", "Fira Sans", 
    "Droid Sans", "Helvetica Neue", sans-serif !important;
}
.sitebackground {
	background-image: none; 
}
#P_H_W_1 {
  display: flex;
}
#P_H_W_3 {
	height: 28px;
  width: 100vw;
}
.zoneheader .menu .menuitem .text {
  line-height: 28px;
  height: auto;
}
.webbutton {
  border-radius: 2px;
  padding: 2px 5px;
}
.pagetitle h1 {
  color: black !important;
}
#P_H_W_Menu_Content > div li:last-child {
  /*display: none;*/
}
#P_H_W_Crumbpath {
  display: none;
}
/*.zonerow {
  display: flex;
  flex-direction: row;
}
.zonecell {
  display: block;
 	margin-bottom: 0; 
}*/
#P_mastercontent {
	padding-top: 20px;
}
.masterheaderback {
  background: #333333;
}
.masterheaderback tbody { 
	display: block;
}
/* Menubar */
.masterheaderback tbody tr:nth-child(2) {
  height: 36px;
  width: 100%;
  padding-right: 24px;
  display: flex;
  align-items: flex-start;
  justify-content: flex-end;
  box-sizing: border-box;
}
.masterheaderback tbody tr:nth-child(2)::before {
    position: absolute;
    height: 36px;
    background: #294151;
    width: 100vw;
    top: 0;
    left: 0;
    content: '';
    z-index: -3;
}
/* Main menu item-row  */
.masterheaderback tbody tr:nth-child(3) {
  position: absolute;
  top: 5px;
  overflow: visible;
  width: 70vw;
}
/* Main menu items */
.menuitemtreeLevel0 .treelevel0 {
  font-size: 12px !important;
 	padding-left: 8px !important;
 	padding-right: 8px !important;
}

/* Menu foldout */
.zoneheader .menu.topfold .menutreeHorizontal li ul {
  margin-top: 3px;
  border-radius: 0 0 2px 2px;
}
.masterheaderback tbody > tr:nth-child(2) > td:nth-child(1) {
	display: none;
}
.masterheaderback tbody > tr:nth-child(2) > td:nth-child(2) {
	display: flex;
  position: relative;
  top:4px;
  align-items: flex-end;
  z-index: 9999;
}
.masterheaderback tbody tr:nth-child(3) .menuitem > a.treelevel0 {
  color: #d8d8d8 !important;
}
/* .masterheaderback tbody > tr:nth-child(2) > td:nth-child(2)  > div::first-child {

} */
.nobackgroundonmobile {
  background-image: none !important;
}
#P_H_W_Logo {
  visibility: hidden;
  height: 0;
}
#P_H_W_Login {
  padding-top: -1px;
  padding-right: 10px;
}
#P_H_W_Login_LiteralLoginWebPart_OptionsMenu {
  color: #8c8c8c;
}
input.searchquery {
  background: #484848;
  border-radius: 2px;
  border: none;
  padding: 4px 8px 5px 8px;
  color: #e0e0e0;
}
#P_H_W_Search_ButtonSearchWebPart_Search {
  background: transparent;
  border-radius: 2px;
  border: 1px solid #5a5a5a;
  min-width: inherit;
  outline: none;
  margin-left: 10px;
}
#P_H_W_Search_ButtonSearchWebPart_Search > span {
  color: #777777 !important;
  padding: 4px 6px 5px 6px;
}
.mastermiddleback {
  padding: 0 20px;
}

/* Menu text color */
.zoneheader .menu .menuitem.normal .text, .zoneheader .menu .webbutton.normal .webbutton-text {
  color: black;
}
/* Header text */
.part .partheader .headertext, .part .fixedpartheader .headertext {
 color: black;
  font-size: 20px;
}
/* Table header text color */
.webview>.viewdata>.scroll>.grid>.head>.filterRow>.filterColumn,
.webview>.viewdata>.scroll>.grid>.head>.headerRow>.rowSelector, .webview>.viewdata>.scroll>.grid>.head>.headerRow>.headerColumn {
  border-bottom: solid 2px #b7b7b7;
}

.webview>.viewdata>.scroll>.grid>.head>.headerRow>.headerColumn>.label {
  color: black;
  
}
/* Table content text */
.webview>.viewdata>.scroll>.grid>.body>.dataRow>.dataColumn {
  color: black;
}
.content.webviewpart .title {
 color: #828282 !important; 
}
/* Anchor text */
body a {
	color: black;
}


.arrow {
  color: transparent;
}
.arrow:after {
    content: '★';
    width: 0px;
    height: 0px;
    position: absolute;
    color: white;
    margin-left: -10px;
}
}

@-moz-document regexp(".*afasinsite.nl/") {
/*
CONTENT
In quite some cases, content is too generic to target specific 
elements without undesirably affecting element on other pages.
So we’ll resort to url specific styling.
*/
/* @document regexp("\.nl/?$") { */
#P_C_W_ZoneLayout .zonetable:nth-child(5),
#P_C_W_ZoneLayout .zonetable:nth-child(2) {
 display: none !important;
}
#P_C_W_5, #P_C_W_6, #P_C_W_, #P_C_W_8, #P_C_W_9, #P_C_W_10 {
  display: none;
	/* inconsisten html, cant be fixed : ( */
}


#P_H_W_ExternalMedia {
  display: none;
}
#P_C_W_CC446AC442A784CE69EE838DCD13FBF0 {
  display: none;
}
#P_C_W_913441AE4FCB483AAD6281B56E0384D2_ABBS {
  border-radius: 4px;
}

/* Handige links text */
.freehtmltextfontcolor {
	color: black !important; 
}
/* } */
}

@-moz-document regexp(".*afasinsite.nl/mijn-insite/?") {
#P_C_W_ZoneLayout > .zonetable .zonecell  {
  padding-top: 0px !important;
}
#P_C_W_ZoneLayout > .zonetable:first-child {
  display: none;
}
.picturepreview {
  border-radius: 2px;
}
}

@-moz-document domain("adfs.afasgroep.nl") {
html, body, div, p, span, li, a {
  font-family: -apple-system, BlinkMacSystemFont, 
    "Segoe UI", "Roboto", "Oxygen", 
    "Ubuntu", "Cantarell", "Fira Sans", 
    "Droid Sans", "Helvetica Neue", sans-serif !important;
}
#fullPage {
 display: flex;
	align-items: center;
  justify-content: center;
}
#brandingWrapper {
	display: none;
}

#header {
	display: none;
}

#contentWrapper, #content {
  height: auto;
  width: auto;
  margin: 0;
  padding: 0;
  overflow: visible;
}
#workArea {
 	width: 250px; 
  margin: 0;
  margin-top: 140px;
  position: relative;
}
#workArea::before {
  content: 'hi';
  position: absolute;
  z-index: 999;
  top: -140px;
  left: 0px;
  width: 110px;
  height: 110px;
  background-image: url(https://www.werkenbijafas.nl/themes/01d33785a35c7580/mobilelogo.png);
  background-size: 80%;
  background-position: center;
  background-repeat: no-repeat;
  background-color: black;
  border-radius: 8px;
}
#loginMessage {
  font-size: 20px;
  margin-bottom: 30px;
}
#userNameInput, #passwordInput {
  border-radius: 2px;
  border: 1px solid rgba(0,0,0,0.2);
  padding: 3px 16px;
}
#userNameInput::placeholder, #passwordInput::placeholder {
  color: silver;
  font-weight: normal;
}
#userNameInput:focus, #passwordInput:focus { 
  outline: none;
  border-color: black;
}
#submissionArea {
  margin: 30px 0 0 0;
}
#submitButton {
  padding: 5px 18px 7px;
	border-radius: 3px;
  background: black;
}
#submitButton:focus {
  outline: none;
}
#footer {
  display: none;
}
}

@-moz-document regexp(".*afasinsite.nl/aanpassen-mijn-uren-per-week-prs/standaard-prs") {
.monthdate .date.today {
  border-color: #005FAA !important;
}
.block {
  border-radius: 2px !important;
}
.block.blocktype0.hover {
  background-color: hsla(206, 75%, 88%, 1) !important;
}
.block .proj {
  margin-bottom: 4px;
  font-size: 11px !important;
}
.block .desc {
  margin-bottom: 5px;
  font-size: 15px !important;
  line-height: 1.1;
}
.block .desc2 {
  font-size: 13px !important;
}
.block .hours {
  background-color: transparent !important;
}

.hoverblock.hover .hoverhours {
  color: rgba(0,0,0,.4) !important;
  font-size: 23px;
}
.itemblocks {
  border-bottom: none;
}
.gridcontainer > .grid > .grid-body > .grid-rows > .grid-row {
  border-bottom: 1px solid rgba(0,0,0,0.2) !important;
}
.itemblock.disabled {
 	background: rgba(0,0,0,0.1) !important; 
}
.itemblock.disabled.last {
	border-bottom: none !important;
}  

.gridcontainer.impl1 {
  border: none;
}
.grid-calendarblock {
  background: rgba(0,0,0,0.03) !important;
  border-left: 1px solid rgba(0,0,0,0.1) !important;
  top: 0;
  bottom: 0;
}

.grid-calendarblock > div {
  border-radius: 0 2px 2px 0;
  border: 1px solid rgba(0,0,0,0.1);
  border-left-width: 0;
 	display: flex;
  flex-direction: column;
  height: 100%;
}
.grid-calendarblock > div > buttons{
  flex: 1;
  display: flex;
  justify-content: flex-end;
  align-items: center;
}

.navtitle {
  color: rgba(0,0,0,.9) !important;
}

.buttons button {
  font-size: 16px;
  padding: 8px !important;
  background-color: rgba(0,0,0,0.15) !important;
  border-color: rgba(0,0,0,0.16) !important;
  transition: all 150ms;
  outline: 0;
}
.buttons button span {
	color: rgba(0,0,0,0.6) !important;
  transition: all 150ms;
}
.buttons button:hover {
  background-color: rgba(0,0,0,0.20) !important;
  border-color: rgba(0,0,0,0.21) !important;
}
.buttons button:hover span {
	color: rgba(0,0,0,0.7) !important;
}

}
