css:
@-moz-document domain("kcw.kddi.ne.jp") {
/* general, logo, background -------------------------------------------------*/

img { image-rendering: pixelated;}

#_logo > img { display: none;}

#_logo {
    width: 143px;
    height: 59px;
    background-image: url(data:image/png;base64,);
}

body {
    background-image: url(http://www.strapya-korea.com/image/makeshop/banner/iface/iface_f.jpg);
    background-size: auto 800px;
    background-position: right -20px bottom 60px;
    background-repeat: no-repeat;
    background-color: #000;
}
#_wrapper, #_chatContent {
    background: transparent;
}



/* chat message -------------------------------------------------------*/

.chatRoomHeader { background-color: #000; color: #eee;}
.chatRoomHeader__actionButtonContainer > div { fill: silver; }

/* 하루의 시작마다 날짜가 표시되는데 */
.timeLine__dateHead { border-top: 1px dashed #969595;}
.timeLine__dateHeadBody { background: #000; color: white; }
.timeLine__dateHeadBody .icoFontClock { color: white; }
.timeLine__dateHeadText { color: white; }

/* 메시지 호버 */
.timelineMessage:hover { background: #66a3003d; }

/* 메시지 퀵바 */
.timelineMessage .actionNav { background: rgba(255, 255, 255, 0.21); border: 0;}
.actionArea { bottom: -23px;}
.actionNav__itemLabel { color: white;}
.actionNav svg { fill: white;}
.actionNav .icoFontActionQuote { color: white;}
.actionNav .icoFontActionTask { color: white;}
.actionNav .icoFontActionLink { color: white;}
.actionNav .icoFontActionMore { color: white;}

/* 나를 멘션한 메시지 */
.timelineMessage--mention { background: transparent; border-left: 5px solid #66a300 !important;}

/* 메시지 상하 보더 제거 */
.timelineMessage--border, .timelineMessage__body { border-top: 0 !important; border-bottom: 0 !important; }

/* 메시지 내용 */
.timelineMessage__message { color: #eee;}
.timeStamp { color: #c1d0d8;}
.chatName { color: #eee;}
.timelineMessage__orgName { display: none;}

/* 코드 블럭 */
.chatCode { background-color: #cacaca; }

/* 챗봇 배경색 */
.chatInfo { background: rgba(255, 255, 255, 0.21); display: inline-block; }
.chatInfo>.title { background: rgba(242, 242, 242, 0.18); font-size: 12px; color: #ccc; }
.chatInfo .icoFontInfo { color: #eee; }

/* 이미지 미리보기 크게 */
.imagePreview { height: 200px !important; }



/* task ------------------------------------------------------------------ */

.chatInfoTaskMetaArea { background-color:  inherit; }
.chatInfoTaskArea { background-color: inherit; }
.taskComplete { color: #ddd; }
.taskComplete .chatInfoTaskMetaArea { background-color: #ababab; }




/* chat writer --------------------------------------------------------------------*/

.chatInput__textarea { background: #000; color: #eee;}




/* search chat messages -----------------------------------------------------------*/

.searchResultListBox { background: #000; }




/* Right Side ------------------------------------------------------------------*/
#_subContentAreaContent { color: #eee;}
#_subRoomDescription pre { color: #eee;}
#_subRoomMyTaskList * { color: #eee;}
img.roomTask__emptyImage { display: none;}

.subRoomInformation__inviteButton { display: inline;}
.subRoomInformation__inviteButton { background: #000; color: #eee; border: 1px solid #999;}
.subRoomInformation__inviteButtonIcon { color: #eee;}
.subRoomInformation__inviteButton:hover { background: #000;}
.roomTask__header { background-color: #000; color: #eee;}
.roomDescription__description--overflow:hover {background: transparent;}
.addTaskContainer__formContainer { background-color: #333;}
.addTaskContainer__textarea { background: #000;}



/* Left Side --------------------------------------------------------------------*/
.roomList::-webkit-scrollbar {
    width: 6px;
    background-color: #000;
}
.roomListItem {
    background: linear-gradient(to bottom, #4c4c4c 0%,#595959 12%,#666666 25%,#474747 39%,#2c2c2c 50%,#000000 51%,#111111 60%,#2b2b2b 76%,#1c1c1c 91%,#131313 100%);
    color: #eee;
    border: 0;
}
.roomListItem:hover { 
    background: linear-gradient(to top, #4c4c4c 0%,#595959 12%,#666666 25%,#474747 39%,#2c2c2c 50%,#000000 51%,#111111 60%,#2b2b2b 76%,#1c1c1c 91%,#131313 100%);
    color: #eee;
}
.roomListItem__pin {fill: #999;}
.button { background-color: #000; color: #aaa;}
.roomFilterMenu { background: #000; border: 0;}
.roomListContainer__header { background-color: #000; color: #aaa;}
.uiDropDown__button { background-color: #000; color: #aaa;}
.uiDropDown__labelIcon { fill: #aaa;}
.uiDropDown__icon { fill: #aaa;}
.roomListItem__roomName { color: silver; }
.roomListItem__roomName--unread { color: #eee;}



}
