css:
@document domain(byui.brightspace.com) {
.d2l-navigation-s-header-container{
    height: 55px;
}

.d2l-navigation-s-main-wrapper{
    height: calc(1rem + 20px);
}

.daylight .d2l-widget{
    margin-bottom: 25px;
    padding-bottom: 30px;
}

.d2l-typography .d2l-heading-1, .d2l-typography .vui-heading-1{
    font-size: 1.4rem;
}

.d2l-typography{
    font-size: .85rem;
    line-height: 1.1rem;
    color: #eee;
}

.d2l-typography .d2l-heading-2, .d2l-typography .vui-heading-2{
    font-size: 1.2rem;
}

.vui-input[type=email], .vui-input[type=email]:hover:disabled, .vui-input[type=password], .vui-input[type=password]:hover:disabled, .vui-input[type=text], .vui-input[type=text]:hover:disabled, .vui-input[type=url], .vui-input[type=url]:hover:disabled{
    padding: 0 5px 0;
}

.vui-input[type=email]:hover, .vui-input[type=password]:hover, .vui-input[type=text]:hover, .vui-input[type=url]:hover{
    padding: 0 5px 0;
}

.vui-input[type=email]:focus, .vui-input[type=password]:focus, .vui-input[type=text]:focus, .vui-input[type=url]:focus{
    padding: 0 5px 0;
}

.vui-input[type=email], .vui-input[type=password], .vui-input[type=text], .vui-input[type=url]{
    padding: 0 5px 0;
    font-size: .85rem;
}

.d2l-button, .vui-button, .vui-button-primary{
    padding: 0 10px 0;
    min-height: calc(1rem + 10px);
}

table.d_t td.d_tt{
    vertical-align: bottom;
}

.d2l-page-header {
    margin-bottom: 10px;
}


.daylight .d2l-le-TreeAccordionItem-wrapper{
    
    padding: 5px 20px 5px 10px;
}

.d2l-progresschart {
    display: none;
}

.d2l-page-bg > div, .d2l-page-main{
    background: #111;
}

#courses>div>div{
    padding: 10px;
}

.d2l-navigation-s-logo img {
    max-height: 35px;
}

#upcomingTable a{
    color: #ddd;
}

.daylight .d2l-msg-container{
    background: #222;
}

.d2l-navigation-s, .d2l-navigation-s-no-login, .d2l-navigation-s-button-highlight{
background: #222;
}

#d2l_body, .d2l-body{
background: #111;
}

.d2l-navigation-s-shadow-gradient{
display: none;
}
}
