css:
@-moz-document domain("youtube.com") {
/*latin*/
@font-face {
  font-family: 'Rosario';
  font-style: italic;
  font-weight: 400;
  src: local('Rosario Italic'), local('Rosario-Italic'), url(https://fonts.gstatic.com/s/rosario/v11/qPuMCAX6nBODQfYbsOfqLltXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
}

/* latin */@font-face {  font-family: 'Satisfy';  font-style: normal;  font-weight: normal;  src: local('Satisfy Regular'), local('Satisfy-Regular'), url(https://fonts.gstatic.com/s/satisfy/v7/2OzALGYfHwQjkPYWELy-cw.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;}

/* latin-ext */@font-face {  font-family: 'Dancing Script';  font-style: normal;  font-weight: 400;
  src: local('Dancing Script Regular'), local('DancingScript-Regular'), url(https://fonts.gstatic.com/s/dancingscript/v8/DK0eTGXiZjN6yA8zAEyM2e87R-l0-Xx_7cYc0ZX1ifE.woff2) format('woff2');  unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;}
/* latin */@font-face {  font-family: 'Dancing Script';  font-style: normal;  font-weight: 400;  src: local('Dancing Script Regular'), local('DancingScript-Regular'), url(https://fonts.gstatic.com/s/dancingscript/v8/DK0eTGXiZjN6yA8zAEyM2Ud0sm1ffa_JvZxsF_BEwQk.woff2) format('woff2');  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;}

.title.ytd-video-primary-info-renderer 
{
    color:peachpuff !important;
    font-family: 'Dancing Script', cursive !important;
    font-size: 22px !important;
}

#author-text.yt-simple-endpoint.ytd-comment-renderer
,.ytp-title-link.yt-uix-sessionlink
,#channel-title 
{
    color:burlywood !important;
    font-family: 'Dancing Script', cursive !important;
    font-size: 22px !important;
}

#content-text 
,#textarea
{
    font-family: 'Satisfy', cursive !important;
    font-size: 16px !important;
    color:burlywood        !important;
    line-height: unset !important;
}

ytd-searchbox[mode=legacy] #container.ytd-searchbox input.ytd-searchbox 
{
    color: crimson;
    font-family: 'Satisfy', cursive !important;
    font-size: 20px;
}

body 
,#container 
,#top
,#page-manager
,.ytd-guide-section-renderer
,.style-scope.ytd-guide-renderer
,ytd-app 
,#columns
{    
    background: linear-gradient(to right, #010101,#000000, #111111) !important; 
    transition: background 5s!important;
    font-family: 'Rosario', sans-serif !important;
}

.style-scope.ytd-browse
{    
    background: linear-gradient(to right, #010101,#111111) !important; 
    transition: background 5s!important;
    font-family: 'Rosario', sans-serif !important;
}
.ytp-menuitem-label
,.html5-video-info-panel
{    
    transition: background 5s!important;
    font-family: 'Rosario', sans-serif !important;
}

.ytd-searchbox *
{
        border: 0px !important;
}

.ytp-progress-list{
    background-color: rgba(0, 0, 0, 0.1);
}
.ytp-play-progress.ytp-swatch-background-color{
    background: linear-gradient(to right, burlywood,Bisque ) !important;
}
.ytp-load-progress{
    background: linear-gradient(to right, LightCoral,darkred ) !important;
}
.ytp-hover-progress.ytp-hover-progress-light{
    background-color:mediumslateblue !important;
}

#autoplay.ytd-compact-autoplay-renderer
,#description-text.ytd-video-renderer
,#title.ytd-shelf-renderer
,#upnext.ytd-compact-autoplay-renderer
,#metadata-line
,#length
,#stats
,#middle-metadata-section
,.metadata-value.style-scope.ytd-movie-upsell-renderer
,.date.ytd-video-secondary-info-renderer
{
 color:darkred !important;
}

#movie_player
,#tabs-inner-container
,#channel-header
,#search-icon-legacy
,.style-scope.ytd-page-manager
,.yt-card
{    
    background: linear-gradient(to right, #000000,#333333, #000000) !important; 
    transition: background 5s!important;
}

#guide-icon.ytd-masthead
,#guide-section-title.ytd-guide-subscriptions-section-renderer
,.style-scope.ytd-multi-page-menu-renderer
,.style-scope.ytd-secondary-search-container-renderer
,.dropdown-content.style-scope.ytd-popup-container
,#yt-masthead-container
,#footer-container
,.dropdown-content.style-scope.yt-dropdown-menu
,.style-scope.ytd-menu-popup-renderer
,.style-scope.yt-next-continuation
,.ytg-1col.yt-card
{
    background: linear-gradient(to right, tan,wheat) !important;
}

#subscribe-button 
,paper-button.ytd-subscribe-button-renderer
{
    background: linear-gradient(to right, chocolate,firebrick) !important;
}
#subscribe-button /*
,#primary.ytd-two-column-browse-results-renderer
,#collapse.ytd-search-sub-menu-renderer */
,#guide-container
{
    background: linear-gradient(to right, BurlyWood ,Peru ) !important;
}

#contents.ytd-section-list-renderer > *.ytd-section-list-renderer:not(:last-child)
,#sections.ytd-guide-renderer > *.ytd-guide-renderer:not(:last-child)
,#content.ytd-playlist-video-renderer
{
    border-bottom:2px solid  black!important;
}
ytd-video-primary-info-renderer
,ytd-video-secondary-info-renderer
,ytd-movie-upsell-renderer
,#placeholder-area.ytd-comment-simplebox-renderer
{
    border-bottom:0.5px solid  tan!important;
}
.content.ytd-video-secondary-info-renderer 
,#subtitle.ytd-shelf-renderer
,span.yt-view-count-renderer
,#result-count.ytd-search-sub-menu-renderer
,.ytp-menuitem-label
,.html5-video-info-panel
,#simplebox-placeholder.ytd-comment-simplebox-renderer
,.title.ytd-guide-entry-renderer
{
    color:burlywood !important;
}
#vote-count.ytd-comment-action-buttons-renderer
,#metadata.ytd-channel-renderer
,#title-annotation
,.style-scope.yt-icon
,.tab-content.style-scope.paper-tab
,#title
,#subscriber-count
,.style-scope.ytd-sub-feed-option-renderer
,.content.style-scope.ytd-channel-video-player-renderer
,#description
{
    color:burlywood !important;
}


.header.ytd-playlist-panel-renderer
,.header.ytd-playlist-panel-renderer
,.playlist-items.ytd-playlist-panel-renderer
,.style-scope.ytd-page-manager
{
    background-color:unset !important;
}

#guide-icon.ytd-masthead
,paper-toggle-button[checked]:not([disabled]) .toggle-bar.paper-toggle-button
,.toggle-button.paper-toggle-button
{
    background: maroon;
}
paper-toggle-button[checked]:not([disabled]) .toggle-button.paper-toggle-button
{
    background-color: red;
}

.align-by-text.ytd-comment-replies-renderer
,#title *
,.title.ytd-mini-channel-renderer
,#channel-title.ytd-channel-renderer
,#vote-count-middle.ytd-comment-action-buttons-renderer
,#filter-group-name.ytd-search-filter-group-renderer
{
    color:burlywood !important;
    text-shadow:1px 1px maroon;
}

#main.ytd-watch, 
#top.ytd-watch
,ytd-watch:not([theater]) #top.ytd-watch
{
max-width:100%;
    width:100%;
    margin-top:0px;
}

a
,.pagination ul li a 
,.more-button.ytd-comment-renderer, .less-button.ytd-comment-renderer
,body:not([dark]) #search
,#vote-count-left.ytd-comment-action-buttons-renderer
,ytd-search-filter-renderer yt-formatted-string.ytd-search-filter-renderer
{
    color: firebrick !important;
}
a:active
,#meta.ytd-playlist-panel-video-renderer:active *
,a.ytd-compact-radio-renderer:active *
,a.yt-simple-endpoint.ytd-compact-video-renderer:active *
,#title:active *
,.ytd-playlist-panel-video-renderer:active *
,.style-scope.ytd-newspaper-hero-video-renderer:active *
,.style-scope.ytd-playlist-video-renderer:active *
,.style-scope.ytd-grid-playlist-renderer:active *
,.style-scope.ytd-radio-renderer:active *

,a:link
,#meta.ytd-playlist-panel-video-renderer:link *
,a.ytd-compact-radio-renderer:link *
,a.yt-simple-endpoint.ytd-compact-video-renderer:link * 
,#title:link *
,.ytd-playlist-panel-video-renderer:link *
,.style-scope.ytd-newspaper-hero-video-renderer:link 
,.style-scope.ytd-playlist-video-renderer:link *
,.style-scope.ytd-grid-playlist-renderer:link *
,.style-scope.ytd-radio-renderer:link *
{
    color: crimson !important;
}

a:visited
,#meta.ytd-playlist-panel-video-renderer:visited *
,a.ytd-compact-radio-renderer:visited *
,a.yt-simple-endpoint.ytd-compact-video-renderer:visited * 
,#title:visited *
,.ytd-playlist-panel-video-renderer:visited *
,.style-scope.ytd-newspaper-hero-video-renderer:visited *
,.style-scope.ytd-playlist-video-renderer:visited *
,.style-scope.ytd-grid-playlist-renderer:visited *
,.style-scope.ytd-radio-renderer:visited *
{
    color: BlanchedAlmond     !important;
}

a:hover
,#meta.ytd-playlist-panel-video-renderer:hover *
,a.ytd-compact-radio-renderer:hover *
,a.yt-simple-endpoint.ytd-compact-video-renderer:hover * 
,#title:hover *
,.ytd-playlist-panel-video-renderer:hover *
,.style-scope.ytd-newspaper-hero-video-renderer:hover *
,.style-scope.ytd-playlist-video-renderer:hover *
,.style-scope.ytd-grid-playlist-renderer:hover *
,.style-scope.ytd-radio-renderer:hover *
{
    color: tan!important;
}
a:hover.active {color: red !important;}

.video-annotations
,#sentiment
{
    display:none!important;
}
.ytp-popup{
background:rgba(0,0,0,0.6)
}
#player:after
,#footer:after
{
    content:"Themed by Enz";
    color:#775555;
}

:-webkit-scrollbar-track{-webkit-box-shadow: inset 0 0 6px rgba(50,50,100,0.3);	border-radius: 10px;background-color: black;}
::-webkit-scrollbar{	width: 12px;background: linear-gradient(black,#222222,black) !important;}
::-webkit-scrollbar-thumb{	border-radius: 10px;	-webkit-box-shadow: inset 0 0 6px rgba(100,100,150,.3);background: linear-gradient( crimson,indianred) !important;}
}
