css:
@-moz-document domain("trello.com") {
.body-tabbed-page {
    background: rgb(41, 41, 41);
    color: white;
}
a.board-tile {
    box-shadow: 0 4px 12px 0 rgba(0, 0, 0, 0.36);
    transition: all 0.15s;
        border-radius: 6px;
}
a.board-tile.mod-add {
    background: rgba(0, 0, 0, 0);
    box-shadow: none;
    border: 1px dotted rgba(250, 250, 250, 0.12);
}
a.board-tile.mod-add:hover {
    background: rgba(220, 220, 220, 0.05);
    box-shadow: none;
    border: 1px dotted rgba(250, 250, 250, 0.12);
    color: rgba(255, 255, 255, 0.58);
}
a.boards-page-board-section-header-options-item.dark-hover {
    background: rgba(0, 0, 0, 0);
}
.board-tile:hover .board-tile-fade {
    background-color: rgba(0, 0, 0, 0);
}

.board-tile:hover {
    color: rgb(255, 255, 255);
    margin-top: -2px;
    box-shadow: 0 22px 27px 0 rgba(0, 0, 0, 0.2);
}

.board-tile-fade {
    transition: all 0.15s;
    border-radius: 6px;
}

div#header {
    background: rgb(41, 41, 41);
    box-shadow: none;
}

.body-board-view #header {
    background: rgba(0, 0, 0, 0);
}

.tabbed-pane-main-col {
    background: rgba(0, 0, 0, 0);
}

a.tabbed-pane-nav-item-button.js-org-profile.active {
    background: rgb(41, 41, 41);
    color: rgb(255, 255, 255);
    border: 1px solid rgba(255, 255, 255, 0.13);
    border-bottom: 0;
}
a.tabbed-pane-nav-item-button.js-org-profile.active:hover {
     box-shadow: 0 0 0 0 rgba(255, 255, 255, 0.41) inset;
}

.tabbed-pane-header, .tabbed-pane-nav {
    background-color: rgba(237, 239, 240, 0.05);
    color: rgba(255, 255, 255, 0.65);
}

.tabbed-pane-nav {
    border-bottom: 1px solid rgba(255, 255, 255, 0.06);
}

.tabbed-pane-nav-item-button {
    background-color: rgba(0, 0, 0, 0);
    border: 1px solid rgba(255, 255, 255, 0);
    border-bottom: 0;
    color: rgba(255, 255, 255, 0.48);
        transition: all 0.15s;
}

.tabbed-pane-nav-item-button:hover {
    background: rgba(0, 0, 0, 0);
    border-color: rgba(0, 0, 0, 0);
    color: rgb(255, 255, 255);
    box-shadow: 0 -2px 0 0 rgba(255, 255, 255, 0.41) inset;
}

a.tabbed-pane-header-image.profile-image {
    box-shadow: 0 5px 9px 0px rgba(0, 0, 0, 0.15);
}

.board-tile-details-name {
	    text-shadow: 0 2px 2px rgba(0, 0, 0, 0.48);
}

.board-tile-details-name {
	transition: all 0.15s
}

a.board-tile:hover > span > .board-tile-details-name {
	text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
    margin-top: -2px
}
}
