    font-size: 1.3rem;
    line-height: 20px;
    border-color: var(--wrc); }

body.win label {
    height: 28px;
    line-height: 20px;
    text-shadow: none;
    color: var(--col1);
    background: var(--btc2);
    border-color: var(--wrc); }

body.win label:hover {
    background: var(--btc2h); }

body.win label.choose {
    background: var(--btc5);
    border-color: var(--wrc); }

/* ブログテーマ編集でウインドウ幅を確保 */
body.win {
    position: relative;
    min-width: 960px;
    background: var(--bgc1); }

#wrapper {
    min-width: 960px;
    max-width: 1500px;
    min-height: 0;
    margin-bottom: 5px;
    background: none; }

header.global-header {
    display: none; }

#wrapperInner {
    padding: 0; }

footer.global-footer {
    display: none; }

.sticky-feedback {
    display: none; }

/* 編集画面ベース */
#container.hasmenu {
    margin: 0;
    float: none; }

.hasmenu #contents {
    margin: 0;
    padding: 0 15px 0 15px;
    /* 編集枠左余裕 画像パレット 右余裕 */
    background: var(--bgc1);
    border: none;
    border-radius: 0; }

header.boxHead {
    display: none; }

/* テンプレート選択 ***********************************/
