/* Nights
 * V1.0
 * BY ZTITUS.DEVIANTART.COM */
 
/* SETTINGS */

:root {
    
    --main-window-bg-color: #171717;
    --main-nav-bg-color: #2b2b2b;
        
    --main-bookmark-bg-color: #2b2b2b;
    --main-bookmark-text-color: #CCCCCC;
        
    --main-url-bg-color: #2b2b2b;
    --main-url-text-color: #888888;

    --main-tab-active-bg-color: #2b2b2b;
    --main-tab-active-hover-bg-color: #2b2b2b;
    --main-tab-active-text-color: #FFFFFF;
    --main-tab-inactive-bg-color: #171717;
    --main-tab-inactive-hover-bg-color: #1f1f1f;
    --main-tab-inactive-text-color: #999999;
    --main-tab-inactive-text-hover-color: #EEEEEE;
        
    --main-new-tab-inactive-bg-color: #171717;
    --main-new-tab-inactive-hover-bg-color: #1f1f1f;
        
    --main-border-radius: 0px;
    --main-theme-border-color: rgba(0, 0, 0, .2);
    --main-page-border-color: rgba(0, 0, 0, .2);
    --main-font-family: "Segoe UI Semibold", sans-serif;
    --main-theme-font-size: 14px;
}


/* ::::::::::::::::::::::::::::::::::::::::::::::::::::::::: THEME ::::::::::::::::::::::::::::::::::::::::::::::::::::::: */

/* PERSONNALISER */
    
#main-window[customize-entered] > #tab-view-deck {
  background-image: none !important;
  background-color: var(--main-window-bg-color) !important;
  background-attachment: fixed;
}
    
/* TABS BACKGROUND SETTINGS */

#main-window[sizemode="normal"]:not([inFullscreen]) #TabsToolbar {
    background: var(--main-window-bg-color) !important;
}
#main-window[sizemode="maximized"] #TabsToolbar {
    background: var(--main-window-bg-color) !important;
    margin-top: 0px !important;
}
#TabsToolbar:not(:-moz-lwtheme)::after {
    display: none;
}


/* TABS */

#TabsToolbar .tabbrowser-tab[selected] {
    font-weight: normal !important;
    border: 0px solid var(--main-theme-border-color) !important;
    border-bottom: 0px solid transparent!important;
    border-radius: 0px !important;
    background: var(--main-tab-active-bg-color) !important;
    color: var(--main-tab-active-text-color) !important;
    transition-property: background, color;
    transition-duration: 0ms;
}

#TabsToolbar .tabbrowser-tab {
    text-align: left!important;
    -moz-border-top-colors: none !important;
    -moz-border-left-colors: none !important;
    -moz-border-right-colors: none !important;
    -moz-border-bottom-colors: none !important;
    background: var(--main-tab-inactive-bg-color) !important;
    border: 0px solid var(--main-theme-border-color)!important;
    border-bottom: 0px solid transparent!important;
    border-radius: 0px !important;
    color: var(--main-tab-inactive-text-color) !important;
    margin: 0px -1px 0px 0px !important;
    padding: 0px 0px 1px 0px;
    font-family: var(--main-font-family) !important;
    font-size: var(--main-theme-font-size) !important;
    transition-property: background, color;
    transition-duration: 150ms;
}

#TabsToolbar .tabbrowser-tab:hover:not([selected]) {
    font-weight: normal !important;
    border: 0px solid var(--main-theme-border-color)!important;
    border-bottom: 0px solid transparent!important;
    background: var(--main-tab-inactive-hover-bg-color) !important;
    color: var(--main-tab-inactive-text-hover-color) !important;
    transition-property: background, color;
    transition-duration: 150ms;
}


/* TAB ICON & TAB LOADING ICON */

tab .tab-icon,
.tab-icon-image,
.tab-throbber {
    filter: grayscale(0);
}
#TabsToolbar .tabbrowser-tab[selected] .tab-icon,
#TabsToolbar .tabbrowser-tab[selected] .tab-icon-image,
#TabsToolbar .tabbrowser-tab[selected] .tab-throbber {
    filter: grayscale(0);
}


/* NEW TAB */

#main-window .tabbrowser-tabs .tabs-newtab-button {
    list-style-image: none;
    width: 35px;
    margin: 0px;
    padding: 0px;
    background: url(data:image/png;base64,) center no-repeat var(--main-new-tab-inactive-bg-color) !important;
    border: 0px solid var(--main-theme-border-color)!important;
    border-bottom: 0px solid transparent!important;
    border-radius: 0px !important;
    
}

#main-window .tabbrowser-tabs .tabs-newtab-button:hover {
    list-style-image: none;
    background: url(data:image/png;base64,) center no-repeat var(--main-new-tab-inactive-hover-bg-color) !important;
    border: 0px solid var(--main-theme-border-color)!important;
    border-bottom: 0px solid transparent!important;
}


/* DISABLE AUSTRALIS TAB */

#TabsToolbar .tab-background {
    background: none;
}

#TabsToolbar .tab-background-start::before,
#TabsToolbar .tab-background-end::before {
    background-image: none !important;
}

.tab-background-start[selected=true]:-moz-locale-dir(ltr)::after,
.tab-background-end[selected=true]:-moz-locale-dir(ltr)::after {
    background-image: none!important;
}

#TabsToolbar .tab-background-middle {
    background: transparent !important;
}

#TabsToolbar .tabbrowser-tab:after,
#TabsToolbar .tabbrowser-tab:before {
    display: none !important;
}


/* NAVIGATION BAR/ADDRESS BAR/SEARCH BAR SETTING */

#TabsToolbar:not(:-moz-lwtheme)::after {display: none;}
    
#navigator-toolbox {background: var(--main-window-bg-color) !important;}    
    
#navigator-toolbox:after {height: 0px !important;}


/* NAV-BAR */

#nav-bar {
    background: var(--main-nav-bg-color) !important;
    box-shadow: none !important;
    border-color: var(--main-theme-border-color) !important;
    border: 0px solid var(--main-theme-border-color) !important;
    margin-top: 0px !important;
    color: var(--main-url-text-color) !important;
    height: 44px !important;
}

#main-window[sizemode="maximized"] #nav-bar {
    border-radius: 0px !important;
}

#main-window[sizemode="normal"]:not([inFullscreen]) #nav-bar {
    border-radius: 0px !important;
}


/* BACK BUTTON */

#forward-button .toolbarbutton-icon {
    border-radius: 0 25px 25px 0!important;
}


/* URL BAR */

#urlbar {
    margin-left: 7px !important;
    background: var(--main-url-bg-color) !important;
    box-shadow: none !important;
    border-color: var(--main-url-bg-color) !important;
      border-right: 0px solid var(--main-theme-border-color)!important;
    border-radius: 0px !important;
    color: var(--main-url-text-color)!important;
    font-family: var(--main-font-family) !important;
    font-size: var(--main-theme-font-size) !important;
}
    
#PopupAutoCompleteRichResult {border: 1px solid #171717 !important;}
    
#PopupAutoCompleteRichResult .box-inherit.scrollbox-innerbox {
    background: #2b2b2b !important;
    border: 0 !important;
    color: var(--main-url-text-color)!important;
    font-family: var(--main-font-family) !important;
    font-size: var(--main-theme-font-size) !important;
}
    
    
/* IDENTITY */

#identity-box,
#identity-icon-labels {
    background: transparent !important;
}


/* .bad-content-blocked */

#notification-popup-box {
  -moz-appearance: none !important;
  background: transparent !important;
  background-image: none!important;
  box-shadow: none !important;
  border-image: none !important;
  border: 0px;
  list-style-image: none;
}


/* SEARCH BAR */

#searchbar .searchbar-textbox {
    margin-left: -7px !important;
    background: var(--main-url-bg-color) !important;
    box-shadow: none !important;
    border-color: var(--main-url-bg-color) !important;
    border-left: 0px solid var(--main-theme-border-color)!important;
    border-right: 0px solid var(--main-theme-border-color)!important;
    border-radius: 0px !important;
    color: var(--main-url-text-color)!important;
    font-family: var(--main-font-family) !important;
    font-size: var(--main-theme-font-size) !important;
}


/* BOOKMARKS  */

#PersonalToolbar { 
  background: var(--main-bookmark-bg-color) !important;
  border-right: 0 !important;
  border-left: 0 !important;
  border-top: 1px solid #171717 !important;
  border-bottom: 1px solid #171717 !important;
  height: 38px !important;
}

#PersonalToolbar .toolbarbutton-text {
  color: var(--main-bookmark-text-color) !important;
  font-family: var(--main-font-family) !important;
  font-size: var(--main-theme-font-size) !important;
}
    
.bookmark-item {padding: 0 2px 0 10px !important;}
    
toolbarseparator {display: none !important;}
    
#PersonalToolbar .bookmark-item .toolbarbutton-icon {  
    height: 0 !important;
    width: 0 !important;
    padding-left: 16px !important;
    padding-top: 16px !important;
    margin: 0 8px 0 0 !important;
    background: url('data:image/png;base64,') no-repeat !important;
    background-size: 16px 16px !important;
}
    
/*** BOOKMARK MENU POPUP ***/
    
#BMB_bookmarksPopup .panel-arrowcontainer .panel-arrowcontent {
    background: var(--main-nav-bg-color) !important;
    color: #FFFFFF !important;
    border-left: 1px solid #171717 !important;
    border-right: 1px solid #171717 !important;
    border-top: 0 !important;
    border-bottom: 1px solid #171717 !important;
    margin: 9px 0 0 0 !important;
}
    
.panel-arrow {display: none !important;}
    
#BMB_bookmarksPopup .menu-iconic-text, #BMB_bookmarksPopup .menu-text {
    font-family: var(--main-font-family) !important;
    font-size: 14px !important;
    color: #CCCCCC !important;
}

.menu-iconic.bookmark-item.subviewbutton .popup-internal-box .arrowscrollbox-scrollbox .box-inherit.scrollbox-innerbox {
    background: var(--main-nav-bg-color) !important;
    color: #FFFFFF !important;
    border: 0 !important; 
}

.menu-right {filter: invert(1) !important;}
    
.menu-iconic-accel {color: #888888 !important;}
    
.menu-iconic.bookmark-item.subviewbutton:hover,
.menuitem-iconic.bookmark-item.menuitem-with-favicon.subviewbutton:hover {
    background: #3b3b3b !important;
}

menupopup {-moz-appearance: none !important;}
    
menupopup,
panel {
    border: 1px solid #171717 !important;;
    background: #2b2b2b !important;;
}
    
.menu-iconic-text,
.menu-text {
    margin: 2px 0 !important;
    color: var(--main-bookmark-text-color) !important;
    font-size: 14px !important;
    font-family: var(--main-font-family) !important; 
    padding: 2px 0 !important;  
}
    
menuseparator {
    -moz-appearance: none !important;
    padding: 4px 0 !important;
    border-top: 0 !important;
    border-bottom: 1px solid #171717 !important;
}

#BMB_bookmarksPopup menupopup[placespopup=true] > hbox {
  /* emulating chrome://browser/content/places/menu.xml#places-popup-arrow but without the arrow */
  box-shadow: none !important;
  background: var(--panel-arrowcontent-background);
  border: 0 !important;
  border-radius: 0 !important;
  margin-top: -4px;
}
  
/*** RIGHT CLIC ***/
    
#context-navigation {
  background-color: var(--main-bookmark-bg-color) !important;
  padding-bottom: 4px;
}
   
#context-back .menu-iconic-icon,
#context-forward .menu-iconic-icon,
#context-reload .menu-iconic-icon,
#context-stop .menu-iconic-icon,
#context-bookmarkpage .menu-iconic-icon {
    filter: invert(1) !important;
}
    
/* TABS CLOSE BUTTON */

.tabs-closebutton-box,
.tab-close-button {
    filter: none;
}

.tabs-closebutton-box:hover,
.tab-close-button:hover {
    filter: none;
}


/* MENU BUTTON */

#PanelUI-menu-button {border-left: 0px solid var(--main-theme-border-color)!important;}

#PanelUI-button {
  background: transparent !important;
  border-image: none !important;
  border-left: 1px solid #171717 !important;
}


/* HIDES PLUGINS NOTIFICATION ICON THAT MAY CHANGE ADDRESS BAR'S HEIGHT */

#plugins-notification-icon {display: none !important;}


/* REMOVES VS SEPARATOR */

#browser-bottombox {border: none !important;}


/* BORDERLESS*/

#browser-border-end,
#browser-border-start {
    background: transparent !important;
    border-left: 1px solid var(--main-page-border-color)!important;
}

#browser-bottombox {
    background: transparent !important;
    border-bottom: 1px solid var(--main-page-border-color)!important;
}


/* BORDERLESS EFFECT FOR BORDERLESS VS */

#main-window {
    -moz-appearance: -moz-win-borderless-glass !important;
    -moz-appearance: -moz-win-glass !important;
}

/*** NEW ICONS ***/
    
#web-developer-button .toolbarbutton-icon {       
    height: 0 !important;
    width: 0 !important;
    padding-left: 16px !important;
    padding-top: 16px !important;
    margin: 0 0 0 0 !important;
    background: url('data:image/png;base64,') no-repeat !important;
    background-size: 16px 16px !important;
}
    
#web-developer-button .dropmarker-icon {filter: invert(1);}
    
#abp-toolbarbutton .toolbarbutton-icon {    
    height: 0 !important;
    width: 0 !important;
    padding-left: 16px !important;
    padding-top: 16px !important;
    margin: 0 8px 0 0 !important;
    background: url('data:image/png;base64,') no-repeat !important;
    background-size: 16px 16px !important;
}
    
#abp-toolbarbutton .dropmarker-icon {filter: invert(1);margin: 0 2px 0 0 !important;}
    
#stylish-toolbar-button .toolbarbutton-icon {    
    height: 0 !important;
    width: 0 !important;
    padding-left: 16px !important;
    padding-top: 16px !important;
    margin: 0 8px 0 0 !important;
    background: url('data:image/png;base64,') no-repeat !important;
    background-size: 16px 16px !important;
}
    
#tbtnInspect .toolbarbutton-icon {      
    height: 0 !important;
    width: 0 !important;
    padding-left: 16px !important;
    padding-top: 16px !important;
    margin: 0 8px 0 0 !important;
    background: url('data:image/png;base64,') no-repeat !important;
    background-size: 16px 16px !important;
}
