css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("studivz.net") {
   
    /* applies font to all generic HTML tags */
    
    body,
    br,
    button,
    li,
    font,
    h3,
    h4,
    h5,
    h6,
    html,
    iframe,
    input,
    label,
    option,
    p,
    select,
    span,
    table,
    td,
    textarea,
    tr  {
        font-family: 'Liberation Sans', Arial, Helvetica, sans-serif !important;
        font-size: 10px !important;
        color: #333333 !important;
    }
    h1, h2, class {
        font-size: 15px !important;
        font-family: Georgia, times, serif !important;
        font-weight: lighter !important;
    }
   thead {
        background-color: #999999 !important;
        color: #ffffff !important;
        border-top: 1px solid #1b1b1b !important;
        border-bottom: 1px solid #1b1b1b !important;
    }
     /* removes link underlining */
    a {
        text-decoration: none !important;
    }
    #mod-invitation-invitationbox { display: none !important; }
/*** red tabs ***/

ul#obj-tabBar {
    border-bottom: 1px solid #000000 !important;
}
#obj-tabBar li.selected {
    background-color: #178AC8 !important;
}
#obj-tabBar li a {
    color:#000000 !important;
}
#obj-tabBar li.selected a {
    color: #fff !important;
}
#obj-tabBar li a:focus {
color: #FFFFFF !important;
    background-color:#178AC8 !important;
}
#obj-tabBar li a:hover {
color: #FFFFFF !important;
    background-color:#178AC8 !important;
}
#obj-tabBar li.selected a:focus {
color: #FFFFFF !important;
    background-color: #178AC8 !important;
}
#obj-tabBar li.selected a:hover {
color: #FFFFFF !important;
    background-color: #178AC8 !important;
}
div.obj-subbar {
color: #FFFFFF !important;
    background-color: #BACDEF !important;
    border-bottom: 1px solid #1E2B57 !important;
    border-top: none !important;
}
}
