css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp(".*rst.com.pl/piszemy.*|.*rst.software/pl/blog/.*") {
  html,
  body,
  footer {
    background-color: #111;
  }
  
  .info_widget p {
    color: #c0c0c0;
  }
  
  .author_widget h3 a {
    color: #c6c2c2;
  }
  
  .postcontents > p:nth-of-type(1) {
    color: #e7e7e7;
  }
  
  p,
  .postcontents ol,
  .postcontents ul {
    color: #d2d2d2;
  }

  .postcontents p{
    text-align: justify;
  }
}
