css:
@-moz-document url-prefix("https://disqus.com/home/channel/thefinalfrontier/") {
li.nav__item.active {
    background-color: inherit;
    border-style: none;
}

div.card-wrap.card--channel {
    background-image: url("http://i.imgur.com/9OOtv0k.png");
    height: 400px;
    width: 760px;
    background-color: rgba(0, 0, 0, 0.2);
    border-radius: 0px 90px 90px 0px;
    -moz-border-radius: 0px 90px 90px 0px;
    -webkit-border-radius: 35px 35px 35px 35px;
    border-style: none;
}

div:hover.card-wrap.card--channel {
    background-image: url("http://i.imgur.com/cCE5cqk.png");
    height: 400px;
    width: 760px;
    background-color: rgba(0, 0, 0, 0.5);
    border-radius: 0px 90px 90px 0px;
    -moz-border-radius: 0px 90px 90px 0px;
    -webkit-border-radius: 35px 35px 35px 35px;
    border-style: none;
}

div.topic-group__wrapper {
    margin-bottom: 25px;
}

a:hover.link-gray-darker.truncate {
    color: #ffFFFF;
    text-transform: uppercase;
    font-size: 16px;
}

a:hover.button--tag {
    border-radius: 0px 90px 90px 0px;
    -moz-border-radius: 0px 90px 90px 0px;
    -webkit-border-radius: 0px 90px 90px 0px;
    width: 150px;
    text-align: right;
    margin-top: 0px;
    background-color: rgba(0, 0, 0, 0.6);
    border-style: none;
    color: #ffbf00;
    padding-right: 15px;
}

div.tag__item {
    height: 10px;
}

div.card__content.-default.has-image {
    padding-left: 95px;
    padding-right: 0px;
    padding-top: 0px;
    margin-top: -8px;
    height: 175px;
}

a.button--recommend {
    width: 150px;
    height: 40px;
    margin-top: -7px;
    border-style: none;
    border-radius: 0px 90px 90px 0px;
    -moz-border-radius: 0px 90px 90px 0px;
    -webkit-border-radius: 0px 90px 90px 0px;
}

div.card__reason.link-inner-gray-dark.align.align--middle {
    width: auto;
    height: 75px;
    margin-left: 80px;
    margin-right: 180px;
    padding-top: 30px;
}

a:hover.truncate.link-gray-dark.post-comments__lnk p {
    color: #FFFFFF;
    line-height: 12px;
    font-size: 10px;
}

div.post-comments__reason {
    padding-bottom: -10px;
}

a.truncate.link-gray-dark.post-comments__lnk p {
    color: #858585;
    line-height: 12px;
    font-size: 10px;
}

a.button--recommend.button-small {
    margin-left: -5px;
    padding-top: 15px;
}

a.button.button-link.dropdown-toggle {
    margin-left: 10px;
    width: 60px;
    text-align: right;
    border-style: none;
    color: #FFFFFF;
    padding-right: 15px;
    height: 30px;
    border-radius: 0px 90px 90px 0px;
    -moz-border-radius: 0px 90px 90px 0px;
    -webkit-border-radius: 90px 90px 90px 90px;
    background-color: rgba(255, 255, 255, 0.2);
    opacity: 0.5;
}

div.truncate {
    height: 90px;
    margin-top: 25px;
    overflow: hidden;
    text-overflow: ellipsis;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: N;
    max-height: XN;
    line-height: 15px;
    color: #FFFFFF;
}

div.card__footer.text-small {
    margin-top: 10px;
    margin-left: -10px;
}

li.nav__item {
    background-color: inherit;
    border-style: none;
}

span.nav-lnk__icon.icon.icon-popular {
    display: none;
}

span.nav-lnk__icon.icon.icon-discussions {
    display: none;
}

ul.nav--aside {
    background-color: inherit;
    border-style: none;
}

a:hover.nav-lnk.-color-muted {
    background-image: url("http://i.imgur.com/Lb0XaoI.png");
    border-style: none;
    color: #ffbf00;
    font-family: Consolas;
    text-transform: uppercase;
}

div.card-wrap.-padded {
    background-color: inherit;
    border-style: none;
}

a:hover.button.button-padding-taller.button-wide {
    border-radius: 90px 90px 90px 90px;
    -moz-border-radius: 90px 90px 90px 90px;
    -webkit-border-radius: 90px 90px 90px 90px;
    border-style: none;
    background-color: #96add4;
    color: #FFFFFF;
}

a.button.button-padding-taller.button-wide {
