    -moz-border-radius: 90px 90px 90px 90px;
    -webkit-border-radius: 90px 90px 90px 90px;
    border-style: none;
    background-color: #51678b;
    color: #FFFFFF;
}

button.button.button-outline.text-medium {
    border-radius: 0px 90px 90px 0px;
    -moz-border-radius: 0px 90px 90px 0px;
    -webkit-border-radius: 0px 90px 90px 0px;
    border-style: none;
    background-color: inherit;
    color: #FFFFFF;
    width: 200px;
    text-align: right;
    margin-right: -10px;
}

nav.layout__nav {
    background-color: inherit;
}

div.aside__wrap {
    background-color: inherit;
    border-style: none;
}

div.aside__subheadings.text-subheading h2 {
    border-radius: 0px 90px 90px 0;
