    -webkit-border-radius: 0px 90px 90px 0;
    background-color: inherit;
    padding-right: 10px;
    margin-bottom: 10px;
    color: #ffffff;
    margin-left: -10px;
    text-align: right;
    width: 200px;
    height: 15px;
}

div.aside__subheadings.hidden-lg {
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: 30% 20%;
}

div.hidden-lg {
