@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mail.google.com") {



/* LOADING STYLE FOR 1024*768 */

#loading .lpb,
.JfQCGf {
background: transparent;
border: 0px !important;
height: 5px !important;
width: 200px !important;
position:absolute;
left:500px;
top:300px;
}



/* HIDE TEXT */

.msg{display: none !important;}
.msgb{display: none !important;}

	

/* LOADING - PROGRESS BAR */

#loading #lpt,
.llGW5d {
background: transparent url(data:image/gif;base64,R0lGODlhyAAFAJEAAAAAAP//zP4BAgAAACH5BAQUAP8ALAAAAADIAAUAAAIehI+py+0Po5y02ouz3rz7D4biSJbmiabqyrbuCzsFADs%3D) repeat !important;
width:200px;
height:5px;
}


		
/* LOADING - PROGRESS BAR - FORCE TO END */

