css:
@-moz-document domain(twitch.tv)
{
  div.fp-container > div.fp-container__pulse {
      width: 0rem;
      padding: 0rem;
      padding-right: 0rem;
      overflow: hidden;
  }

  div.fp-container > div.fp-container__side.js-fp-side {
      left: 0;
	  /*  Free space around the main content */
      max-width: 100rem;
      padding-left: 0;
      padding-right: 0;
      margin-right: auto;
      margin-left: auto;
  }

  div.fp-container > div.fp-container__side.js-fp-side > div.ember-view {
      width: 100%;
  }
}
