    -moz-transition: all 0.5s;
    -webkit-box-shadow: 6px 7px 13px -3px rgba(0,0,0,0.75);
    -webkit-transition: all 0.5s;
    border-color: #2b2b2b;
    border-radius: 0px;
    border-style: none;
    transition: all 0.5s;
}

img:hover {
    -moz-box-shadow: 6px 7px 13px -3px rgba(0,0,0,0.75);
    -moz-transform: scale(1.05);
    -webkit-box-shadow: 6px 7px 13px -3px rgba(0,0,0,0.75);
    -webkit-transform: scale(1.05);
    border-color: #2b2b2b;
    border-radius: 0px;
    border-style: none;
    transform: scale(1.05);
}

