css:
@-moz-document domain(jisho.org) {
	body {
    background-color: #af0000;
}

body a {
	color: #ff7272;
}

p {
    color: #fff;
}

body a:hover {
	color: #fff;
}

li {
    color: #fff;
}

header.row, header.page {
    background-color: #af0000;
}

h1.logo a {
    background-image: url(http://i.imgur.com/O3iTdQ7.png)
}

form.search .inner {
    background: #af0000;
}

form.search .main {
    background-color: #ff7272;
}

input[type=text] {
	color: #fff;    
}

form.search .search_type {
    color: #ff7272 !important;
}

input::placeholder {
    color: #ff7272;
}

form.search .input_methods {
    color: #fff
}

h4 {
    color: #fff !important;
}

form.search .search_type {
    border-right-color: #ff7272;
}

form.search .submit

form.search .search-form_clear-button {
    background: transparent !important;
    color: #fff !important;
}

form.search .submit {
    display: none;
}

form.search .input_method_button:hover {
    background-color: transparent;
}

#main_results h4 .result_count {
    color: #ff7272;
}

.kanji_light .sense {
	color: #fff;    
}

.kanji .character, .kanji_light .character, .kanji_light .readings, .kanji_light .info, .concept_light-meanings .meaning-definition-section_divider, .concept_light-meanings .meaning-meaning, .concept_light-meanings .sentences {
    color: #fff;
}

span.furigana {
    color: #ff7272;
}

.clear-icon {
    fill: #ff7272;
}

form.search .search-form_clear-button {
    background: transparent;
}

.concept_light-meanings .meaning-tags, .concept_light-meanings .meaning-abstract, .concept_light-meanings .supplemental_info, #secondary aside .minor-text, .sentence .inline_copyright a {
    color: #ff7272;
}

.concept_light-meanings .meaning-definition.meaning-representation_notes, .search-secondary_column .concept_light-readings {
    color: #fff;
}

.button-group.radius>*>a, .button-group.radius>*>.button {
    background-color: #fff;
    color: #af0000;
    transition: background-color 0.3s ease;
}

.button-group.radius>* {

	margin: 0px;
}

.button-group.radius>*>a:hover, .button-group.radius>*>.button:hover {
    background-color: #af0000;
    color: #fff;
    transition: background-color 0.3s ease;
}

html>body>div>div.large-12.columns>form#search.search>div.overlay>div#search_main.main.in>div#search_sub.sub>div#advanced_area.hidden.area.advanced>div.filters>p>a {
    color: #fff;
    text-decoration: none;
}

h3 {
    color: #fff;
}

.fact {
    background-color: #ff7272;
    outline: none;
    border: 3px solid #fff;
    color: #fff;
}

h6 {
    color: #fff;
}

h6>a {
    color: #fff !important;
    text-decoration: none;
}

div#no-matches {
    color: #fff;
}

#number_conversion.fact, .show_more, .show_less.link {
    color: #fff;
}

.result {
    background-color: #af0000 !important;
}

.speech_area_active #speech_button, .radical_area_active #radical_button, .handwriting_area_active #handwriting_button {
    border-bottom: 3px solid #ff3838;
}

.radical_table {
    background: repeating-linear-gradient(180deg, #fff, #fff 32px, #ffeded 32px, #ffeded 64px);
}

.radical_table .number {
    background-color: #ff7272;
    color: #fff;
}

.vertical-bottom-inner {
    display: inherit !important;
}

form.search .results .list, form.search .results {
    margin-bottom: 0;
}

.radical_table .radical.selected {
    background-color: transparent;
    border: none;
    color: #af0000;
}

#radical_area .results .g1, #radical_area .results .g2, #radical_area .results .g3, #radical_area .results .g4, #radical_area .results .g5, #radical_area .results .g6, #radical_area .results .g7, #radical_area .results .g8, form.search .results .result_label {
    color: #fff;
}

button.tiny.english, button.tiny.japanese {
    background-color: #af0000;
    color: #fff;
}

button.tiny.english:hover, button.tiny.japanese:hover {
    background-color: #fff;
    color: #af0000;
}

button.tiny.english {
    margin-left: 45px;
}

.explanation>.text, #zen_bar a {
    color: #fff;
}

.arrow, .kanji_strokes .actions h3 {
    color: #fff;
}

#zen_bar {
    margin: 0;
}

body {
    color: #fff;
}

.kanji-details__main-readings-list a {
    border: none;
}

h1, h2, h3, h4, h5, h6 {
    color: #fff;
}

.stroke_order_diagram--bounding_box, .stroke_order_diagram--guide_line{
   	stroke: #ff7272;
}

.stroke_order_diagram--existing_path {
    stroke: #fff;
}

.stroke_order_diagram--current_path {
    stroke: #590000;
}

.kanji_strokes .kanji_play_button {
    background: none;
}

.kanji_strokes .kanji_play_button, .kanji_strokes .replay_button, .kanji_strokes .toggle_kanji_actions  {
    color: #ff7272;
}

.kanji_strokes .actions {
    background-color: #ff7272;
    border-radius: 12%;
}

.noUi-background {
    background-color: #af0000;
}

.noUi-origin {
    background: #fff;
}

.noUi-active {
    background-color: #fff;
}

.noUi-base {
    border: 1px solid #fff;
}

button.secondary, .button.secondary, button.secondary:hover, button.secondary:focus, .button.secondary:hover, .button.secondary:focus {
    background: none;
    border: none;
    color: #fff;
}

.noUi-handle {
    background: #fff;
    border: #fff
}

noUi-handle.noUi-handle-lower.noUi-active {
    border: none;
}

.kanji_strokes .kanji_play_button {
    top: 30px;
    left: 20px;
    height: 50%;
    width: 50%;
}

.f-dropdown {
    background-color: #fff;
    border: 1px solid #ff7272;
}

.f-dropdown:after {
    border-color: transparent transparent #ff7272 transparent;
}

.f-dropdown li a {
    color: #ff7272;
    text-decoration: none;
}

.f-dropdown li:hover, .f-dropdown li:focus {
    background: #ff7272;
    text-decoration: none;
}

  
.f-dropdown li a:hover, .f-dropdown li a:focus{
	color: #fff !important;   
}

.button-group.radius>*>button, .button-group.radius>* .button {
    border-left: none;
}

.concept_light-status .concept_light-tag.concept_light-common {
    background: #ff7272;
}

.concept_light-status .concept_light-tag {
    background-color: #ff7272;
}

body a {
    text-decoration: none;
}

.concept_light-status .concept_light-tag, .concept_light-status .concept_light-tag a {
    color: #af0000;
}

.reveal-modal, dialog {
    background-color: #af0000
}

form>p>input {
	color: #af0000 !important;
    border-color: #ff7272;
}

form>p>input.button {
    background-color: #fff;
}

form>p>input.button:hover {
    background-color: #ff7272;
    transition: background-color 0.3s ease;
}

input[type="text"], input[type="password"], input[type="date"], input[type="datetime"], input[type="datetime-local"], input[type="month"], input[type="week"], input[type="email"], input[type="number"], input[type="search"], input[type="tel"], input[type="time"], input[type="url"], input[type="color"], textarea, input[type="text"]:focus, input[type="password"]:focus, input[type="date"]:focus, input[type="datetime"]:focus, input[type="datetime-local"]:focus, input[type="month"]:focus, input[type="week"]:focus, input[type="email"]:focus, input[type="number"]:focus, input[type="search"]:focus, input[type="tel"]:focus, input[type="time"]:focus, input[type="url"]:focus, input[type="color"]:focus, textarea:focus {
    border-color: #ff7272;
}

.reveal-modal .close-reveal-modal, dialog .close-reveal-modal {
    color: #fff;
}

.reveal-modal, dialog {
    box-shadow: none;
    border: none;
}

.discussion_thread .comment_body {
    border: 1px solid #ff7272;
    background: #ff7272;
}

.discussions_list .discussion_thread .comment_wrapper .discussion_title h2 a {
    color: #fff;
}

.discussion_thread .comment_body .discussion_status, .discussion_thread .comment_body .comment_status {
    background: #fff;
    color: #ff7272;
    border-bottom: none;
}

.discussion_thread .comment_body .discussion_about {
    color: #fff;
    border-bottom: none;
    background: #ff7272;
}

.discussion_thread .comment_body a {
    color: #fff;
}

.discussion_thread .comment_body .discussion_status a, .discussion_thread .comment_body .comment_status a {
    color: #ff7272;
}

footer {
    background: none;
}

table caption {
    color: #fff;
}

table tr.even td, table tr.alt td, table tr:nth-of-type(even) td {
    background-color: #ff7272;
    color: #fff !important;
}

.speech_area_active #speech_button, .radical_area_active #radical_button, .handwriting_area_active #handwriting_button {
    border-bottom-color: #ff7272;
}

form.search .instructions .arrow {
    color: #fff;
}

.tabs dd.active a, .tabs .tab-title.active a {
    color: #af0000;
    background-color: #ff7272;
}

.tabs dd>a, .tabs .tab-title>a {
    color: #af0000;
    background-color: #fff;
}

.tabs dd>a:hover, .tabs .tab-title>a:hover {
    background-color: #fff;
    color: #ff7272;
}

table {
    border: none;
}

table tr th, table tr td {
    color: #ff7272;
}

.japanese_word__furigana_wrapper, .japanese_word__furigana-invisible {
    color: #ff7272;
}

#zen_bar li[data-pos=Noun] a, #zen_bar li[data-pos=Suffix] a, #zen_bar li[data-pos=Prefix] a, #zen_bar li[data-pos=Symbol] a, #zen_bar li[data-pos=Interjection] a, #zen_bar li[data-pos=Propernoun] a, #zen_bar li[data-pos=Verb] a,  #zen_bar li[data-pos=Particle] a, #zen_bar li[data-pos=Adverb] a {
    color: #fff;
    border-bottom: none;
}
}
