css:
@-moz-document url-prefix("https://forum.kaspersky.com")
  {
li.ipsDataItem.ipsDataItem_responsivePhoto.pinnedT {
    border-left: 5px solid #006d5c;
    border-bottom: 1px solid #ebebeb;
}

ol.cTopicList li.pinnedT {
    background: initial !important;
}

ol.cTopicList li.ipsDataItem.ipsDataItem_responsivePhoto:nth-child(even) {
    background-color: rgba(0,0,0,.05) !important;
}

ol.cTopicList li.ipsDataItem.ipsDataItem_responsivePhoto.ipsModerated {
	background-color: #f7edf1 !important;
}


li.ipsDataItem.ipsDataItem_responsivePhoto.ipsModerated.pinnedT {
	background: #f7edf1 !important;
}

.pinnedT:last-of-type  {
    border-bottom: 10px solid #006d5c;
}

убирает логотип + все баджи с форума
li.ipsDataItem.ipsDataItem_responsivePhoto.pinnedT > div.ipsDataItem_main > h4 > span {
	display: none;
}

ul.ipsTags.ipsTags_inline.ipsList_inline {
	display: none;
} 

ul.ipsTags.ipsList_inline {
	display: none;
}

.ipsContained_container .ipsTag_prefix {
	display: none;
}

#elLogo {
	display: none;
}

.ipsLayout_container {
    border-bottom: 1px solid white;
    margin-bottom: 15px;
}

.ipsButtonBar .ipsPagination .ipsPagination_pageJump a {
	color: #fff;
}

.ipsButtonBar .ipsPagination .ipsPagination_page.ipsPagination_active a {
    background: #598f5b;
    border-color: #3e4148;
    color: #fff;
}

.ipsType_sectionTitle {
	color: #fff;
}

.ipsApp .ipsToolList .ipsButton_link, .ipsApp .ipsButton_link.ipsButton_link_secondary {
	border: 1px solid rgba(0,0,0,0.1);
}

.ipsPagination:not( .ipsPagination_mini ) .ipsPagination_first a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_last a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_next a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_prev a {
    background: #d8d8d8;
}

.ipsPagination:not( .ipsPagination_mini ) .ipsPagination_page a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_next a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_prev a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_first a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_last a {
 	background: #d8d8d8;
}

.ipsPagination .ipsPagination_page.ipsPagination_active a {
	background: #598f5b;
}

ul.ipsComment_controls.ipsClearfix.ipsItemControls_left {
    text-align: right;
}

.ipsWidget.ipsWidget_vertical .ipsWidget_title, .ipsWidget.ipsWidget_horizontal .ipsWidget_title {
	color: #fff;
}

div.ipsPos_left.ipsResponsive_hidePhone.ipsResponsive_block {
	display: none;
}

.ipsComposeArea_withPhoto .ipsComposeArea_editor:before {
	content: '';
    position: absolute;
    top: 0px;
    display: block;
    width: 0;
    height: 0;
    border-width: 0px;
    border-style: solid;
}

html[dir="ltr"] .ipsComposeArea_withPhoto .ipsComposeArea_editor {
    margin-left: 0px;
}

.ipsComposeArea_withPhoto .ipsComposeArea_editor {
	padding: 0;
}

.ipsPad, .ipsApp ul.ipsPad, .ipsApp ol.ipsPad {
	padding: 5px;
}

.ipsComposeArea [data-ipsEditor] + [data-ipsEditor-toolList] {
    margin-top: 0px;
    margin-bottom: 0px;
    padding: 10px 7px;
}

a.ipsType_sectionTitle, .ipsType_sectionTitle a {
	color: #fff;
}

.ipsList_reset .ipsPad {
	margin-top: 5px;
}

div.cWidgetContainer > ul.ipsList_reset > li.ipsWidget.ipsWidget_horizontal.ipsBox > h3.ipsType_reset.ipsWidget_title {
    background: #FBC02D;
}

div.cWidgetContainer > ul.ipsList_reset > li.ipsWidget.ipsWidget_horizontal.ipsBox > h3.ipsType_reset.ipsWidget_title {
	color: #000;
    font-weight: bold;
    letter-spacing: 3px;
}

h3.ipsType_reset.ipsWidget_title:before {
    content: "\f129";
    font-family: FontAwesome;
    font-style: normal;
    font-weight: normal;
    text-decoration: inherit;
    color: #000;
    font-size: 18px;
    padding-right: 0.3em;
    position: relative;
    top: 1px;
}

div.ipsPhotoPanel.ipsPhotoPanel_small.ipsPhotoPanel_notPhone.ipsClearfix > a.ipsUserPhoto.ipsUserPhoto_small > img {
	display: none;
}

html[dir="ltr"] .ipsPhotoPanel.ipsPhotoPanel_small > div {
	margin-left: 0px;
}

.ipsPageHeader {
	    margin: 30px 0;
}

.ipsPageHead_barText, .ipsPageHead_barText_small {
	background: rgba(0,0,0,0);
}

.cUserHovercard .ipsPageHead_special {
    background-image: url('https://www.toptal.com/designers/subtlepatterns/patterns/footer_lodyas.png');
    background-repeat: repeat;
}

#elProfileHeader {
	background-image: url('https://www.toptal.com/designers/subtlepatterns/patterns/footer_lodyas.png');
    background-repeat: repeat;
}

header > div.ipsLayout_container {
	margin: 0;
    padding: 0;
    max-width: 100%;
}

#elMobileNav {
    background: #00594a;
}

#elLogo > img {
    padding-bottom: 5px;
}

#elRegisterButton {
    background: #016d5d;
}

ul.ipsTags.ipsTags_inline.ipsList_inline {
	opacity: 0.8
}

.ipsTag_prefix {
	opacity: 0.8
}


