@media only screen and (max-width: 1200px) {    
    #elLogo > img {
                	padding-top: 10px;
    				padding-bottom: 10px;
                	}
}


@media only screen and (max-width: 979px) {
  #elLogo > img {
                	padding-top: 0;
    				padding-bottom: 10px;
                	}
	#elUserNav {
                    left: 30%;
                    top: -40px;
                	}
}
