.headerInfo.headerInfo-article .headerInfo-links {background-color: rgba(255,255,255,0.15);}

.landing header h1 .sub {color: white;}

.landing .landing-v0 h2 .kicker {color: white;}

.leftnav-open #feedlyFrame {width: calc(100% - 252px);}

.leftnav-open #feedlyFrame #feedlyPageHolderFX #feedlyPageFX.container.centered .fx > div header.header.row {margin: 0;}

.leftnav-open #feedlyFrame #feedlyPageHolderFX #feedlyPageFX.container.centered .fx > div header.header.row .sub {color: white;}

.leftnav-open #headerBarFX.fixed-bar {box-shadow: 0 0 0 0 rgba(0,0,0,0); left: 50% !important; right: auto; transform: translateX(-50%);}

.leftnav-open #headerBarFX.fixed-bar > div {display:inline-block; float: left; height: 100%; width: 100%;}

.leftnav-open #headerBarFX.fixed-bar > div > header.header.row {background-color: rgba(0,0,0,0); box-shadow: 0 0 0 0 rgba(0,0,0,0); float: left; height: 100%; margin: 0; padding: 0;}

.leftnav-open #headerBarFX.fixed-bar > div > header.header.row > * {display: inline-block; float: left; height: 100%; padding: 0; width: auto;}

.leftnav-open #headerBarFX.fixed-bar > div > header.header.row > h1 {height: auto; margin-right: 20px; position: relative; top: 50%; transform: translateY(-50%);}

.leftnav-open #barsFX.fx #headerBarFX.fixed-bar > div > header.header.row > .col-xs-6.col-md-6.right-col > button.secondary[data-uri='cortex'] {padding: 7px;}

.leftnav-open #barsFX.fx #headerBarFX.fixed-bar > div > header.header.row > .col-xs-6.col-md-6.right-col > button.tertiary.button-icon-only {position: relative;}

.leftnav-open .fx #headerBarFX.fixed-bar > div > header.header.row > .col-xs-6.col-md-6.right-col .button-dropdown {border: 0 solid rgba(0,0,0,0); display: inline-block; float: left;}

.leftnav-open .fx #headerBarFX.fixed-bar > div > header.header.row > .col-xs-6.col-md-6.right-col .button-dropdown .tertiary.button-icon-only {display: inline-block; float: left; height: 30px; margin: 0; width: 30px;}

.leftnav-open .fx #headerBarFX.fixed-bar > div > header.header.row > .col-xs-6.col-md-6.right-col .icon-toolbar-refresh-secondary.button-icon-only.primary {display: inline-block; float: left; height: 30px; margin: 0; width: 30px;}

.leftnav-open .fx #headerBarFX.fixed-bar > div > header.header.row > .col-xs-6.col-md-6.right-col .mark-as-read-button-group.button.secondary {display: inline-block; float: left; height: 30px;}

.list-entries {float: left; width: 100%;}

.list-entries > h4 {color: white !important; float: left; font-size: 1rem; margin: 5px 0 5px 5px; width: 100%;}

.opml-file-picker {outline-offset: -10px;}

.pageActionBar {height: 24px;}

.pageActionBar #pageActionLayouts[style^='display:none'] {display: none !important; opacity: 0; visibility: visible;}

.pageActionBar #pageActionLayouts ~ [style^='display:none'] {display: none !important; opacity: 0; visibility: visible;}

.presentation-100 > div:nth-child(2) > div.stream {border: none !important; clear: left; display: inline-block; float: left; background-color: rgba(0,0,0,0.15) !important; color: #999 !important; margin: 10px 0; padding: 0; position: relative; width: 100%; z-index: initial;}

.presentation-100 > div:nth-child(2) > div.stream > h4:first-child {margin: 8px 0;}

.presentation-100 > div:nth-child(2) > div > h4 {font-size: 1rem; margin: 5px 0; padding-left: 5px;}

.presentation-100 > div > header.header.row > .col-xs-6.col-md-6.p-t-2.right-col.extra {height: 40px; padding: 0 !important;}

.presentation-100 > div > header.header.row > .col-xs-6.col-md-6.p-t-2.right-col.extra .actions-container {background-color: rgba(255,255,255,0.25); height: 100%; position: relative;}

.presentation-100 > div > header.header.row > .col-xs-6.col-md-6.p-t-2.right-col.extra .actions-container > * {display: inline-block; float: left; height: 30px; position: relative; top: 50%; transform: translateY(-50%);}

.presentation-100 > div > header.header.row > .col-xs-6.col-md-6.p-t-2.right-col.extra .actions-container > span > .button-dropdown {top: 50%; transform: translateY(-50%);}

#searchBarFX.fixed-bar {background-color: rgba(0,0,0,0.5); height: 36px; padding: 3px 0 0 0;}

#searchBarFX.fixed-bar .container.centered {float: left; height: 30px; width: 100%;}

#searchBarFX button.pro {top: 3px;}

#searchBarFX .feedly-logo {margin-top: 2px;}

#searchBarFX .search-bar-right-col {height: 100%;}

.selectorNewMarker {margin-top: 0;}

.unread {color: #aaa !important;}

