css:
@-moz-document domain("games.espn.com") {
#content *, .comparePlayersMessage, div.gamesmain *, .fpop_window_modal *, .fpop_window * {
    font-family: "BentonSans", Roboto, Segoe UI, Arial, sans-serif !important;
}

body.nfl-scoreboard .bg-elements, 
body.nfl-secondary-bg .bg-elements, 
.bg-elements, 
body.trans-fantasy div.bg-elements {
    background: #e5e5e5 !important;
}

body.trans-fantasy .nav-main-breadcrumbs-row {
    height: auto !important;
    vertical-align: top;
    margin-bottom: 20px;
}

body.trans-fantasy div.nav-main-breadcrumbs {
    font-size: 11px;
    font-weight: 500;
    padding: 0;
    line-height: 11px;
    color: #7b7b7b;
}

div[style*="width:946px"], div[style*="width:956px"],
div[style*="width: 946px"], div[style*="width: 956px"] {
    width: 100% !important;
}

strong, b {
    font-weight: 500;
}

.games-toolset + br,
table:not(#chatTable) + br {
    line-height: 24px;
}

.maincontainertbl {
    width: 1200px;
}

.gamesmain {
    border: 0px solid #e3f5e5;
    border-top: 0;
}

.games-nav {
    border-left: 1px solid #e3f5e5;
    border-right: 1px solid #e3f5e5;
    width: 100%;
    background: #2aa43b;
    margin: 0;
}

.comparePlayersLinkDiv,
.playerSearchDiv {
    font-size: 11px;
    font-weight: 500;
    margin: -8px 0 0 14px;
    line-height: 24px;
    height: auto;
    display: inline-block;
}

.games-topcol {
    width: 100%;
    padding: 0px 0px !important;
    margin: 0 ;
    box-sizing: border-box;
    position: relative;
}

.games-topcol.games-topcol-expand {
    height: auto  !important;
    margin-top: 0;
}

.games-bottomcol {
    margin: 24px 0 0 !important;
}

.playerTableContainerDiv br[style*="line-height: 12px"],
.games-bottomcol form br {  
    display: none;
}

.games-innercol {
    padding: 0;
    background: transparent !important;
}

.games-innercol h1 {
    color: #4d4d4d;
    text-transform: none;
    font-weight: 500;
}

.games-innercol .games-pageheader {
 	margin: 0 0 24px 0;   
}

.games-pageheader h1 {
    margin-top: 24px;
}

.games-pageheader div[style*="float: right"] {
    margin-top: 24px;
}

.games-pageheader + div[style*="float:right"] {
    margin-top: 16px;
}

.games-innercol2 {
    box-sizing: border-box;
    background: none !important;
}

.games-fullcol {
    margin: 0 !important;
}

.games-fullcol.games-fullcol-extramargin {
    margin: 0 !important;
    position: relative;
}

.games-leftcol {
    float: left;
    width: 70% !important;
    margin: 24px 0 0 !important;
    position: relative;
}

.games-leftcol > div > div[style*="float: right"] {
    position: absolute;
    top: 0;
    right: 0;
    text-align: center;
    font-size: 12px;
    padding: 18px 22px;
}

.games-leftcol > div > div[style*="float: right"] b {
    display: block;
    font-weight: 500;
    padding-bottom: 4px;
}

.games-rightcol {
    float: right;
    width: 28% !important;
    margin: 24px 0 0;
    display: flex;
    flex-direction: column;
}

.games-rightcol-spacer {
 	display: none;   
}

.games-univ-mod1 {
    background: #fff;
    color: #4d4d4d;
    height: 100%;
    width: auto;
    margin-left: 10px;
    position: relative;
}

.games-univ-mod1 a {
    display: inline-block;
}

.games-univ-mod1 p {
    position: absolute;
    left: 0;
    bottom: 0;
    width: 100%;
    padding: 0 !important;
    text-align: center;
}

.games-univ-mod1 p > strong {
	display: inline-block;
    margin: 0 0 4px;
    background: #2f6cb8;
    color: #fff;
    padding: 0px 12px;
    line-height: 20px;
    vertical-align: top;
    font-size: 10px;
    width: auto;
    box-sizing: border-box;
}

.games-univ-mod1 p br {
    display: none;
}

.games-univ-mod1 p a {
    padding: 2px 0px; 
    display: inline-block;
}

.games-univ-mod1 p a:hover {
 	opacity: 0.8;   
}

.games-univ-mod1 p a[href*="teamsettings"] {
    font-size: 0;
    float: left;
    position: absolute;
    right: -34px;
    bottom: 12px;
}

.games-univ-mod1 p a[href*="teamsettings"]:after {
    content: url(https://i.imgur.com/aUMF7W7.png);
}

.games-univ-mod1 p img {
    display: none;
}

.context-clubhouse img[width="151"] {
    border: 1px solid #e1e1e1 !important;
    padding: 0;
    margin: 12px;
}

.games-univ-mod1 > img,
.games-univ-mod1 > a > img {
    padding: 12px;
    margin-left: 0px;
}

/* Removes Streak For The Cash */

.games-univ-mod5.univ-right-mod5 {
    display: none !important;
}

.games-univ-mod2 {
    margin: 0;
    width: auto;
    float: none;
}

.games-univ-mod3 .team-name {
    font-weight: 500;
    overflow: visible;
  /*  letter-spacing: -0.25px; */
    margin-bottom: 6px;
}

.games-univ-mod3 {
    background: #fff;
    height: auto;
    width: auto;
    padding: 40px 20px 0px;
    line-height: 22px;
    font-size: 12px;
}

.games-univ-mod3 .team-name em {
    color: #7b7b7b;
}

.games-topcol a, .games-topcol a:link,
.games-topcol a:visited, 
.games-topcol a:hover, 
.games-topcol a:visited:hover {
    font-size: 10px;
}

.games-univ-mod3 a[href*="leagueoffice"] {
    display: none;
}

.games-univ-mod3 ul li.univBtn1 {
    background: #41ca41;
    color: #fff;
    height: auto;
    width: auto;
    line-height: 18px;
    display: none;
}

.games-univ-mod3 ul li.univBtn1 a {
    padding: 0px 5px;
    color: #fff;
}

.games-univ-mod4 {
	background: #fff;
    padding: 14px 16px;
    text-align: center;
    height: auto;
    border-radius: 0;
    top: 0;
    width: 15%;
    box-sizing: border-box;
}

.games-univ-mod4 h4 {
    font-size: 24px;
    font-weight: 500;
    position: absolute;
    left: 40%;
    top: 44px;
    background: #4d4d4d;
    color: #fff;
    padding: 4px 18px;
    border-radius: 0px;
}

.games-univ-mod4 h4 strong {
    font-size: 12px;
    font-weight: 500;
    display: block;
    background: #fff;
    padding: 4px 0;
    line-height: 22px;
    margin-bottom: 10px;
    border-bottom: 2px solid #2aa43b;
    color: #4d4d4d;
    text-align: left;
    display: none;
}

.games-univ-mod4 h4 em {
    font-weight: 500;
    font-size: 11px;
    color: #4d4d4d;
    padding: 4px 0;
    margin: 0;
    letter-spacing: 0.1px;
    position: absolute;
    bottom: -20px;
    width: 100%;
    left: 0;
    text-align: center;
    background: #e5e5e5;
}

.games-univ-mod4 a {
    background: #41ca41;
}

.games-univ-mod4 a.games-schedule,
.games-univ-mod4 a.games-schedule:hover {
    background: #fff;
    color: #2aa43b;
    padding: 4px 0px;
    text-decoration: none;
    margin: 0px 0px;
    display: block;
    position: absolute;
    top: 18px;
    right: 22px;
    font-size: 11px;
    z-index: 2;
}

.games-univ-mod4 a.games-schedule:hover {
    background: #fff;
    color: #41ca41;
}

.games-univ-mod4 a.games-schedule:before {
    content: 'Full ';
}

.games-univ-mod4 a.games-schedule:after {
    content: '';
}

.games-univ-mod5 {
    position: absolute;
    right: 22px;
    top: 16px;
    background: none;
    padding: 0;
    text-align: center;
    font-size: 11px;
    height: auto;
    width: auto;
    width: 42.5%;
}

.games-univ-mod5 ul li.games-btm-area:before,
.games-univ-mod5:before {
    content: 'Matchups:';
    display: block;
    width: 52.5%;
    color: #4d4d4d;
    text-align: left;
    border-bottom: 2px solid #2aa43b;
    font-size: 12px;
    padding: 2px 0 4px;
    line-height: 22px;
    box-sizing: border-box;
    margin-left: 20px;
    float: right;
}

.games-univ-mod5 ul li.games-btm-area:before {
    content: 'Transactions:';
    width: 100%;
}

.games-univ-mod5 ul {
    font-size: 11px;
}

.games-univ-mod5 ul li.games-firstlist {
    padding: 18px 0px 14px;
    font-size: 0;
    width: 52.5%;
    background: #fff;
    text-align: left;
    float: right;
}

.games-univ-mod5 ul li.games-secondlist strong,
.games-univ-mod5 ul li.games-firstlist strong {
    padding: 0 2px;
    font-size: 0;
    font-weight: 500;
}

.games-univ-mod5 ul li.games-secondlist strong:before,
.games-univ-mod5 ul li.games-firstlist strong:before {
    content: 'This Week: ';
    font-size: 12px;
    color: #4d4d4d;
}

.games-univ-mod5 ul li.games-secondlist strong:before {
 	content: 'Last Week: ';   
}

.games-univ-mod5 ul li.games-firstlist a, 
.games-univ-mod5 ul li.games-firstlist a:hover {
    text-decoration: none;
    font-size: 12px;
    color: #2aa43b;
    font-weight: 500;
}

.games-univ-mod5 ul li.games-firstlist a[href*="matchuppreview"],
.games-univ-mod5 ul li.games-firstlist a[href*="boxscore"] {
    background: #2f6bc8;
    margin: -4px 0 0;
    padding: 4px 6px;
    color: #fff;
    float: right;
    width: 35%;
    text-align: center;
}

.games-univ-mod5 ul li.games-firstlist a[href*="boxscore"]:after {
    content: ' Score';
}

.games-univ-mod5 ul li.games-firstlist a[href*="matchuppreview"]:hover,
.games-univ-mod5 ul li.games-firstlist a[href*="boxscore"]:hover {
    opacity: 0.8;
}

.games-univ-mod5 ul li.games-secondlist {
    background: #fff;
    padding: 0 0 0;
    line-height: 22px;
    height: auto;
    font-weight: 500;
    color: #4d4d4d;
    font-size: 0;
    text-align: left;
    width: 52.5%;
    float: right;
}

.games-univ-mod5 ul li ul {
	float: left;
    background: #fff;
    padding: 10px 0;
    line-height: 22px;
    font-size: 12px;
    font-weight: 500;
    color: #4d4d4d;
    width: 100%;
}

.games-univ-mod5 ul li ul li {
    line-height: 20px;
}

.games-univ-mod5 ul li ul li + li + li {
    color: #fff;
}

.games-univ-mod5 ul li ul li:not(:last-child) {
    display: block;
    padding: 0;
}

.games-univ-mod5 ul li ul li strong {
    font-weight: 500;
    width: 74%;
    display: inline-block;
    color: #4d4d4d;
}

.games-univ-mod5 ul li ul li a {
    font-size: 12px !important;
    display: inline-block;
}

.games-univ-mod5 ul li.games-btm-area {
    margin: 0;
    position: absolute;
    top: 0;
    left: 0;
    width: 40%;
    text-align: left;
}

.games-univ-mod5 ul li.games-secondlist a, 
.games-univ-mod5 ul li.games-secondlist a:hover {
    font-size: 12px;
    font-weight: 500;
    margin: 0 1px;
    color: #2f6bc8;
    text-decoration: none;
}

.games-univ-mod5 ul li.games-secondlist a[href*="boxscorequick"] {
    background: #4d4d4d;
    color: #fff;
    padding: 4px 6px;
    margin: -1px 0 0;
    float: right;
    width: 35%;
    text-align: center;
}

.games-univ-mod5 ul li.games-secondlist a[href*="boxscorequick"]:hover {
 	opacity: 0.8;   
}





/* Team Page - Week Picker Block ========================================================================== */


.context-clubhouse .games-topcol {
 	background: #fff;
    padding: 10px 0 16px !important;
    border-bottom: 1px solid #dedede;
}

.games-dates-mod {
    width: 100%;
    margin: 22px 0px -22px;
    padding: 8px 0;
    text-align: center;
    height: auto;
    border-top: 1px solid #ececec;
}

.games-dates-mod,
.games-dates-mod ul li.date-on div {
    background: #fafafa !important;
}

.games-dates-mod ul {
    padding: 0;
    width: 200px;
    margin: 0 auto;
}

.games-dates-mod ul li {
    float: none;
    display: inline-block;
    color: #41ca41 !important;
    margin: 0;
    line-height: 22px;
    z-index: 2;
    position: relative;
    vertical-align: top;
}

.games-dates-mod ul li h3 {
    display: none;
}

.games-dates-mod ul li a:link, .games-dates-mod ul li a:hover, .games-dates-mod ul li a:visited {
    color: #41c141;
    font-size: 14px;
}

.games-topcol .games-dates-mod ul li a, 
.games-topcol .games-dates-mod ul li a:hover {
    font-size: inherit;
    padding: 0;
    line-height: 22px;
}

.games-dates-mod ul li a:first-of-type {
    font-size: 0 !important;
    z-index: 3;
}

.games-dates-mod li:nth-of-type(2) a:before {
    content: '‹';
    font-size: 22px;
    z-index: 4;
    margin-right: -7px;
}

.games-dates-mod li.date-on + li a:after {
    content: '›';
    font-size: 22px;
    z-index: 4;
    margin-left: -7px;
}

.games-dates-mod li.date-on + li + li a:after {
    content: '››';
    font-size: 22px;
    z-index: 4;
    letter-spacing: -2px;
    position: absolute;
    right: -22px;
}

.games-dates-mod ul li.date-on {
    background: none;
    width: 134px;
    position: relative;
    vertical-align: top;
    z-index: 0;
    padding: 0 7px;
}

.games-dates-mod li.date-on:before, .games-dates-mod li.date-on:after {
    content: '›';
    font-size: 22px !important;
    position: absolute;
    right: 0;
    top: 0;
    opacity: 0.25;
    z-index: 0;
}

.games-dates-mod li.date-on:before {
    left: 0;
    right: initial;
    content: '‹';
}

.games-dates-mod ul li.date-on div {
    padding: 3px 0 0;
    margin-left: 0;
    border-radius: 0;
    font-weight: 500;
    color: #41ca41;
    font-size: 16px;
}







/* Home Page & Sidebar ==================================================================================== */


.lo-sidebar-box {
    background: #fff;
    border: 0px solid #f5f5f5;
    margin: 0 0 22px;
    padding: 18px 22px 22px;
    border-radius: 0;
    width: 100%;
    box-sizing: border-box;
    order: 2;
    position: relative;
}

.lo-sidebar-box br {
 	display: none;   
}

.needHelpModule + .lo-sidebar-box {
    order: 1;
}

.lo-sidebar-box .content {
    margin: 0 !important;
}

.lo-sidebar-box h1 {
    background: #fff;
    color: #4d4d4d;
    padding: 0 0 7px;
    margin: 0 0 12px !important;
    border-bottom: 2px solid #2aa43b;
}

.lo-message-info {
    width: 70%;
}

.lo-message-info a, .lo-message-info a:link {
 	font-weight: 500;
    display: inline-block;
    margin-bottom: 4px;
}

.lo-message-author {
    display: block;
}

.lo-message-replies {
    width: 25%;
    background: #e3f5e5;
}

.lo-message-replies + .clear {
 	display: none;   
}

.lo-message-entry {
    padding: 8px 0;
}

.lo-sidebar-box table {
    margin-top: -8px;
    width: 100% !important;
}

.lo-sidebar-box .division-name {
    background: #e3f5e5;
    padding: 6px;
    text-align: center;
    border: solid #fff;
    border-width: 20px 0 8px;
}

.lo-sidebar-box tr td {
    padding: 4px 3px;
}

.lo-sidebar-box td em {
    padding: 12px 0 4px;
    display: block;
    text-align: center;
    color: #2aa43b;
}

.lo-sidebar-box > .trophy-links-wrap,
.lo-sidebar-box > .trophy-links-wrap a,
.lo-sidebar-box > a.action-link {
    background: #fff;
    color: #2aa43b !important;
    text-decoration: none ;
    padding: 6px 8px;
    border-radius: 2px;
    font-size: 11px !important;
    font-weight: 500 !important;
    letter-spacing: 0.1px;
    margin: 0 auto;
    display: inline-block;
    text-align: center;
	position: absolute;
    top: 15px;
    right: 14px;
}

.lo-sidebar-box > .trophy-links-wrap a {
 	position: relative;
    top: 0;
    right: 0;
    padding: 0;
}

.lo-sidebar-box > a.action-link + a {
	right: 86px;
}

.lo-sidebar-box a.action-link:hover {
    color: #41ca41;
}

.columnist-parent {
    margin: 0;
    padding: 18px 0 0;
}

.columnist-parent.columnist-first {
    padding-top: 8px;
}

.columnist-mug {
    width: 18%;
}

.columnist-details {
    width: 80%;
}

.columnist-details div {
    font-size: 11px;
    color: #4d4d4d;
    padding-bottom: 2px;
    letter-spacing: 0.1px;
}

.needHelpModule {
    display: none;   
}

.needHelpModule p {
    margin: 0 !important;
}

.needHelpModule ul {
    float: none;
    margin: 6px 0 -6px;
}

.needHelpModule button {
    display: none;
}

#lm-note h2,
#lo-recent-activity h1 {
    background: #fff;
    width: 100%;
    color: #4d4d4d ;
    font-size: 14px;
    padding: 0 0 7px;
    margin-bottom: 10px ;
    border-bottom: 2px solid #2aa43b;
    font-weight: 500;
}

#lo-recent-activity {
 	background: #fff;
    padding: 18px 22px;
    margin: 0;
    position: relative;
}

#lo-recent-activity-list, #lo-recent-activity-list ul {
    float: none;
}

#lo-recent-activity-list li.lo-recent-activity-item {
    float: none;
    border: 0;
    border-bottom: 1px solid #eee;
    padding: 0;
}

li.lo-recent-activity-item ul {
    padding: 12px 16px !important;
    box-sizing: border-box;
}

li.lo-recent-activity-item:nth-of-type(even) ul {
    background: #fff;
}

#lo-recent-activity-list li ul li.recent-activity-when, #lo-recent-activity-list li ul li.recent-activity-image, #lo-recent-activity-list li ul li.recent-activity-description {
    float: none;
    display: inline-block;
    vertical-align: top;
    line-height: 22px;
}

#lo-recent-activity-list li ul li.recent-activity-when {
    width: 144px;
    color: #4d4d4d;
}

#lo-recent-activity-list li ul li span.recent-activity-date {
    width: 55px;
    display: inline-block;
    font-size: 14px;
}

#lo-recent-activity-list li ul li span.recent-activity-time {
    font-style: normal;
    color: #7b7b7b;
    margin-left: 12px;
    font-size: 13px;
}

#lo-recent-activity-list li ul li.recent-activity-image {
    margin: 1px 18px 0 0;
}

#lo-recent-activity-list li ul li.recent-activity-description {
    font-size: 12px;
    width: 450px;
    color: #4d4d4d;
    letter-spacing: 0.1px;
    position: relative;
}

#lo-recent-activity a, #lo-recent-activity a:link, #lo-recent-activity a:visited {
    font-size: 12px;
    padding: 0 1px;
    line-height: 22px;
    text-decoration: none;
    font-weight: 500;
    color: #2f6bc8;
}

#lo-recent-activity-list li.lo-recent-activity-item.bottom-item {
    padding-bottom: 16px;
}

#lo-recent-activity-list span.recent-activity-faab-amount {
    background: #7a7;
    color: #fff;
    padding: 2px 4px;
    border-radius: 2px;
    letter-spacing: 0.5px;
    font-size: 12.5px;
    font-weight: 500;
    position: absolute ;
    top: 50% ;
    transform: translateY(-50%);
    right: 18%;
    width: 36px;
    text-align: center;
}

#lo-recent-activity-list li ul li.recent-activity-fulldesc:not([style*="none"]) {
    margin: 12px 0 4px;
    padding: 8px 14px;
    float: none;
    margin-left: 182px;
    box-sizing: border-box;
    background: #e3f5e5;
    border: 1px solid #f7f7f7;
    border-radius: 4px;
    line-height: 22px;
    width: 65% ;
    display: inline-block;
}

#lo-recent-activity-list li ul li.recent-activity-fulldesc b {
    color: #4d4d4d;
    font-size: 13px;
    font-weight: 500;
}

#lo-recent-activity-tools {
    background: none;
    position: absolute;
    top: 6px;
    right: 10px;
}

#lo-recent-activity-tools a.action-link {
    background: #fff;
    color: #2aa43b ;
    text-decoration: none ;
    padding: 2px 4px 0;
    border-radius: 2px;
    font-size: 11px ;
    font-weight: 500 ;
    letter-spacing: 0.1px;
    display: inline-block;
    text-align: center;
    margin-right: -2px;
}

#lo-recent-activity-tools a.action-link:hover {
    color: #41ca41;
}

div#lm-note {
    width: 100%;
    margin: 0 0 22px;
    background: #fff;
    padding: 18px 22px;
    box-sizing: border-box;
}

#lm-note h1 {
    font-size: 24px;
    margin: 10px 0 16px !important;
    display: inline-block;
    text-align: center;
    width: 100%;
}

#lm-note .lm-note-copy {
    float: none;
