.pendingMoveBatchDiv {
    background: transparent;
    padding: 0 48px 0 66px;
    margin: 24px 0;
    font-size: 11px;
    line-height: 20px;
    position: relative;
    color: #222;
    border-top: 0px solid #e5e5e5;
    border-bottom: 0 !important;
}

.pendingMoveBatchDiv b {
    color: #2f6bc8;
    font-weight: 500;
    padding: 0 1px;
    font-size: 12px;
}

.pendingMoveBatchDiv form {
    position: absolute;
    left: 40%;
    top: 50%;
    transform: translateY(-50%);
    font-size: 0;
    line-height: 26px;
}

.pendingMoveBatchDiv form br {
    display: none;
}

.pendingMoveBatchDiv form span {
    vertical-align: top;
    display: inline-block;
    height: 26px !important;
}

.pendingMoveBatchDiv form span[id*="editBid"] {
    font-size: 11px;
    background: #fff;
    color: #222;
    border: 1px solid #7b7b7b;
    padding: 0 0 0 11px;
    text-align: center;
    border-radius: 3px;
}

.pendingMoveBatchDiv form span[id*="editBid"]:before {
    content: '$';
}

.pendingMoveBatchDiv form strong {
    font-size: 11px;
    background: #2aa43b;
    color: #fff;
    font-weight: 500;
    padding: 0;
    width: 42px !important;
    border-radius: 3px;
    display: inline-block;
    text-align: center;
    line-height: 26px;
}

.pendingMoveBatchDiv form strong ~ a {
    font-size: 10px;
    text-transform: uppercase;
    text-decoration: none !important;
    position: absolute;
    left: 0;
