css:
@-moz-document domain("www.komplett.no"), domain("www.komplett.se"), domain("www.komplett.dk") {
.banner-carousel-image {
    /* display: none !important; */
}
body {
    background-image: none;
    background-color: #1e1e1e !important;
}
.navigation-header {
    background: #1e1e1e !important;
}
.row hidden-xs hidden-sm {
    background-color: #1e1e1e !important;
}
.caas .navigation-header .top-nav .btn {
    background-color: #1e1e1e !important;
}
.caas .btn-default {
    background-color: #1e1e1e !important;
}
.caas .multi-level-navigation ul.first-level li.first-level button.first-level {
    background-color: #1e1e1e !important;
}
.caas .navigation-header .search .input-group .search-form-input.form-control {
    background-color: #1e1e1e !important;
}
.caas .main-body {
    background-color: rgba(30, 30, 30, 0) !important;
    border: solid;
    border-width: 1px;
    box-shadow: 0px 0px 7px 0px black;
}
.caas .footer {
    background-color: rgba(30, 30, 30, 0) !important;
}
*,
:after,
:before {
    color: crimson !important;
}
.caas .recommendations-title.with-border:before {
    border-top: 1px solid #434343;
}
.caas .recommendations-title-content {
    background: #1e1e1e !important;
    border-style: solid !important;
    border-width: 1px !important;
    border-color: rgb(67, 67, 67) !important;
}
.caas .navigation-header .logo>a>img {
    filter: grayscale(100%);
    box-shadow: inset 0px 0px 7px 0px black;
    background-color: #fdfdfd;
    border-style: solid;
    border-width: 0px;
    padding: 6px;
    border-radius: 2px 20px 2px 20px;
}
.caas .multi-level-navigation ul.second-level li.second-level a.second-level,
.caas .multi-level-navigation ul.second-level li.second-level button.second-level {
    background: #1e1e1e !important;
}
.caas .multi-level-navigation .third-level-positioner .third-level-container {
    background-color: #1e1e1e !important;
}
.caas .multi-level-navigation div.third-fourth-level {
    background-color: #1e1e1e !important;
}
.caas .multi-level-navigation ul.fourth-level li.fourth-level a.fourth-level,
.caas .multi-level-navigation ul.fourth-level li.fourth-level button.fourth-level {
    background-color: #1e1e1e !important;
}
.caas .multi-level-navigation ul.second-level li.second-level a.second-level,
.caas .multi-level-navigation ul.second-level li.second-level button.second-level {
    border-bottom: 1px solid #434343 !important;
}
.caas .multi-level-navigation .second-level-container {
    border: 1px solid #434343 !important;
    border-bottom: 1px solid #434343 !important;
}
.caas .multi-level-vertical-menu .category-groups .category-group {
    background-color: #1e1e1e !important;
}
.caas .navigation-header .main-header {
    z-index: 9999;
    box-shadow: 0px 0px 20px black;
    border-bottom: 0px solid #f8b912 !important;
}
.caas .navigation-header .main-header:after {
    display: none;
}
.caas .product-section-menu .menu-bar {
    background-color: #1e1e1e !important;
}
.banner-image img {
    background-color: rgba(240, 248, 255, 0) !important;
}
.caas .multi-level-navigation .fifth-level-positioner .fifth-level-container {
    background-color: #1e1e1e !important;
}
.caas .search-view-controls {
    background-color: #1e1e1e !important;
}
.caas .facets .facet .listing ul li a {
    background-color: #1e1e1e !important;
}
.caas .facets .facet .slider .values .value {
    background-color: #1e1e1e !important;
}
.caas form.responsive-form .checkbox-set input[type=checkbox]+label:before {
    background: #434343;
    border-radius: 2px;
}
.caas .product-list-item:focus,
.caas .product-list-item:hover {
    box-shadow: 0 0 20px 3px #000000 !important;
}
.caas .product-list-item {
    border: none;
}
.caas .product-main-info .product-main-info-buy {
    background-color: #1e1e1e !important;
}
.caas .product-main-info .new-arrangement .product-main-info-buy {
    border: 1px solid #434343 !important;
}
.caas .product-main-info .links .linkbox {
    background-color: #1e1e1e !important;
    border: 1px solid #dc143c !important;
}
.caas .product-section {
    border-top: 1px solid #434343 !important;
}
.a {
    background-color: #1e1e1e !important;
}
.caas .multi-level-navigation ul.first-level li.first-level a.first-level:hover,
.caas .multi-level-navigation ul.first-level li.first-level button.first-level:hover {
    background-color: #434343 !important;
}
}
