#_0507f777-2480-4d48-baf1-3b9c8feeb2b4_-browser-action {
    background-image: none !important;
    background-position: center !important;
    background-repeat: no-repeat !important;
    filter:opacity(60%) !important;
}
#_0507f777-2480-4d48-baf1-3b9c8feeb2b4_-browser-action:hover {
    background-color: none !important;
    background-image: none !important;
    filter:opacity(75%) !important;
/*
    -moz-transform: rotate(-43deg) !important;
    -moz-transition-duration: 0.2s !important; 
*/
}
#_0507f777-2480-4d48-baf1-3b9c8feeb2b4_-browser-action:active, #_0507f777-2480-4d48-baf1-3b9c8feeb2b4_-browser-action[open] {
    background-color: none !important;
    background-image: none !important;
    filter:opacity(90%) !important;
    -moz-transform: rotate(1440deg) !important;
    -moz-transition-duration: 0.8s !important; 
    -moz-transition-timing-function: ease-out;
}
#_0507f777-2480-4d48-baf1-3b9c8feeb2b4_-browser-action .toolbarbutton-badge-stack {
    background: none !important;
    border: none !important;
}
