css:
@-moz-document domain("lainchan.org") {
/* Lain Comfy Compact
 * Created by: Desu
 * Mascot: TMP - Girls Frontline */

body {
	background: #141414;
    color: #afafaf;
}
body, body * {
    font-size: 12px!important;
}
body #pagewrap {
    margin-right: 50%;
}
img.board_image {
    display: none;
}
strong {
	color: #d8d8d8;   
}
pre {
    font-size: 1em;
    max-width: 100%!important;
    overflow-x: auto;
    overflow-y: none;
}
pre .hljs {
    padding: .5em;
}
textarea, input[type="text"], input[type="submit"], input[type="button"], input[type="file"], button, select {
	background: rgba(255,255,255,.1);
    border: 1px solid rgba(0,0,0,.9);
    cursor: pointer;
}
textarea:hover, input[type="text"]:hover, input[type="submit"]:hover, input[type="button"]:hover, button:hover, select:hover {
	background: rgba(0,0,0,.6);
    border: 1px solid rgba(0,0,0,.9);
}
button, input[type="button"] {
	padding: 3px;
    border-radius: 3px;
}

/*home and other pages*/
div.ban h2, .box.left h2, .box.right h2 {
	background: #333;
    color: #111;
}
div.box {
	background: transparent!important;
    color: #ccc!important;
}
/*make homepage images not as obtrusively large*/
center img.lain_banner {
	width: 300px; 
}
#global {
	overflow: auto;   
}
/*fix the alignment of the stats box*/
body > div.box {
	clear: both!important;
    float: none!important;
    margin: 0 auto;
}

/*header*/
div.bar,
div.bar.top {
    border: 0!important;
}
div.boardlist {
    text-shadow: none!important;
    border-color: #111!important;
    background: #222!important;
}
header {
    margin: 3em 0 1em 0;
}
header h1 {
    font-size: 16px!important;
}
h2 {
	color: #999;   
}
header div.subtitle {
    color: #ccc!important;
}
div.blotter {
    margin-bottom: 2em;
}

/*catalog*/
div#Grid div.mix div.thread {
    background-color: transparent;
    cursor: pointer;
}

/*remove distracting colors from blotter*/
div.blotter,
div.blotter * {
    color: #ccc!important;
    font-weight: normal;
    font-size: .9em;
}
div.blotter a {
    text-decoration: underline;
}
form[name="postcontrols"] {
    margin-top: 2em;
}

/*remove clutter*/
hr,
.unimportant {
    display: none;
}

/*thread*/
span.omitted {
    padding: 0 .5em;
    color: #f4d5d2;
}
span.quote {
    color: #92e5a0;
}
div.files p.fileinfo, div.files p.fileinfo * {
    color: #666;
}
.post-image {
    margin: 0 5px 0 0!important;
    width: 200px!important;
    height: auto!important;
    padding: 6px!important;
}
div.post.op .post-image {
	margin: 0 5px 5px 0!important;   
}
div.files div.multifile {
    width: 150px!important;
    margin-right: 1em;
}
div.files div.multifile p.fileinfo {
	margin: 0 0 3px 0!important;
    padding: 0!important;
}
div.files div.multifile p.fileinfo, div.files div.multifile p.fileinfo * {
	font-size: 10px!important;
}
div.files div.multifile .post-image {
	width: 150px!important;
    height: auto!important;
    padding: 0!important;
}
div.thread {
    clear: both;
    overflow: auto;
    margin: .5em 0;
}
div.thread div.post {
    width: auto!important;
    display: block;
}
div.thread > br {
    display: none;
}
div.thread div.postcontainer {
    overflow: auto;
    margin: 0 0 5px 0!important;
    clear: both;
}
div.post.op {
    margin: 0 0 10px 0!important;
}
div.post.reply {
    overflow: auto;
    padding: 0;
    border-color: #333;
    margin: 0!important;
    background: #222!important;
    max-width: inherit!important;
}
div.post div.body {
    margin: 0!important;
    padding: .5em;
}
div.post div.body a {
	color: #8ce8ff!important;   
}
div.post div.body a:hover {
	text-decoration: underline!important;   
}
p.intro {
    font-size: 1em!important;
    padding: .5em .5em 0 .5em;
}
p.intro a {
    color: #f4f2b2;
}
p.intro span.subject {
    color: #3de589;
}
p.intro span.name {
    color: #76897f;
}

/*post form*/
form[name="post"] {
    display: block;
    position: fixed;
    top: 3.5em;
    right: 2em;
    background: rgba(0, 0, 0, .5);
    padding: .5em;
    border-radius: 3px;
}
form[name="post"] table tr th {
    background: #222;
    padding: 0 .5em;
    font-size: .8em;
}
input[name="post"],
input[type="file"],
textarea#body,
input[type="text"],
select {
    border: 1px solid #111;
}
input[name="post"],
input[type="file"] {
    font-size: 1em;
}
input[name="post"] {
    background: #adaeb2;
    border: 1px solid #000;
    border-radius: 7px;
    color: #222;
    cursor: pointer;
}
input[name="subject"] {
    float: none!important;
}
input[name="url"], input[name="q"] {
    display: block;
    clear: both!important;
}
input[name="spoiler"],
label[for="spoiler"] {
    display: block;
    position: absolute;
    bottom: 6px;
    right: 120px;
}
label[for="spoiler"] {
    width: 100px;
    right: 20px;
    bottom: 11px;
    font-size: 10px!important;
}
form[name="post"] div.format-text {
    width: 270px!important;
}
textarea#body {
    width: 270px!important;
}

/*unfix the footer menu*/
.bar.bottom {
    position: relative!important;
}

/*
 *begin base64 mascot as background
 *to replace with your own mascot, encode your image here first: https://www.base64-image.de/
 */
body.active-index, body.active-thread, body.active-ukko {
    background: #141414  url('data:image/png;base64,') 72% bottom fixed no-repeat;
}
}
