@-moz-document domain("www.zone-telechargement.ws") {
.recherche_menu {
    position: fixed!important;
    top: 0px!important;
    left: 0!important;
    z-index: 2!important;
    padding: 5px 100% 5px calc(50% - 90px)!important;
    background: linear-gradient(to bottom,#e5e5e5,#dedede)!important;
    box-shadow: 0 1px 3px rgba(0,0,0,.3)!important;
}
.searchbox .sinputt input{
    /*background-color: #eaeaea!important;
    border: 1px solid #d3d3d3!important;
    box-shadow: none!important;*/
