css:
@-moz-document domain("polygon.com"), domain("theverge.com"), domain("riftherald.com"), domain("heroesneverdie.com"), domain("theflyingcourier.com"), domain("vox.com") {
/* Articles */
.l-root {
    background-color: black;
}
.l-main-content {
    background-color: black;
    color: grey;
}
.c-page-title {
    color: lightgrey !important;
}
.c-entry-content {
    color: lightgrey;
}
.c-entry-content blockquote {
    background-color: #202020;
}
.c-comments__cbody {
    color: lightgrey;
}
span.e-image__image {
    border: 1px solid;
    border-image: linear-gradient(to right, #8e2a87 , #ff0054);
    border-image-slice: 1;
}
.c-action-box {
    background-color: #202020;
}
.c-comments__message {
    background-color: #202020;
    color: grey;
}

/* Home page */
.c-showcase-eight-up__main {
    background-color: #202020;
    background: linear-gradient(to right, #8e2a87 , #ff0054);
}
.c-entry-box--compact {
    background-color: black !important;
}
.c-entry-box--compact__title {
    color: grey;
}
.l-hub-wrapper {
    background-color: black;
}
.c-showcase-eight-up .c-entry-box--compact:nth-child(2) {
    border-top: 0;
}
.c-two-group-breaker__main {
    background-color: #202020;
}
.c-rock-newsletter {
    background-color: #202020;
}
.c-two-group-breaker__item-1up .c-entry-box--compact__title a {
    color: white;
}
.c-two-up .c-entry-box--compact {
    border-left: 0;
}
.c-two-up .c-entry-box--compact:nth-child(2) {
    border-right: 0;
}
.c-two-up .c-byline, .c-two-up .c-entry-box--compact__community, .c-two-up .c-entry-sponsorship {
    color: grey;
}

/* category page titles */
.p-page-title {
    color: lightgrey;
}
.c-group-header {
    border-image: linear-gradient(to right, #8e2a87 , #ff0054);
    border-image-slice: 1;
}

/* reviews */
.c-scorecard-meta {
    background-color: #606060;
}
.c-scorecard {
    background-color: #606060;
}
.c-entry-sidebar {
    background-color: #202020;
    color: lightgrey;
}
.c-scorecard__additional-info {
    background-color: #202020;
}

/* guide hub */
.c-hub-title__inner {
    background-color: black;
    color: lightgrey;
}
.c-hub-title__inner img {
    background-color: white;
}

/* entry */
.c-compact-river__entry {
    border-image: linear-gradient(to right, #8e2a87 , #ff0054);
    border-image-slice: 1;
}

/* feedback */
.p-feedback-form {
    color: grey;
}
.p-feedback-form .p-search-input, .p-feedback-form .p-selectbox, .p-feedback-form .p-text-input, .p-feedback-form .p-textarea {
    background-color: #202020;
    color: lightgrey;
}

/* archives */
.c-pagination {
    background-color: #202020;
}

/* comics */
.c-showcase-five-up__main {
    background: #202020;
    background: linear-gradient(to right, #8e2a87 , #ff0054);
}

/* newsletter signup */
.c-newsletter-signup-form .p-text-input {
    background-color: black;
    color: lightgrey;
}

.c-tab-bar {
    background: hsla(0, 0%, 28%, 0.9);
}

.c-tab-bar__logo {
    background-color: rgba(0, 0, 0, 0.50);
}

/* Guides */
.c-package-subnav .collapsible .c-package-subnav__items {
    background-color: #202020;
}
.c-package-subnav__nested-vertical ul ul a {
    color: lightgrey;
}
.c-package-subnav__nested-vertical.c-package-subnav__recirc .c-package-subnav__text {
    background-color: black;
    border: lightgrey solid 1px;
}
.c-call-to-action-button__label {
    color: white;
}
}

@-moz-document domain("theverge.com") {
/* homepage */
.c-seven-up .c-entry-box--compact:last-child {
    color: lightgrey;
}
.c-byline__item {
    color: grey;
}

/* Entry */
.c-compact-river__entry {
    border-image: linear-gradient(to right, #f8c627 , #e5127d);
    border-image-slice: 1;
}

/* articles */
.c-group-header {
    border-image: linear-gradient(to right, #f8c627 , #e5127d);
    border-image-slice: 1;
}
span.e-image__image {
    border-image: linear-gradient(to right, #f8c627 , #e5127d);
    border-image-slice: 1;
}

/* comments */
.c-comments__recommended {
    background-color: #202020;
}
.c-comments__cbody {
    color: grey;
}
}

@-moz-document url-prefix("https://www.theverge.com/forums"), regexp("https:\\/\\/www\\.theverge\\.com\\/\\d{4}\\/\\d{1,2}\\/\\d{1,2}\\/\\d+\\/[\\-a-zA-Z0-9]+") {
/* forums */
#verge {
    background-color: black;
    color: grey;
}
.l-page {
    background-color: black;
}
.p-big-search .label {
    background-color: #505050;
}
.p-big-search .label:after {
    border-left-color: #505050
}
.p-big-search {
    border-bottom-color: #505050;
}
.p-big-search.-has-label form input[type='text'] {
    color: lightgrey;
}
.m-forum-index__icons h3 {
    color: #ec008c;
}
.m-forum-index__table tr:nth-child(even) {
    background-color: #202020;
}
.m-forum-index__table th:nth-child(1) a, .m-forum-index__table td:nth-child(1) a {
    color: #a0a0a0;
}
.m-forum-index__table th:nth-child(1) span, .m-forum-index__table td:nth-child(1) span {
    color: #505050;
}

/* forum posts */
.p-entry-header {
    border-top: 1px solid #ddd;
}
.p-entry-header h1 {
    color: lightgrey;
}
.p-entry-header__comments {
    background-color: #7d004a;
}
.p-entry-header__comments:after {
    border-top-color: #7d004a;
}
.p-entry-header__comments:before {
    color: inherit;
}
.m-forum-header {
    background-color: #505050;
}

/* blockquotes */
.m-article__entry blockquote, .m-stream__header blockquote, .m-comments .citem .cbody blockquote {
    background-color: #202020;
    color: grey;
}

/* poll in post */
.m-poll {
    background-color: #505050;
}
.m-poll__body {
    border: 1px solid grey;
    background-color: #202020;
    color: lightgrey;
}
.m-poll__body.results header h6 {
    color: inherit;
}
.m-poll__thermometer {
    background-color: black;
}
.m-poll__controls {
    border: 1px solid grey;
    border-width: 0px 1px;
    color: grey;
    background-color: black;
}
.m-poll__totals {
    color: inherit;
}
.m-comments .comments-header {
    background-color: #202020;
}
}

