}
/* i really want this to be global */
.header-panel-button.selected {
    background: linear-gradient(#020014, #060032);
    -webkit-box-shadow: inset 0 -2px 0 -1px #060032;
    -moz-box-shadow: inset 0 -2px 0 -1px #060032;
    box-shadow: inset 0 -2px 0 -1px #060032;
}
#footer {
    background: #010103;
    -webkit-box-shadow: inset 0 2px 0 -1px #010103;
    -moz-box-shadow: inset 0 2px 0 -1px #010103;
    box-shadow: inset 0 2px 0 -1px #010103;
}
#playlist-button {
    background: #010103;
    -webkit-box-shadow: inset 0 2px 0 -1px #010103;
    -moz-box-shadow: inset 0 2px 0 -1px #010103;
    box-shadow: inset 0 2px 0 -1px #010103;
}
#footer .bar-button .section .chevron:before {
    background: #ffffff;
}
#footer .bar-button .section .chevron:after {
    background: #ffffff;
}
.social-menu {
    display: none;
}
#dj-button {
    background: rgba(0, 0, 0, 0.4);
}
#dj-button.is-leave .left,
#dj-button.is-quit .left {
    background: #f46b40;
    background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0, rgba(0, 0, 0, 0) 100%);
}
#playback .background {
    opacity: 0.2;
}
#playback-controls .button.refresh {
    background: rgba(0, 181, 230, 0.4);
}
#playback-controls .button.hd {
    background: rgba(66, 165, 220, 0.4);
}
#playback-controls .button.snooze {
    background: rgba(99, 52, 189, 0.4);
}
#playback-controls.button.p3-fullscreen {
    background-color: rgba(0, 50, 230, 0.4);
    width: 25%;
}
.room-background {
    left: 307.5px;
    top: 54px;
    width: 1600px;
    height: 900px;
    background: url(https://a.pomf.space/adwdrqcjgdrq.png) no-repeat !important;
}
#room .app-right {
    background: rgba(10, 10, 10, 0.4);
}
#chat .system {
    -webkit-box-shadow: inset 0 0 0 1px #42a5dc;
    -moz-box-shadow: inset 0 0 0 1px #42a5dc;
    box-shadow: inset 0 0 0 1px rgba(0, 0, 0, 0.4);
}
#chat .system {
    padding: 6px 0 7px;
    background: rgba(0, 0, 0, 0);
    color: #fff;
}
#chat-header {
    display: none;
}
#chat-header,
#user-lists .header,
#waitlist .header {
    background-color: rgba(0, 0, 0, 0.4);
}
#chat .cm:nth-child(2n+1) {
    background-color: rgba(0, 0, 0, 0.4);
}
#user-lists,
#waitlist {
    background-color: rgba(0, 0, 0, 0.4);
}
#user-lists .header,
#waitlist .header {
    border-bottom: rgba(255, 255, 255, 0) 0px solid;
}
#user-lists .header,
#waitlist .header {
    border-bottom: rgba(255, 255, 255, 0) 0px solid;
}
#waitlist .header .divider {
    display: none;
}
.app-right .friends .list-header .divider {
    display: none;
}
.app-right .friends {
    background: rgba(10, 10, 10, 0);
}
#user-lists .list-header {
    border-bottom: #1c1f25 0px solid;
}
.app-right .friends .list-header .input-background {
    display: none;
}
.app-right .friends .list-header {
    border-bottom: #1c1f25 0px solid;
}
#chat-input {
    background: rgba(0, 0, 0, 0);
}
#chat-messages {
    border-bottom: #000000 0px solid;
    color: #fff;
    font-size: 12px;
    font-family: Helvetica;
}
.badge-box {
    border-radius: 0px;
    background: rgba(0, 0, 0, 0);
}
#woot .bottom,
#woot.selected {
    background: rgba(4, 255, 0, 0.4);
}
#vote .crowd-response {
    background: rgba(0, 0, 0, 0.4);
}
#vote .crowd-response.disabled .bottom {
    background: rgba(0, 0, 0, 0.4);
}
#grab .bottom,
#grab.selected {
    background: rgba(170, 116, 255, 0.4);
}
#meh .bottom,
#meh.selected {
    background: rgba(255, 0, 22, 0.4);
}
#footer-user .back,
#footer-user .signup {
    background: rgb(5, 0, 46);
}
#room-settings-menu {
    background: rgba(28, 31, 37, 0);
}
#room-settings-menu .title {
    color: #ffffff;
    text-transform: uppercase;
    text-align: center;
}
#room-settings {
    background: rgba(17, 19, 23, 0.9);
}
.dropdown {
    background: rgba(40, 44, 53, 0);
}
#room-settings .general-settings .option button {
    background: rgba(40, 44, 53, 0);
}
#room-settings .general-settings .option button.off {
    background: rgba(66, 165, 220, 0.4);
}
#room-name {
    text-transform: uppercase;
}
#search {
    background: linear-gradient(#060032, #020014);
    -webkit-box-shadow: inset 0 -2px 0 -1px #020014;
    -moz-box-shadow: inset 0 -2px 0 -1px #020014;
    box-shadow: inset 0 -2px 0 -1px #020014;
}
#playlist-panel {
    background: rgba(0, 0, 0, 0.9);
}
#playlist-menu {
    background: rgba(0, 0, 0, 0);
}
#playlist-edit-button {
    background: rgba(146, 90, 255, 0.4);
}
#playlist-shuffle-button {
    background: rgba(69, 122, 255, 0.4);
}
#playlist-delete-button {
    background: rgba(255, 0, 0, 0.4);
}
#search-bar {
    background: rgba(0, 19, 58, 0);
    -webkit-box-shadow: inset 0 0 0 1px #ffffff;
    -moz-box-shadow: inset 0 0 0 1px #ffffff;
    box-shadow: inset 0 0 0 1px #ffffff;
}
#filter-bar {
    background: rgba(17, 19, 23, 0);
    -webkit-box-shadow: inset 0 0 0 1px #ffffff;
    -moz-box-shadow: inset 0 0 0 1px #ffffff;
    box-shadow: inset 0 0 0 1px #ffffff;
}
.media-list .row.now-playing {
    border-bottom: rgb(255, 255, 255) 0px solid!important;
    background: rgba(9, 0, 140, 0.4)!important;
}
#playlist-menu .menu .row .count {
    color: #ffffff;
    font-weight: 800;
}
#playlist-import {
    background: rgba(9, 0, 140, 0.4)!important;
}
#playlist-create {
    background: rgba(9, 0, 140, 0.4)!important;
}
.media-list .meta {
    border-bottom: #ffffff 0px solid;
}
.media-list img {
    margin-top: 0px;
    margin-left: -1px;
    width: 55px;
    height: 55px;
}
