/*** Global ***/
* {
  font-family: "Source Sans Pro", sans-serif;
  box-sizing: border-box;
}
body {
  background-color: #ddd;
  text-align: center;
  color: #212121;
  padding-bottom: 3rem;
}
#header_holder {
  width: 90rem;
  margin: 0 auto;
}

#header_logo a,
#site_menu span:last-child a,
form#searchsearch_submit input[type="submit"],
.show_info_rating_score,
.show_info_rating_score a,
p#back-top a {
  transition: background 200ms ease-out, color 200ms ease-out, opacity 200ms ease-out;
}
site_menu a {
  transition: border-bottom 2s ease-in;
}

/* Warning message */
table:nth-of-type(2) {
  display: none;
}


/*** Menu ***/
#site_menu {
  cursor: default;
  position: absolute;
  padding: 1rem 12rem 1rem;
  top: 0;
  left: 0;
  width: 100%;
  height: 3.6rem;
  text-align: left;
  background-color: #272727;
  color: #272727;
  border: none;
/*   box-shadow: 0 .1rem .1rem rgba(0, 0, 0, .7); */
}
#site_menu img:first-child {
  display: none;
}
#site_menu img {
  display: inline-block;
  margin: -.15rem .5rem 0 0;
  width: 1px;
  height: 1rem;
/*   border: 1px solid #1b7fe8; */
  background: #373737;
  content: url('data:image/gif;base64,R0lGODlhAQABAIAAAAAAAP///yH5BAEAAAAALAAAAAABAAEAAAIBRAA7');
}
#site_menu a {
  display: inline-block;
}
#site_menu a:hover {
  text-decoration: none;
  border-bottom: 1px solid #dd9d1c;
}

/*** Login Button ***/
#site_menu span:last-child {
  position: relative;
  top: -.1rem;
  left: 1rem;
  padding: 0 !important;
  background-color: transparent !important;
}
#site_menu span:last-child a {
  padding: .1rem .9rem .2rem;
  border-radius: .2rem;
  background: #1b7fe8;
}
#site_menu span:last-child a:hover {
  text-decoration: none;
  border: none;
  background: #dd9d1c;
}
#site_menu span:last-child img {
  display: none;
}

/*** Logo ***/
#header_holder > div:first-child {
  width: auto !important;
  position: absolute !important;
  top: 1.2rem;
  left: 1rem;
  z-index: 9999;
}
#header_logo {
  background: none;
  width: inherit;
  height: inherit;
}
#header_logo a::before {
  content: "EZTV";
}
#header_logo a {
  font-family: 'Merriweather', serif;
  font-size: 2rem;
  letter-spacing: .1rem;
  color: #1b7fe8;
}
#header_logo a:hover {
  text-decoration: none;
  color: #dd9d1c;
}
#header_logo img {
  display: none;
}

/*** Search ***/
table.forum_header_border:first-of-type {
  cursor: default;
  box-sizing: border-box;
  position: absolute !important;
  width: auto !important;
  top: .5rem !important;
  right: 1rem;
  padding: .5rem .8rem;
  color: #eee;
  border: none;
  border-radius: .2rem;
  background: #333;
  z-index: 9999;
}
.forum_header_border:first-of-type .section_create {
  padding: 0;
}

form#searchsearch_submit {
/*   padding-top: .2rem; */
  padding-left: .3rem;
}
.forum_header_border:first-of-type tr,
.forum_header_border:first-of-type td {
  background: none !important;
}

/* Fields & Button */
form#searchsearch_submit > div:nth-child(3) {
/*   float: none !important; */
/*   display: inline-block; */
  display: none;
}
form#searchsearch_submit input,
form#searchsearch_submit select {
  height: 1.6rem !important;
  border-radius: .2rem !important;
}
form#searchsearch_submit input[type="txt"] {
  margin-left: .2rem;
  margin-right: .6rem;
  width: 12rem !important;
}

/* Button */
form#searchsearch_submit input[type="submit"] {
  padding: 0 .6rem !important;
  font-size: 1rem !important;
  font-weight: normal;
/*   background: #1b7fe8; */
  background: #666;
}
form#searchsearch_submit input[type="submit"]:hover {
  background: #dd9d1c;
}

/* Drop-down */
form#searchsearch_submit .tv-show-search-select,
form#searchsearch_submit .select2,
form#searchsearch_submit #select2-q2-ba-container {
  margin-left: .6rem;
  margin-right: .6rem;
  width: 12rem !important;
  border-radius: .2rem !important;
}
form#searchsearch_submit .select2-results {
  text-align: left;
  color: #000;
}

/*** Gap below the header ***/
table.forum_header_border:first-of-type + #gap {
  margin-top: 4rem;
}

/*** News ***/
#gap + .forum_header_border tr {
  background: none !important;
}
a.thread_link,
a.thread_link:visited {
  margin-left: .4rem;
  font-size: .9rem;
  font-weight: normal;
  color: #111;
}
a.thread_link:hover {
  font-weight: normal;
}
a.thread_link b font,
a.thread_link:visited b font {
	margin-left: -1rem;
  font-family: 'Merriweather', serif;
  font-size: .8rem;
  color: #1562B3;
}
a.thread_link:hover,
a.thread_link:hover b font {
  text-decoration: underline;
  color: #1562B3;
}

/*** Announcements ***/
/* Login Form */
.forum_thread_post + .forum_header {
  float: right;
  margin: .5rem;
  padding: .2rem;
  border: 1px solid #999;
  border-bottom: 1px solid #999 !important;
  border-radius: .1rem;
  background: #eee;
}
.forum_thread_post + .forum_header form {
  padding: .4rem;
}
.forum_thread_post + .forum_header .forum_header {
  padding: 0;
  background: #eee;
}
.forum_thread_post + .forum_header .forum_header p {
  padding: .2rem .5rem;
}
.forum_thread_post + .forum_header .forum_header input[type="button"],
.forum_thread_post + .forum_header .forum_header input[type="submit"] {
  margin-top: .6rem;
}


/* #header_holder > div + div, */
#header_holder > div + div + span,
#header_holder > div + div + span + br {
  display: none;
}

/*** Pagination ***/
td[width="150"] {
  cursor: default;
  padding-left: 1rem;
}
td[align="right"] {
  cursor: default;
  padding-right: 1rem;
}
td[width="150"] a,
td[align="center"] a,
td[align="center"] a u,
td[align="right"] a {
  display: inline-block;
  padding: .15rem 0;
  font-size: 1.3rem;
  text-decoration: none;
  color: #1562B3;
}
td[width="150"] a::first-letter,
td[align="right"] a::first-letter {
  text-transform: uppercase;
}
td[align="center"] {
  cursor: default;
  font-size: 1.3rem;
}
td[width="150"] a:hover,
td[align="center"] a:hover,
td[align="center"] a u:hover,
td[align="right"] a:hover {
  color: #dd9d1c;
}

/*** Lists ***/
.forum_header_border {
  padding: .3rem;
  border-radius: .3rem;
  background: #fff;
}
.section_post_header a {
  padding: 0 .2rem;
  color: #1b7fe8 !important;
}
.section_post_header a:hover {
  color: #dd9d1c !important;
  text-decoration: none;
}
tbody tr:first-child .section_post_header {
  cursor: default;
  border-radius: .2rem .2rem 0 0;
}
.forum_thread_header,
.forum_thread_header_end {
  display: none;
}
#header_holder table.forum_header_border:nth-child(1n+14) tr td {
  padding: .5rem 0;
}
#header_holder .section_post_header,
.header_date {
  cursor: default;
  padding: .5rem 1rem !important;
}
td.header_date,
td.header_date b {
  cursor: default;
  font-size: 1.3rem;
  background: #1b7fe8;
}
.forum_header_border tr:nth-child(odd),
.forum_header_noborder tr:nth-child(odd) {
  background-color: #f9f9f9;
}
.forum_header_border tr:nth-child(even),
.forum_header_noborder tr:nth-child(even) {
  background-color: #fff;
}
.forum_header_border tr:hover,
.forum_header_noborder tr:hover {
  background-color: #F2F8FF;
}
.forum_thread_post,
.forum_thread_post_end {
  border: none;
}
.section_header_column {
  background: none;
}
.forum_thread_header,
.forum_thread_header_end,
.section_post_header {
  background-image: none;
  background-color: #333;
  border: none;
  color: #ccc;
}
.forum_header_border_normal tr {
  background: #aaa;
}
/* List Items */
#header_holder table.forum_header_border:nth-child(1n+14) .forum_thread_post a img {
  position: relative;
  top: .2rem;
  left: 0;
  content: url('data:image/png;base64,');
}
a.epinfo,
span.epinfo {
  font-weight: normal;
  color: #1b7fe8;
/*   color: #1562B3; */
  font-size: 1.5rem;
}
.forum_thread_post:hover a.epinfo,
.forum_thread_post:hover span.epinfo {
/*   color: #dd9d1c; */
  color: #1562B3;
/*   text-decoration: none; */
}
a.magnet {
  margin-top: 2px;
  background-size: 15px 15px;
}
a.download_1 {
  background-image: url('data:image/png;base64,');
}
.forum_thread_post_end a img {
  float: right;
  margin-right: .6rem;
  content: url('data:image/png;base64,');
}

a.magnet,
.forum_thread_post a img,
a.download_1,
.forum_thread_post_end a img {
  opacity: .7;
}
a.magnet:hover,
.forum_thread_post a img:hover,
a.download_1:hover,
.forum_thread_post_end a img:hover {
  opacity: 1;
}

