/*************************************************/

/*** Show Page ***/
div[itemtype="http://schema.org/TVSeries"] .forum_header_border_normal {
  border: none;
}
div[itemtype="http://schema.org/TVSeries"] .section_thread_post table:first-child tr {
  background: #eee;
}
.show_info_main_logo {
  position: relative;
  height: 350px;
}
.show_info_main_logo img {
  position: absolute;
  top: .6rem;
  left: .6rem;
  border: 1px solid #111;
  border-radius: .1rem;
}
.show_info_banner_logo {
  text-align: left !important;
  box-sizing: border-box;
  padding: 1.3rem 2.6rem !important;
  border: none !important;
}
.show_info_banner_logo span[itemprop="description"] {
  font-size: 1.1rem;
}
.show_info_banner_logo span[itemprop="description"]:first-child,
.show_info_banner_logo span[itemprop="description"] em,
.show_info_banner_logo span[itemprop="description"] p,
.show_info_banner_logo span[itemprop="description"] p strong,
.show_info_banner_logo span[itemprop="description"] b {
  text-align: left;
  font-family: 'Merriweather', serif;
  font-size: 1.3rem;
}
.show_info_banner_logo span[itemprop="description"] em,
.show_info_banner_logo span[itemprop="description"] p strong {
  font-style: italic;
}
.show_info_banner_logo span[itemprop="description"] br + br {
  display: none;
}
  
/* Rating */
.show_info_rating_score {
  border: none !important;
}
.show_info_rating_score div[itemprop="aggregateRating"] {
  cursor: default;
  float: none !important;
  width: inherit !important;
  display: inline-block;
  padding: .5rem 1.2rem .3rem .5rem;
  background: #DDD;
  border-radius: .3rem;
  opacity: .8;
}
.show_info_rating_score div[itemprop="aggregateRating"]:hover {
  opacity: 1;
}
.show_info_rating_score div[itemprop="aggregateRating"],
.show_info_rating_score div[itemprop="aggregateRating"] span {
  font-size: 1.3rem;
  color: #111 !important;
}
/* IMDB Link */
.show_info_rating_score a {
  position: relative;
  top: -.15rem;
  margin-right: .4rem;
  opacity: .8;
}
.show_info_rating_score a:hover {
  opacity: 1;
}
.show_info_rating_score img {
  content: url('data:image/png;base64,');
}

/* Air time */
.show_info_airs_status {
  padding: 2rem 0 3rem !important;
}
.show_info_airs_status span {
  color: #1b7fe8 !important;
  font-size: 1.1rem !important;
}

/*** Shows List ***/
form[action="/showlist/"] {
  padding-top: .5rem;
}
form[action="/showlist/"] input[type="checkbox"] {
  position: relative;
  top: .4rem;
}
form[action="/showlist/"] div:last-child {
  margin-right: 0 !important;
}
form[action="/showlist/"] select {
  height: 1.6rem !important;
  width: 8rem;
  border-radius: .2rem !important;
}
form[action="/showlist/"] .button.blue {
  font-weight: normal;
}
form[action="/showlist/"] .button.blue:hover {
  font-weight: normal;
  background: #dd9d1c;
}

/* List items */
td[align="center"] .thread_link img {
  margin-top: .2rem;
  height: 3rem;
  width: 4rem;
  border-radius: .1rem;
}
td[align="center"] + .forum_thread_post .thread_link {
  font-size: 1.3rem;
  font-weight: normal;
  color: #1b7fe8;
}
td[align="center"] + .forum_thread_post:hover .thread_link {
  color: #1562B3;
}

font.airing,
font.break,
font.ended,
font.pending {
  cursor: default;
  padding: .1rem .7rem .2rem;
  border-radius: .2rem;
  font-weight: normal;
  color: #fff;
  opacity: .8;
}
font.airing {
  background: #3d8f3d;
}
font.break {
  background: #a0a0a0;
}
font.ended {
  background: #8f3d3d;
}
font.pending {
  background: #a0a0a0;
}
.forum_header_border tr:hover font,
.forum_header_noborder tr:hover font {
  opacity: 1;
}

/* Back to top */
p#back-top {
  right: 3rem;
  bottom: 2rem;
  margin-right: 0;
}
p#back-top a {
  width: 48px;
  height: 48px;
  font-size: 0;
  opacity: .8;
  background: url('data:image/png;base64,') no-repeat;
}
p#back-top a:hover {
  opacity: 1;
}
p#back-top a span {
  display: none;
}
}
