css:
@-moz-document domain(youtube.com) {
    [data-spf-name="watch"] #masthead-positioner{
        position: relative !important;
    }
    [data-spf-name="watch"] #masthead-positioner-height-offset{
        display: none !important;
    }
    [data-spf-name="watch"] .watch-stage-mode #player-api,
    [data-spf-name="watch"] .watch-stage-mode #placeholder-player .player-api
    {
        width: 99vw !important;
        height: 100vh !important;
        left: -50vw !important;
    }
    [data-spf-name="watch"] #theater-background{
       display: none;
    }
}
